//#define ANIMATED_TEXTURE

//-----------------------------------------------------------------------------
//-- Constants ----------------------------------------------------------------
const uint  LRPN_GLOBAL_SEED = 0u;
//-----------------------------------------------------------------------------
//-- Constants ----------------------------------------------------------------
const float m_pi        = 3.14159265359;           /* MathConstant: PI          */
const float m_2_pi      = 6.28318530718;           /* MathConstant: PI*2       	*/
const float m_i_pi      = 0.31830988618;           /* MathConstant: 1 / PI   	*/
const float m_sqrt_2    = 1.41421356237;           /* MathConstant: sqrt(2)    	*/
const float m_i_sqrt_2  = 0.70710678119;           /* MathConstant: 1/sqrt(2)   */

const float m_eps_3f    = 0.001;            
const float m_eps_4f    = 0.0001;            

//-----------------------------------------------------------------------------
//-- Tangent Space Maths (PBRT) -----------------------------------------------
float cos_theta(const vec3 w)       {return w.z;}
float cos_2_theta(const vec3 w)     {return w.z*w.z;}
float sin_2_theta(const vec3 w)     {return max(0.f, 1.f - cos_2_theta(w));}
float sin_theta(const vec3 w)       {return sqrt(sin_2_theta(w));}
float tan_theta(const vec3 w)       {return sin_theta(w) / cos_theta(w);}
float cos_phi(const vec3 w)         {return (sin_theta(w) == 0.f) ? 1.f : clamp(w.x / sin_theta(w), -1.f, 1.f);}
float sin_phi(const vec3 w)         {return (sin_theta(w) == 0.f) ? 0.f : clamp(w.y / sin_theta(w), -1.f, 1.f);}
float cos_2_phi(const vec3 w)       {return cos_phi(w) * cos_phi(w);}
float sin_2_phi(const vec3 w)       {return sin_phi(w) * sin_phi(w);} 

//-----------------------------------------------------------------------------
//-- Ray ----------------------------------------------------------------------
vec3 compute_camera_ray_direction(in vec2 screen_coordinates, in vec3 origin, in vec3 target, float cr )
{
	vec3 cw = normalize(target-origin);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw )*normalize(vec3(screen_coordinates,2.0));
}


//-----------------------------------------------------------------------------
//-- Plane Intersection Functions ---------------------------------------------
float plane_get_hit(in vec3 ro, in vec3 rd, in vec4 plane)
{
    return -(dot(ro,plane.xyz)+plane.w)/dot(rd,plane.xyz);
}

vec3 plane_get_normal(in vec3 pos, in vec4 plane)
{
    return(normalize(plane.xyz));
}

vec2 plane_get_texcoords(in vec3 pos, in float factor)
{
    return(pos.xz*factor);
}

//-----------------------------------------------------------------------------
//-- Orthonormal Basis Function -----------------------------------------------
mat3 orthonormal_basis(in vec3 n)
{
    vec3 f,r;
    if(n.z < -0.999999)
    {
        f = vec3(0 , -1, 0);
        r = vec3(-1, 0, 0);
    }
    else
    {
        float a = 1./(1. + n.z);
        float b = -n.x*n.y*a;
        f = normalize(vec3(1. - n.x*n.x*a, b, -n.x));
        r = normalize(vec3(b, 1. - n.y*n.y*a , -n.y));
    }
    return( mat3(f,r,n) );
}


//--------------------------------------------------------------------------------------------------------
//-- Pseudo Random Number Generation ---------------------------------------------------------------------  
// From Texton Noise Source Code provided by Arthur Leclaire et al. 
// https://www.idpoisson.fr/galerne/texton_noise/index.html
// Sourced ::
/* 
 * From http://www.reedbeta.com/blog/2013/01/12/quick-and-easy-gpu-random-numbers-in-d3d11/
 * Same strategy as in Gabor noise by example
 * Apply hashtable to create cellseed
 * Use a linear congruential generator as fast PRNG
 */

uint  wang_hash(uint seed)                                          {seed=(seed^61u)^(seed>>16u);seed*=9u;seed=seed^(seed>>4u);seed*=668265261u;seed=seed^(seed>>15u);return(seed);}
uint  cell_seed(const in ivec2 c, const in uint offset)             {const uint period=1024u;uint s=((uint(c.y)%period)*period+(uint(c.x)%period))*period+offset; if(s==0u){s = 1u;}return(s);}
uint  myrand(inout uint p)                                          {p^=(p<<13u);p^=(p>>17u);p^=(p<<5u);return p;}
float myrand_uniform_0_1(inout uint p)                              {return float(myrand(p))/float(4294967295u);}
float myrand_uniform_m_M(inout uint p, in float mi, in float ma)    {return mi + (myrand_uniform_0_1(p) * (ma - mi));}

//--------------------------------------------------------------------------------------------------------
//-- Normal Mapping Function -----------------------------------------------------------------------------  
vec3 normal_map_from_slope(float dx, float dy) // using partial derivatives
{
    float norm = length(vec3(dx,dy,1.0));
    float nx = -dx / norm; 
    float ny = -dy / norm; 
    float nz = 1.0 / norm; 
    return 0.5 + 0.5 * vec3(nx,ny,nz);
}

//--------------------------------------------------------------------------------------------------------
//-- Gaussian Window Function ----------------------------------------------------------------------------  
float gaussian(in vec2 st, in float c, in vec2 mu, in mat2 sig)
{
    vec2 p = st-mu;
    float body = -0.5*dot(p,inverse(sig)*p);
    return c*exp(body);
}

//--------------------------------------------------------------------------------------------------------
//-- Local Random Phase Noise ----------------------------------------------------------------------------
vec3 local_random_phase_noise(
        in vec2  texcoords,
        in float resolution,
        in int   cosines,
        in vec2  range_frequency,
        in vec2  range_orientation,
    	in float time
    )
{
    vec2  scaled_coords = texcoords * resolution;
    vec2  cell_coords   = fract(scaled_coords);
    vec2  cell_index    = floor(scaled_coords);

    ivec2 cell_ID;
    uint  prng, seed;

    vec3  lrpn   = vec3(0.);
    float weight = 1. / float(cosines);
    mat2  sigma  = mat2( (1.33*1.33)/m_2_pi );

    for (int m=-1; m<=+1; m++)
    for (int n=-1; n<=+1; n++)
    {
        cell_ID.x = int(cell_index.x) + m;
        cell_ID.y = int(cell_index.y) + n;
        cell_ID.x = int(mod(float(cell_ID.x),resolution));
        cell_ID.y = int(mod(float(cell_ID.y),resolution));
        seed = cell_seed(cell_ID,LRPN_GLOBAL_SEED);
        prng = wang_hash(seed);

        vec2 xy = cell_coords - vec2(m,n) - vec2(0.5);
        float d = dot(xy,xy);

        float sum_of_cosines    = 0.; 
        float sum_of_partial_x  = 0.; 
        float sum_of_partial_y  = 0.; 
        for(int k=0; k<cosines; k++)
        {
            float fr = myrand_uniform_m_M(prng,range_frequency.x,range_frequency.y) * resolution;   // Scaled Frequency 
            float or = myrand_uniform_m_M(prng,range_orientation.x,range_orientation.y);            // Orientation
            //float ph = m_pi*(myrand_uniform_0_1(prng)*2.-1.);                                       // Phase
            float ph = time;
            float ff = m_2_pi * dot(xy, fr * vec2(cos(or),sin(or)) );                               // Oriented Frequency

            sum_of_cosines   += cos(ff+ph);
            sum_of_partial_x += -m_2_pi * fr * cos(or) * sin(ff+ph);
            sum_of_partial_y += -m_2_pi * fr * sin(or) * sin(ff+ph); 
        }

        lrpn += gaussian(xy, 1., vec2(0.0), sigma) * weight * vec3(sum_of_cosines, sum_of_partial_x, sum_of_partial_y);
    }
    return vec3(lrpn); // vec3(Noise, Partial X, Partial Y); 
}

