//--------------------------------------------------------------------------------------------------------
//-- User Parameters -------------------------------------------------------------------------------------  
const int   LRPN_COSINES     = 10;
const float LRPN_RESOLUTION  = 10.0;
const float LRPN_SLOPE_SCALE = 0.1;
//--------------------------------------------------------------------------------------------------------
//-- Main ------------------------------------------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 texcoords = fragCoord.xy / iResolution.xy;
    vec2 mouse = clamp(iMouse.xy / iResolution.xy, 0., 1.);

    float Dx = 0.;
    float Dy = 0.;

#ifdef ANIMATED_TEXTURE
    vec2  F = vec2(0.5,0.6);    //~ Frequecy range [fmin;fmax]
    vec2  O = vec2(0.,m_pi);   	//~ Orientation range [omin;omax]
    float R = LRPN_RESOLUTION;  //~ Grid resolution
    int   K = LRPN_COSINES;     //~ Number of cosines

    vec3  lrpn = local_random_phase_noise(texcoords,R,K,F,O,iTime);
    Dx = lrpn.y * LRPN_SLOPE_SCALE;
    Dy = lrpn.z * LRPN_SLOPE_SCALE;
#else
	float H_00 = dot(vec3(0.333),texelFetch(iChannel3,(ivec2(fragCoord) + ivec2(0,0))%textureSize(iChannel3,0), 0).rgb)*5.; 
    float H_01 = dot(vec3(0.333),texelFetch(iChannel3,(ivec2(fragCoord) + ivec2(0,1))%textureSize(iChannel3,0), 0).rgb)*5.; 
    float H_10 = dot(vec3(0.333),texelFetch(iChannel3,(ivec2(fragCoord) + ivec2(1,0))%textureSize(iChannel3,0), 0).rgb)*5.; 

    Dx = H_00 - H_01;
    Dy = H_00 - H_10;
#endif


    fragColor.rgb = vec3(vec2(Dx,Dy),0.);
}

