void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float PI = 3.14159;

    vec3 color = vec3(0.448,0.800,0.785);
    vec3 background = vec3(0.418,0.421,0.440);

    float width = iResolution.x;
    float height = iResolution.y;
    float ellipseScale = 2.0;

    float animationSpeed = 0.5;

    float x = (fragCoord.x / iResolution.x);
    float y = (fragCoord.y / iResolution.y);

    x = (x * 2.0) - 1.0;
    y = (y * 2.0) - 1.0;

    float theta = atan(y, x);
    float tanTheta = y/x;

    float a = width / ellipseScale;
    float b = height / ellipseScale;

    //intersections with the ellipse and the function y = x * tan(theta) https://math.stackexchange.com/questions/22064/calculating-a-point-that-lies-on-an-ellipse-given-an-angle
    float x1 = a*b / sqrt(b*b + a*a * tanTheta * tanTheta);
    float y1 = a*b*tanTheta / sqrt(b*b + a*a * tanTheta * tanTheta);

    vec2 point = vec2(x, y) * max(width, height); // work in a square space
    vec2 pointOnEllipse = vec2(x1, y1);

    float distanceToEllipse = abs(length(pointOnEllipse) - length(point)) / max(width, height);
    float borderWidth = 0.120;
    float r = 1.0 - (distanceToEllipse / borderWidth);

    float current = (theta + PI); //[0 - 360]
    current = mod(current + PI / 2.0, 2.0 * PI);  //offset to put the beguining at the top
    current = current / (2.0 * PI); //ratio for animation
    float angular = mod(iTime * animationSpeed, 1.0); 
    if(current < angular){
    	fragColor = vec4(r * color, 1.0);
    } else {
        fragColor = vec4(r * background, 1.0);
    }
}

//CC0 1.0 Universal https://creativecommons.org/publicdomain/zero/1.0/
//To the extent possible under law, Blackle Mori has waived all copyright and related or neighboring rights to this work.

float smin(float a, float b, float k) {
  float h = max(0., k-abs(a-b))/k;
  return min(a,b)-h*h*h*k/6.;
}

//smooth triangle wave for smooth domain repetition https://www.desmos.com/calculator/ototv6tja8
vec4 stri(vec4 p, float k) {
  return asin(sin(p*3.14)*k)/3.14+0.5;
}

float scene(vec4 p) {
  vec4 q = abs(p) - 1.;
  float cube = length(max(q,0.0)) + min(max(max(q.x,q.w),max(q.y,q.z)),0.0) - 0.1;
  float scale = 1.;
  vec4 p2 = p+iTime*0.2;
  p2 = (stri(p2/scale, .9)-0.5)*scale;
  float spheres = length(p2)-0.2;
  spheres = -smin(-(length(p) - 2.), -spheres, 0.1);
  return smin(cube, spheres, 0.5);
}

vec4 norm(vec4 p) {
  mat4 k = mat4(p,p,p,p) - mat4(0.001);
  return normalize(scene(p) - vec4( scene(k[0]),scene(k[1]),scene(k[2]),scene(k[3]) ) );
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax,p)*ax,p,cos(ro)) + sin(ro)*cross(ax,p);
}

vec3 srgb(float r, float g, float b) {
  return pow(vec3(r,g,b),vec3(2.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = (fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
  vec2 mouse = (iMouse.xy - 0.5*iResolution.xy)/iResolution.y;

  vec4 cam = normalize(vec4(1,uv,0));
  vec4 init = vec4(-5,0,0,sin(iTime*0.1));

  float wrot = cos(iTime*0.5);
  float zrot = cos(iTime*0.25);
  float yrot = sin(iTime*0.5);
  float zrot2 = iTime;
  if (iMouse.z > 0.) {
    zrot = mouse.x*2.;
    wrot = radians(45.0);
    yrot = mouse.y*2.;
    zrot2 = 0.;
  }
  cam.xyw = erot(cam.xyw, vec3(0,1,0), zrot);
  init.xyw = erot(init.xyw, vec3(0,1,0), zrot);
  cam.xyz = erot(cam.xyz, vec3(0,1,0), yrot);
  init.xyz = erot(init.xyz, vec3(0,1,0), yrot);
  cam.yzw = erot(cam.yzw, vec3(0,1,0), wrot);
  init.yzw = erot(init.yzw, vec3(0,1,0), wrot);
  cam.xyz = erot(cam.xyz, vec3(0,0,1), zrot2);
  init.xyz = erot(init.xyz, vec3(0,0,1), zrot2);

  vec4 p = init;
  bool hit = false;
  for (int i = 0; i<200 && !hit;i++) {
    float dist = scene(p);
    hit = dist*dist < 1e-6;
    p+=dist*cam;
  }
  vec4 n = norm(p);
  vec4 r = reflect(cam,n);
  vec4 aon = reflect(cam, norm(p+r*0.3));
  float factor = length(sin(aon*3.)*0.5+0.5)/2.;
  vec3 color = mix(srgb(0.1,0.1,0.2), srgb(0.2,0.6,0.9), factor) + pow(factor, 10.);
  fragColor.xyz = hit ? color : srgb(0.1,0.1,0.1);
  fragColor.xyz = sqrt(fragColor.xyz);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec3 p;
  vec2 r=iResolution.xy;
  float t=iTime;
  float d,l,b1,b2,R=1.57;

  fragColor=vec4(.1,.3,.6,1.);
  for(int i=0;i<77;i++)
  {
    p=vec3((gl_FragCoord.xy-r*.5)/r.y,1.)*l;
    p.yz*=mat2(cos(-.2+vec4(0,-R,R,0)));
    p+=vec3(0,3.,t);
    p.y=mod(p.y-1.5,6.)-3.;
    p.xz=mod(p.xz,1.)-.5;
    b1=length(p-vec3(sin(t*R*12.)/5.,cos(t*R*12.)/8.-2.82,.0))-.1;
    b2=length(p-vec3(0,-3.1,.0))-.2;
    d=min(b1,b2);
    l+=d;
    if(d<.01||b1+b2<.19){fragColor+=vec4(sin(vec3(R*4./3.,R/3.*8.,0)+p.x+l*9.+t*30.)*2.+2.,1)/(l);break;};
  }
}

vec3 ACESFilm(vec3 x){
    float a = 2.51;
    float b = 0.03;
    float c = 2.43;
    float d = 0.59;
    float e = 0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}


mat3 gaussianFilter = mat3(41, 26, 7,
                           26, 16, 4,
                           7,  4,  1) / 273.;

vec3 bloom(float scale, float threshold, vec2 fragCoord){
    float logScale = log2(scale);
    vec3 bloom = vec3(0);
    for(int y = -2; y <= 2; y++)
        for(int x = -2; x <= 2; x++)
            bloom += gaussianFilter[abs(x)][abs(y)] * textureLod(iChannel0, (fragCoord+vec2(x, y)*scale)/iResolution.xy, logScale).rgb;

    return max(bloom - vec3(threshold), vec3(0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    Camera cam = getCam(iTime);

    vec4 col = vec4(0.);

    float frd = iResolution.y*cam.aperture*DoFClamping;
    int rd = int(ceil(frd - .5));
    for(int y = -rd; y <= rd; y++){
        int ln = int(ceil(sqrt(frd*frd-float(y*y)) - .5));
        for(int x = -ln; x <= ln; x++){
            vec2 coords = clamp(fragCoord + vec2(x, y), vec2(0), iResolution.xy-1.);
            vec4 p = texelFetch(iChannel0, ivec2(coords), 0);
            float dof = min(abs(p.a-cam.focalDistance)/p.a, DoFClamping) * iResolution.y*cam.aperture;
            col += vec4(p.rgb, 1.) * clamp((dof - length(vec2(x, y))) + .5, 0.0, 1.0) / (dof*dof+.1);
    	}
    }

    col /= col.a;

    vec3 bloomSum = vec3(0.);
    bloomSum += bloom(.05 * iResolution.y, .2, fragCoord) * .1;

    fragColor = vec4(ACESFilm(col.rgb + bloomSum), 1.);
}

