#define IndirectSamples 4
#define SamplesLimit 256
#define Bounces 2

#define Pi 3.14159265359
#define Epsilon .00002

#define MaxStepsDirect 8
#define MaxStepsIndirect 5
#define MaxStepsShadow 4

#define SceneRadius 2.0

#define PixelAcceptance 2.
#define PixelCheckDistance .75

#define DoFClamping .2


vec3 LightDir = normalize(vec3(.2, -1.9, -1.));
vec3 LightColor = vec3(.7, .5, .4) * 7.0;

struct Camera {
    vec3 pos, rot;
    float focalLength, focalDistance, aperture;
};


mat3 rotationMatrix(vec3 rotEuler){
    float c = cos(rotEuler.x), s = sin(rotEuler.x);
    mat3 rx = mat3(1, 0, 0, 0, c, -s, 0, s, c);
    c = cos(rotEuler.y), s = sin(rotEuler.y);
    mat3 ry = mat3(c, 0, -s, 0, 1, 0, s, 0, c);
    c = cos(rotEuler.z), s = sin(rotEuler.z);
    mat3 rz = mat3(c, -s, 0, s, c, 0, 0, 0, 1);

    return rz * rx * ry;
}

Camera getCam(float time){
    vec3 rot = vec3(.8, time*.3, 0.);
    vec3 pos = vec3(.0, .0, -10.) * rotationMatrix(rot);
    return Camera(pos, rot, 1.3, 8., .03);
}

vec3 uv2dir(Camera cam, vec2 uv){
    return normalize(vec3(uv, cam.focalLength)) * rotationMatrix(cam.rot);
}

vec2 pos2uv(Camera cam, vec3 pos){
    vec3 dir = normalize(pos - cam.pos) * inverse(rotationMatrix(cam.rot));
    return dir.xy * cam.focalLength / dir.z;
}

vec3 dirFromUv(Camera cam, vec2 uv){
    return normalize(vec3(uv, cam.focalLength)) * rotationMatrix(cam.rot);
}


bool isAir(in ivec3 voxel, out vec3 dc, out vec3 ec) {
    if(voxel == ivec3(1, 0, -2)) {
        dc = vec3(0.);
        ec = vec3(1., 5., 15.);
        return false;
    }
    if(voxel.x == -1 && voxel.y > -1) {
        dc = vec3(1., .5, .2);
        ec = vec3(0.);
        return false;
    }
    if(voxel.y <= -1 || voxel.xz == ivec2(1, 1)) {
        dc = vec3(1.);
        ec = vec3(0.);
        return false;
    }
    if(voxel.z == -1) {
        dc = vec3(.2, 1., .2);
        ec = vec3(0.);
        return false;
    }
    return true;
}

ivec3 closestVoxel(vec3 pos){
    return ivec3(floor(pos));
}

ivec3 nextVoxel(inout vec3 pos, vec3 dir){

    dir += (vec3(1.0)-abs(sign(dir)))*Epsilon; //prevent zeros
    vec3 s = sign(dir);
    vec3 l = (floor(pos + s*(.5+Epsilon) + .5) - pos) / dir;
    float closest = min(min(l.x, l.y), l.z);
    pos += dir*(closest+Epsilon);
    return closestVoxel(pos);
}

bool isInScene(in vec3 pos, vec3 dir){
    vec3 apos = abs(pos);
    return max(max(apos.x, apos.y), apos.z) <= SceneRadius;
}

bool intersectScene(inout vec3 pos, in vec3 dir){

    dir += (vec3(1.0)-abs(sign(dir)))*Epsilon; //prevent zeros

    vec3 i1 = (-sign(dir)*(SceneRadius+2.*Epsilon)-pos) / dir;
    float t = max(max(i1.x, i1.y), i1.z);
    pos += dir*max(0., t);
    return isInScene(pos+sign(dir)*4.*Epsilon, dir);
}

bool trace(inout vec3 ro, in vec3 rd, int maxSteps){
    if(!intersectScene(ro, rd)) return false;
    vec3 dc, ec;
    for(int i = 0; i < maxSteps; i++){
        if(!isAir(nextVoxel(ro, rd), dc, ec)){
            ro -= rd*Epsilon*2.0;
            return true;
        }
        if(!isInScene(ro, rd)) break;
    }
    return false;
}

void surfaceInfo(in vec3 pos, out vec3 normal, out vec3 dc, out vec3 ec){
    vec3 d = fract(pos)-.5;
    vec3 ad = abs(d);
    float m = max(max(ad.x, ad.y), ad.z);
    normal = (abs(sign(ad-m))-1.)*sign(d);
    isAir(closestVoxel(pos-normal), dc, ec);
}

vec3 directLight(vec3 pos, vec3 normal){
    vec3 dir = -LightDir;
    float dotLight = dot(normal, dir);
    if(dotLight < 0.0) return vec3(0);
    return trace(pos, dir, MaxStepsShadow) ? vec3(0.) : LightColor * dotLight;
}

