    vec4 hit = texelFetch(iChannel1, ivec2(fragCoord), 0);
    vec3 finalPos = hit.rgb;
    col.a = length(cam.pos-finalPos);

    vec2 uv = (fragCoord-iResolution.xy/2.0) / iResolution.y;
