        for(uint i = 0u; i < count; ++i)
        {
            vec2 o = Float11(Roberts(uvec2(0u), i));
            vec2 off = o * 0.75;
            //off = vec2(Sample_Triangle(o.x), Sample_Triangle(o.y))*1.5;

