vec3 UnitDiskToHemisphere(vec2 p)
{
    float s = dot(p, p);
    float l = sqrt(2.0 - s);

    return vec3(p.x * l, 1.0 - s, p.y * l);
}


void mainImage(out vec4 outCol, in vec2 uvO)
{     
