            //r = sqrt((2.0*rr-rr*rr)*(2.0-(2.0*rr-rr*rr))) / Pow2(rr - 1.0)*0.5;

           #elif 0

            r = sqrt(2.0*rr-rr*rr) / (1.0 - rr)*0.5;

           #elif 0

            r = sqrt(rr / sqrt(1.0 - rr*rr));

           #elif 1

            r = log((1.0+r)/(1.0-r));
            r*=r;
            r *= 0.125;
            //r = pow(r, 1.25);
