        	off += AngToVec(h0 * Pi) * r * (0.125 * iResolution.x);

           //vec2 gauss = Sample_Gauss2D(h01.x, h01.y*2.0-1.0);

            //off += gauss * (0.125*0.5  * iResolution.x);
