        res.uv = vec2( dot(mm.yzx, xyz), dot(mm.zxy, xyz) );
        if(abs(mm.x)>0.) res.uv=res.uv.yx; //invert xz 
        //relative to absolute normals:
   		res.normal  = rotate( -theta2,2) * rotate(- theta1,3) *res.normal;
    }
    return res;  
}
