// ###############################
// ### STRUCTS and other stuff ###
// ###############################

#define LIGHT_WIDTH 1.
#define CEIL_HEIGHT 20.
#define N_LIGHTS 7
#define END 50.

#define TSCALE 0.5

#define TAO 6.2831853
#define PI 3.14159

#define UNDEFINED_ID -1
#define LIGHT_ID 0
#define CURTAIN_ID 1
#define FLOOR_ID 2

#define LUTSIZE  8.0
#define MATRIX_PARAM_OFFSET 8.0

#define ep 0.005

struct CAM {
    vec3 lookat;
    vec3 ro;

    vec3 forw;
    vec3 up;
    vec3 right;

    float field;
};

struct RAY {
	vec3 ro;       
    vec3 rd;
};

/*
struct PETAL {
    float curve1;
    float curve2;
    float curve3;
    float curve4;
    float curve5;
    float wide;
    float bound;
    float opt;
};
*/    

mat2 rot(float a)
{
    float c0 = cos(a);
    float s0 = sin(a);
    return mat2(
        c0,-s0,
        s0, c0
    );
}

struct LIGHT {
    vec3 points[4];
    vec3 col;
    vec3 pos;
};

int OBJ_ID = UNDEFINED_ID;
LIGHT all_light[N_LIGHTS];
CAM camera;
float LDIST[N_LIGHTS-1] = float[N_LIGHTS-1](END, END, END, END, END, END);
bool is_belowfloor = false;

// #############
// ### SCENE ###
// #############

// --- Useful Funcs ---

float smin( float a, float b, float k )
{
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*k*(1.0/4.0);
}

