// --- PRIMITIVES ---   

float sdBox( vec3 p, vec3 b)
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdVerticalCapsule( vec3 p, float h, float r )
{
  //p.y -= clamp( p.y, 0.0, h );
  return length( p.xz ) - r;
}

// --- Actual shapes ---

float sdLightCube(vec3 P)
{
    vec3 p = P;
    p.yz *= rot(-TSCALE*iTime*3.);
    p.xy *= rot(-TSCALE*iTime);

    float d = sdBox(p, vec3(LIGHT_WIDTH));
    return d;
}

float sdLight(vec3 P)
{
    float d = min(sdLightCube(P), abs(P.y - CEIL_HEIGHT));
    return d;
}

float sdRods(vec3 p, float m, float thc)
{

    float ang = TAO/m;
    float a = mod(atan(p.z,p.x), ang) - ang/2.;
    p.y /= 2.;
    p.xz = vec2(length(p.xz), 0.) * rot(a);
    p.y += 3.;
    p.x += cos(p.y)*3.;
	float d = sdVerticalCapsule(p, 6., thc);

    return d*.4;
}

float sdCurtain(vec3 P)
{

    float clamp1 = length(P) - 3.5;
    if (clamp1 > 0.) return clamp1+ep;
    //float clamp2 = length(P) - 2.;
    //if (clamp2 < 0.) return END;

    vec3 p = P;

    p.yz *= rot(sin(TSCALE*iTime*.7)/4.);
    float s = sin(TSCALE*iTime) + TSCALE*iTime;
    p.xz *= rot(s);

    p.xz *= rot(cos(p.y));
    float d = sdRods(p, 20., .03);
    d = smin(d, sdRods(p, 12.36, .05), .1);
    //d = smin(d, sdRods(p, 7.63, .07), .07);
    //d = smin(d, sdRods(p, 4.72, .1), .05);

    return d;
}

float sdFloor(vec3 P)
{
    float a = P.y + 3.1;
    if (abs(a) > .05) return abs(a);
    return abs(a + texture(iChannel1, P.xz/10.).x*.04)*.8;
}

float sdScene(vec3 P)
{

   	float d0 = sdLight(P);
    float d1 = sdCurtain(P);
    float d2 = sdFloor(P);

    float d = d0;
    OBJ_ID = LIGHT_ID;

    if (d > d1)
    {
        d = d1;
        OBJ_ID = CURTAIN_ID;
    }

    if (d > d2 && !is_belowfloor)
    {
        d = d2;
        OBJ_ID = FLOOR_ID;
    }

    return d;
}

// #################
// ### RENDERING ###
// #################

// --- Camera ---

void InitCamera(inout CAM c)
{
    vec2 M = (iMouse.xy/iResolution.xy - 0.5) * PI;
    if (length(iMouse.xy) < ep) M = vec2(0., .3);

    // x,y,z = right, up, forw
    c.lookat = vec3(0., .5, 0.);

    c.ro = vec3(0., 0., 9.);
    c.ro.yz *= rot(M.y);
    c.ro.xz *= rot(M.x);

    c.forw = normalize(c.lookat - c.ro);
    c.right = normalize(cross(c.forw, vec3(0.0, 1.0, 0.0)));
    c.up = normalize(cross(c.right, c.forw));
    c.field = 2.;
}

void InitRay(inout RAY r, CAM c, vec2 uv)
{
    r.rd = normalize(uv.x*c.right + uv.y*c.up + c.field*c.forw);
    r.ro = c.ro;
}

void InitLight(inout LIGHT all_light[N_LIGHTS])
{
    // These was a pain to type

    vec3 p0 = vec3( LIGHT_WIDTH, LIGHT_WIDTH, LIGHT_WIDTH);
    vec3 p1 = vec3(-LIGHT_WIDTH, LIGHT_WIDTH, LIGHT_WIDTH);
    vec3 p2 = vec3( LIGHT_WIDTH,-LIGHT_WIDTH, LIGHT_WIDTH);
    vec3 p3 = vec3(-LIGHT_WIDTH,-LIGHT_WIDTH, LIGHT_WIDTH);
    vec3 p4 = vec3( LIGHT_WIDTH, LIGHT_WIDTH,-LIGHT_WIDTH);
    vec3 p5 = vec3(-LIGHT_WIDTH, LIGHT_WIDTH,-LIGHT_WIDTH);
    vec3 p6 = vec3( LIGHT_WIDTH,-LIGHT_WIDTH,-LIGHT_WIDTH);
    vec3 p7 = vec3(-LIGHT_WIDTH,-LIGHT_WIDTH,-LIGHT_WIDTH);

    vec3 pp0 = vec3( 0.,-LIGHT_WIDTH, 0.);
    vec3 pp1 = vec3(-LIGHT_WIDTH, 0., 0.);
    vec3 pp2 = vec3( 0., 0.,-LIGHT_WIDTH);
    vec3 pp3 = vec3( 0., 0., LIGHT_WIDTH);
    vec3 pp4 = vec3( LIGHT_WIDTH, 0., 0.);
    vec3 pp5 = vec3( 0., LIGHT_WIDTH, 0.);

    mat2 m1 = rot(TSCALE*iTime);
    mat2 m2 = rot(TSCALE*iTime*3.);
    p0.xy *= m1; p0.yz *= m2;
    p1.xy *= m1; p1.yz *= m2;
    p2.xy *= m1; p2.yz *= m2;
    p3.xy *= m1; p3.yz *= m2;
    p4.xy *= m1; p4.yz *= m2;
    p5.xy *= m1; p5.yz *= m2;
    p6.xy *= m1; p6.yz *= m2; 
    p7.xy *= m1; p7.yz *= m2;

    pp0.xy *= m1; pp0.yz *= m2;
    pp1.xy *= m1; pp1.yz *= m2;
    pp2.yz *= m2;
    pp3.yz *= m2;
    pp4.xy *= m1; pp4.yz *= m2;
    pp5.xy *= m1; pp5.yz *= m2;

    vec3 c = vec3(CEIL_HEIGHT, 10., -10.);

    all_light = LIGHT[N_LIGHTS](

        LIGHT(vec3[4](p0, p1, p5, p4), vec3(1., .5, .6), pp5),
        LIGHT(vec3[4](p1, p0, p2, p3), vec3(.4, 1., .6), pp3),
        LIGHT(vec3[4](p0, p4, p6, p2), vec3(.3, .4, 1.), pp4),
        LIGHT(vec3[4](p3, p7, p5, p1), vec3(1., 1., .2), pp1),
        LIGHT(vec3[4](p4, p5, p7, p6), vec3(.3, 1., 1.), pp2),
        LIGHT(vec3[4](p2, p6, p7, p3), vec3(1., .4, 1.), pp0),

        LIGHT(vec3[4](c.yxy, c.yxz, c.zxz, c.zxy), 2.*vec3(.5, .3, .2), vec3(0., 10., 0.))

    );
}

// --- Raytracing ---

vec3 get_normal(vec3 p) {
	const vec2 e = vec2(0.002, 0);
	return normalize(vec3(sdScene(p + e.xyy)-sdScene(p - e.xyy), 
                          sdScene(p + e.yxy)-sdScene(p - e.yxy),	
                          sdScene(p + e.yyx)-sdScene(p - e.yyx)));
}

float Intersect(RAY r)
{
    float t = 0.01;
    float ld; vec3 ln;
    vec3 p;
    for( int i=0; i<256; i++ )
    {
        p = r.ro + r.rd*t;
        float c = sdScene(p);

        for (int i = 0; i < N_LIGHTS-1; ++i)
        {
            ln = normalize(all_light[i].pos);
            ld = max(dot(ln, all_light[i].pos) - dot(ln, p), sdLightCube(p));
            if (ld < LDIST[i]) LDIST[i] = ld;
        }

        if( c < ep ) break;
        t += c;
        if( t>END ) return END;
    }
    return t;
}

// -- Shading --

float IntegrateEdge(vec3 v1, vec3 v2)
{
    float cosTheta = dot(v1, v2);
    float theta = acos(cosTheta);    
    float res = cross(v1, v2).z * ((theta > 0.001) ? theta/sin(theta) : 1.0);

    return res;
}

void ClipQuadToHorizon(inout vec3 L[5], out int n)
{
    // detect clipping config
    int config = 0;
    if (L[0].z > 0.0) config += 1;
    if (L[1].z > 0.0) config += 2;
    if (L[2].z > 0.0) config += 4;
    if (L[3].z > 0.0) config += 8;

    // clip
    n = 0;

    if (config == 0)
    {
        // clip all
    }
    else if (config == 1) // V1 clip V2 V3 V4
    {
        n = 3;
        L[1] = -L[1].z * L[0] + L[0].z * L[1];
        L[2] = -L[3].z * L[0] + L[0].z * L[3];
    }
    else if (config == 2) // V2 clip V1 V3 V4
    {
        n = 3;
        L[0] = -L[0].z * L[1] + L[1].z * L[0];
        L[2] = -L[2].z * L[1] + L[1].z * L[2];
    }
    else if (config == 3) // V1 V2 clip V3 V4
    {
        n = 4;
        L[2] = -L[2].z * L[1] + L[1].z * L[2];
        L[3] = -L[3].z * L[0] + L[0].z * L[3];
    }
    else if (config == 4) // V3 clip V1 V2 V4
    {
        n = 3;
        L[0] = -L[3].z * L[2] + L[2].z * L[3];
        L[1] = -L[1].z * L[2] + L[2].z * L[1];
    }
    else if (config == 5) // V1 V3 clip V2 V4) impossible
    {
        n = 0;
    }
    else if (config == 6) // V2 V3 clip V1 V4
    {
        n = 4;
        L[0] = -L[0].z * L[1] + L[1].z * L[0];
        L[3] = -L[3].z * L[2] + L[2].z * L[3];
    }
    else if (config == 7) // V1 V2 V3 clip V4
    {
        n = 5;
        L[4] = -L[3].z * L[0] + L[0].z * L[3];
        L[3] = -L[3].z * L[2] + L[2].z * L[3];
    }
    else if (config == 8) // V4 clip V1 V2 V3
    {
        n = 3;
        L[0] = -L[0].z * L[3] + L[3].z * L[0];
        L[1] = -L[2].z * L[3] + L[3].z * L[2];
        L[2] =  L[3];
    }
    else if (config == 9) // V1 V4 clip V2 V3
    {
        n = 4;
        L[1] = -L[1].z * L[0] + L[0].z * L[1];
        L[2] = -L[2].z * L[3] + L[3].z * L[2];
    }
    else if (config == 10) // V2 V4 clip V1 V3) impossible
    {
        n = 0;
    }
    else if (config == 11) // V1 V2 V4 clip V3
    {
        n = 5;
        L[4] = L[3];
        L[3] = -L[2].z * L[3] + L[3].z * L[2];
        L[2] = -L[2].z * L[1] + L[1].z * L[2];
    }
    else if (config == 12) // V3 V4 clip V1 V2
    {
        n = 4;
        L[1] = -L[1].z * L[2] + L[2].z * L[1];
        L[0] = -L[0].z * L[3] + L[3].z * L[0];
    }
    else if (config == 13) // V1 V3 V4 clip V2
    {
        n = 5;
        L[4] = L[3];
        L[3] = L[2];
        L[2] = -L[1].z * L[2] + L[2].z * L[1];
        L[1] = -L[1].z * L[0] + L[0].z * L[1];
    }
    else if (config == 14) // V2 V3 V4 clip V1
    {
        n = 5;
        L[4] = -L[0].z * L[3] + L[3].z * L[0];
        L[0] = -L[0].z * L[1] + L[1].z * L[0];
    }
    else if (config == 15) // V1 V2 V3 V4
    {
        n = 4;
    }

    if (n == 3)
        L[3] = L[0];
    if (n == 4)
        L[4] = L[0];
}


vec3 LTC_Evaluate(vec3 N, vec3 V, vec3 P, mat3 Minv, vec3 points[4])
{
    vec3 T1, T2;
    T1 = normalize(V - N*dot(V, N));
    T2 = cross(N, T1);

    Minv = Minv * transpose(mat3(T1, T2, N));

    vec3 L[5];
    L[0] = Minv * (points[0] - P);
    L[1] = Minv * (points[1] - P);
    L[2] = Minv * (points[2] - P);
    L[3] = Minv * (points[3] - P);

    int n=0;
    ClipQuadToHorizon(L, n);

    if (n == 0)
        return vec3(0, 0, 0);

    L[0] = normalize(L[0]);
    L[1] = normalize(L[1]);
    L[2] = normalize(L[2]);
    L[3] = normalize(L[3]);
    L[4] = normalize(L[4]);

    float sum = 0.0;

    sum += IntegrateEdge(L[0], L[1]);
    sum += IntegrateEdge(L[1], L[2]);
    sum += IntegrateEdge(L[2], L[3]);
    if (n >= 4)
        sum += IntegrateEdge(L[3], L[4]);
    if (n == 5)
        sum += IntegrateEdge(L[4], L[0]);

    sum =  max(0.0, sum);

    vec3 Lo_i = vec3(sum);

    return Lo_i;
}


float shadow( in RAY r, float w )
{
    float t = 0.01;
    float c = 0.;
    float s = 1.;
    for( int i=0; i<256; i++ )
    {
        vec3 p = r.ro + r.rd*t;
        c = min(sdCurtain(p), sdLight(p));
        s = min( s, 0.5+0.5*c/(w*t) );
        if( c < ep) break;
        t += c;
        if( t > END ) break;
    }

    s = min(max(s,0.0), 1.);
    s = s*s*(3.0-2.0*s);

    return 1. - s;
}

void LTC_shading(float roughness, 
                 vec3 N, 
                 vec3 V, 
                 vec3 pos, 
                 vec3 m_spec, 
                 vec3 m_diff,
                 LIGHT light,
                 inout vec3 col)
{


    float theta = acos(dot(N, V));
    vec2 uv = vec2(roughness, theta/(0.5*PI)) * float(LUTSIZE-1.);   
    vec2 wx = uv/iChannelResolution[0].xy;

    uv += vec2(0.5);

    vec4 params = texture(iChannel0, (uv+vec2(MATRIX_PARAM_OFFSET, 0.0))/iChannelResolution[0].xy);

    mat3 Minv = mat3(
        vec3(  1,        0,      params.y),
        vec3(  0,     params.z,   0),
        vec3(params.w,   0,      params.x)
    );

    vec3 spec = LTC_Evaluate(N, V, pos, Minv, light.points)*m_spec;

    spec *= texture(iChannel0, uv/iChannelResolution[0].xy).x;

    vec3 diff = LTC_Evaluate(N, V, pos, mat3(1), light.points)*m_diff; 

    RAY r = RAY(pos+.1*N, normalize(light.pos-pos));
    float sha = shadow( r, .008 );

    vec3 col0  = light.col * (m_spec*spec + m_diff*diff)*sha;
    col0 /= 2.0*PI;

    // Some inaccuracy but it looks nicer so.
    col += col0*2.;
}

vec3 Render(vec2 uv)
{

    if (camera.ro.y < -3.1) is_belowfloor = true;

    RAY ray;

    InitLight(all_light);
    InitRay(ray, camera, uv);

    float d = Intersect(ray);
    vec3 P = d*ray.rd + ray.ro;
    vec3 N = get_normal(P);
    vec3 V = -ray.rd;

    vec3 n;
    vec3 col = vec3(0);

    float roughness;
    vec3 spec;
    vec3 diff;

    for (int i = 0; i < N_LIGHTS-1; ++i)
    {
        // Add cheap af fog
        col += pow(clamp(abs(1./LDIST[i])*0.03, 0., .5), .8)*all_light[i].col*.5;
    }

    if (d == END || abs(P.y - CEIL_HEIGHT) < ep)
    {
        P = END*ray.rd + ray.ro;
        float grad = P.y/(END);
        col += vec3(grad*grad) *  2.*vec3(.5, .3, .2);
        return col;
    }

    if (OBJ_ID == CURTAIN_ID) 
    {
        roughness = .3;
        diff = vec3(1.);
        spec = vec3(1.); 
    }

  	if (OBJ_ID == FLOOR_ID)
    {
        roughness = 1. - texture(iChannel1, P.xz/10.).x - .1;
        diff = vec3(1.);
        spec = vec3(1.); 
    }

    if (OBJ_ID == LIGHT_ID)
    {

        float ld;
     	for (int i = 0; i < N_LIGHTS-1; ++i)
        {
            vec3 ln = normalize(all_light[i].pos);
            ld = dot(ln, all_light[i].pos) - dot(ln, P);
            if (ld < ep)
            {
                col += all_light[i].col;
                return col;
            }
        }
    }

    for (int i = 0; i < N_LIGHTS; ++i)
    {
    	LTC_shading(roughness, N, V, P, diff, spec, all_light[i], col);
    }


    return col;
}

