float SmoothBump (float lo, float hi, float w, float x);
float Fbm2 (vec2 p);
vec4 Loadv4 (int idVar);

vec3 qHit, qnHit, ltDir;
float dstFar, cylRad, cylWid;
const int nObj = 32;
const int nSphObj = 20;
const float pi = 3.14159;

#if ! SHOW_SPH

vec4 CylAnHit (vec3 ro, vec3 rd, float r, float h)
{
  vec3 s, vn;
  float d, a, b, w, ww, ri, g;
  d = dstFar;
  ri = r - 2. * h;
  a = dot (rd.xy, rd.xy);
  b = dot (rd.xy, ro.xy);
  ww = b * b - a * dot (ro.xy, ro.xy);
  w = ww + a * r * r;
  vn = vec3 (0.);
  if (w > 0.) {
    if (a > 0.) {
      d = (- b - sqrt (w)) / a;
      s = ro + d * rd;
      if (abs (s.z) < h) vn.xy = s.xy;
    } else s = vec3 (0., 0., h);
    if (d < 0. || abs (s.z) > h) {
      d = - (sign (rd.z) * ro.z + h) / abs (rd.z);
      g = length (ro.xy + d * rd.xy);
      if (d < 0. || g > r) d = dstFar;
      else if (g < ri) {
        w = ww + a * ri * ri;
        if (w > 0.) {
          d = (- b + sqrt (w)) / a;
          s = ro + d * rd;
          if (abs (s.z) < h) vn.xy = - s.xy;
          else d = dstFar;
        }
      } else vn.z = - sign (rd.z);
    }
  }
  return vec4 (d, vn);
}

float ObjHit (vec3 ro, vec3 rd)
{
  mat3 mRot;
  vec4 cdn4;
  vec3 rm, rom, rdm;
  float dMin;
  dMin = dstFar;
  for (int n = 0; n < nObj; n ++) {
    rm = Loadv4 (4 * n).xyz;
    mRot = QtToRMat (Loadv4 (4 * n + 2));
    rom = (ro - rm) * mRot;
    rdm = rd * mRot;
    cdn4 = CylAnHit (rom, rdm, cylRad, cylWid);
    if (cdn4.x < dMin) {
      dMin = cdn4.x;
      qHit = rom + dMin * rdm;
      qnHit = mRot * cdn4.yzw;
    }
  }
  qnHit = normalize (qnHit);
  return dMin;
}

float ObjHitSh (vec3 ro, vec3 rd, float rng)
{
  mat3 mRot;
  vec4 cdn4;
  vec3 rm, rom, rdm;
  float dMin;
  dMin = dstFar;
  for (int n = 0; n < nObj; n ++) {
    rm = Loadv4 (4 * n).xyz;
    mRot = QtToRMat (Loadv4 (4 * n + 2));
    rom = (ro - rm) * mRot;
    rdm = rd * mRot;
    cdn4 = CylAnHit (rom, rdm, cylRad, cylWid);
    if (cdn4.x < dMin) dMin = cdn4.x;
  }
  return smoothstep (0.5 * rng, rng, dMin);
}

#else

vec3 RSph (float s)
{
  vec3 r;
  float a;
  a = (2. * pi / float (nSphObj)) * s;
  r = vec3 ((cylRad - 0.5) * cos (a), (cylRad - 0.5) * sin (a), 0.);
  return r;
}

float ObjHit (vec3 ro, vec3 rd)
{
  mat3 mRot;
  vec3 rm, v, vn;
  float dMin, b, d, w, sz;
  dMin = dstFar;
  sz = 0.5;
  for (int n = 0; n < nObj; n ++) {
    rm = Loadv4 (4 * n).xyz;
    mRot = QtToRMat (Loadv4 (4 * n + 2));
    for (int j = 0; j < nSphObj; j ++) {
      v = ro - (rm + mRot * RSph (float (j)));
      b = dot (rd, v);
      w = b * b + sz * sz - dot (v, v);
      if (w >= 0.) {
        d = - b - sqrt (w);
        if (d > 0. && d < dMin) {
          dMin = d;
          vn = v + d * rd;
        }
      }
    }
  }
  qnHit = vn / sz;
  return dMin;
}

float ObjHitSh (vec3 ro, vec3 rd, float rng)
{
  mat3 mRot;
  vec3 rm, v;
  float dMin, b, d, w, sz;
  dMin = dstFar;
  sz = 0.5;
  for (int n = 0; n < nObj; n ++) {
    rm = Loadv4 (4 * n).xyz;
    mRot = QtToRMat (Loadv4 (4 * n + 2));
    for (int j = 0; j < nSphObj; j ++) {
      v = ro - (rm + mRot * RSph (float (j)));
      b = dot (rd, v);
      w = b * b + sz * sz - dot (v, v);
      if (w >= 0.) {
        d = - b - sqrt (w);
        if (d > 0. && d < dMin) dMin = d;
      }
    }
  }
  return smoothstep (0.5 * rng, rng, dMin);
}

#endif

vec3 BgCol (vec3 ro, vec3 rd, float sh)
{
  vec3 col, c1, c2, ca;
  float dst;
  c1 = vec3 (0.7, 0.3, 0.1);
  c2 = vec3 (0.4, 0.2, 0.1);
  ca = mix (c1, c2, 0.5);
  if (rd.y < 0.) {
    dst = -ro.y / rd.y;
    ro += dst * rd;
    col = mix (c1, c2, Fbm2 (ro.xz * vec2 (1., 0.15))) *
       (0.5 + 0.5 * smoothstep (0.05, 0.1, mod (ro.x, 4.)));
    col = mix (col, ca, smoothstep (0.7, 1., dst / (4. * dstFar)));
    col *= 0.2 + 0.8 * sh * max (ltDir.y, 0.);
  } else col = mix (ca, vec3 (1.), 0.3);
  return col;
}

vec3 ObjCol (vec3 rd, vec3 vn, float sh)
{
  vec4 col4;
  vec3 col;
  float r;
  col4 = vec4 (0.2, 0.4, 0.6, 0.3);
#if ! SHOW_SPH
  r = length (qHit.xy);
  if (abs (r - cylRad + cylWid) < 0.8 * cylWid || abs (qHit.z) < 0.8 * cylWid) {
    col4.rgb *= 1. - 0.5 * SmoothBump (0.4, 0.6, 0.1, mod (((r > 0.51) ? 16. : 4.) *
       atan (qHit.y, qHit.x) / pi, 1.));
  } else col4.rgb *= 1.2;
#endif
  col = col4.rgb * (0.2 +  0.1 * max (dot (normalize (- ltDir.xz), vn.xz), 0.) +
     0.8 * sh * pow (max (dot (vn, ltDir), 0.), 1.5)) +
     col4.a * sh * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 vn, col, colR;
  vec2 p;
  float dstFlr, dstObj, sh;
  bool isBg;
  cylRad = Loadv4 (4 * nObj).z;
  cylWid = 0.45;
  dstFlr = (rd.y < 0.) ? - (ro.y - 0.5) / rd.y : dstFar;
  dstObj = ObjHit (ro, rd);
  isBg = false;
  if (min (dstObj, dstFlr) < dstFar) {
    if (dstFlr < dstObj) {
      ro += dstFlr * rd;
      vn = vec3 (0., 1., 0.);
      isBg = true;
    } else {
      ro += dstObj * rd;
      vn = qnHit;
    }
  } else isBg = true;
  if (! isBg || rd.y < 0.) {
    sh = ObjHitSh (ro + 0.01 * ltDir, ltDir, 20.);
    sh = 0.4 + 0.6 * sh;
  } else sh = 1.;
  if (! isBg) col = ObjCol (rd, vn, sh);
  else col = BgCol (ro, rd, sh);
  if (isBg && rd.y < 0.) {
    rd = reflect (rd, vn);
    ro += 0.01 * rd;
    dstObj = ObjHit (ro, rd);
    if (dstObj < dstFar) {
      ro += dstObj * rd;
      vn = qnHit;
      colR = ObjCol (rd, vn, 1.);
    } else colR = vec3 (0.4, 0.4, 0.4);
    col = mix (col, colR, 0.3);
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 col, rd, ro;
  vec2 canvas, uv;
  float az, el, asp, zmFac, tCur;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  asp = canvas.x / canvas.y;
  mPtr = Loadv4 (4 * nObj + 1);
  dstFar = 200.;
  az = 0.2 * pi * sin (0.02 * pi * tCur);
  el = -0.15 * pi;
  if (mPtr.z > 0. && (mPtr.x < 0.5 - 0.05 / asp || mPtr.y < 0.5 - 0.05)) {
    el += 2. * mPtr.y;
    az += pi * mPtr.x;
  }
  el = clamp (el, -0.45 * pi, -0.1 * pi);
  zmFac = 3.5;
  vuMat = StdVuMat (el, az);
  rd = vuMat * normalize (vec3 (uv, zmFac));
  ro = vuMat * vec3 (0., 0., -80.);
  ltDir = normalize (vec3 (1., 1.5, -1.));
  col = ShowScene (ro, rd);
  if (mPtr.z > 0. && min (uv.x - asp, uv.y - 1.) > -0.1)
     col = mix (col, vec3 (1., 1., 0.), 0.3);
  fragColor = vec4 (col, 1.);
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 128.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) /
     txSize);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

