mat3 QtToRMat (vec4 q);
vec4 RMatToQt (mat3 m);
mat3 LpStepMat (vec3 a);
vec2 Rot2D (vec2 q, float a);
float Hashff (float p);
vec4 Loadv4 (int idVar);
void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord);

float cylRad, nRun;
const int nObj = 32;
const int nSphObj = 20;
const float txRow = 128.;
const float pi = 3.14159;

vec3 RSph (float s)
{
  vec3 r;
  float a;
  a = (2. * pi / float (nSphObj)) * s;
  r = vec3 ((cylRad - 0.5) * cos (a), (cylRad - 0.5) * sin (a), 0.);
  return r;
}

vec3 FcFun (vec3 dr, float rSep, vec3 dv)
{
  vec3 f;
  float vRel, fo, drv;
  const float fOvlap = 1000., fricN = 1., fricT = 2., fricS = 10.;
  fo = fOvlap * (1. / rSep - 1.);
  drv = dot (dr, dv) / (rSep * rSep);
  dv -= drv * dr;
  vRel = length (dv);
  fo = max (fo - fricN * drv, 0.);
  f = fo * dr;
  if (vRel > 0.001) f -= min (fricT, fricS * abs (fo) * rSep / vRel) * dv;
  return f;
}

void Step (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  mat3 mRot, mRotN;
  vec3 rmN, vmN, wmN, dr, dv, rts, rtsN, rms, vms, fc, am, wam, rMom;
  float farSep, rSep, grav, dt;
  grav = 10.;
  dt = 0.01;
  rm = Loadv4 (4 * mId).xyz;
  vm = Loadv4 (4 * mId + 1).xyz;
  qm = Loadv4 (4 * mId + 2);
  wm = Loadv4 (4 * mId + 3).xyz;
  mRot = QtToRMat (qm);
  farSep = 2. * (cylRad + 1.);
  am = vec3 (0.);
  wam = vec3 (0.);
  for (int n = 0; n < nObj; n ++) {
    rmN = Loadv4 (4 * n).xyz;
    if (n != mId && length (rm - rmN) < farSep) {
      vmN = Loadv4 (4 * n + 1).xyz;
      mRotN = QtToRMat (Loadv4 (4 * n + 2));
      wmN = Loadv4 (4 * n + 3).xyz;
      for (int j1 = 0; j1 < nSphObj; j1 ++) {
        rts = mRot * RSph (float (j1));
        rms = rm + rts;
        vms = vm + cross (wm, rts);
        dv = vms - vmN;
        fc = vec3 (0.);
        for (int j2 = 0; j2 < nSphObj; j2 ++) {
          rtsN = mRotN * RSph (float (j2));
          dr = rms - (rmN + rtsN);
          rSep = length (dr);
          if (rSep < 1.) fc += FcFun (dr, rSep, dv - cross (wmN, rtsN));
        }
        am += fc;
        wam += cross (rts, fc);
      }
    }
  }
  rMom = vec3 (0.);
  for (int j = 0; j < nSphObj; j ++) {
    rts = RSph (float (j));
    rMom += dot (rts, rts) - rts * rts + 1./6.;
    rts = mRot * rts;
    dr = rm + rts;
    rSep = abs (dr.y);
    if (rSep < 1.) {
      fc = FcFun (vec3 (0., dr.y, 0.), rSep, vm + cross (wm, rts));
      am += fc;
      wam += cross (rts, fc);
    }
  }
  rMom /= float (nSphObj);
  wam = mRot * (wam * mRot / rMom);
  am.y -=  grav;
  vm += dt * am;
  rm += dt * vm;
  wm += dt * wam;
  qm = normalize (QtMul (RMatToQt (LpStepMat (0.5 * dt * wm)), qm));
}

void Init (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  vec3 u;
  float b;
  b = 2. * Hashff (33.3 * float (mId) + nRun + 17.11) - 1.;
  rm = vec3 (32. * sin (2. * pi * (float (mId) / float (nObj)) + vec2 (0.5 * pi, 0.)), 6.).xzy;
  u = normalize (vec3 (rm.z, - rm.x, 0.)).xzy;
  u.xz = Rot2D (u.xz, 0.1 * pi * b);
  qm = RMatToQt (DirToRMatT (u, vec3 (0., 1., 0.)));
  vm = vec3 (- 0.3 * (1. + 0.2 * b) * rm.xz, 1.5 * (1. + b)).xzy;
  wm = -5. * u * b;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr, stDat, qm;
  vec3 rm, vm, wm;
  vec2 iFrag, canvas;
  float tCur, asp, mxStep, nStep;
  int mId, pxId, kp;
  bool doInit;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (iFrag.x >= txRow || pxId >= 4 * nObj + 2) discard;
  canvas = iResolution.xy;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  mId = (pxId < 4 * nObj) ? mId = pxId / 4 : -1;
  doInit = false;
  mxStep = 1800.;
  if (iFrame <= 5) {
    doInit = true;
    cylRad = 3.;
    nRun = 0.;
  } else {
    stDat = Loadv4 (4 * nObj);
    nStep = stDat.x;
    cylRad = stDat.z;
    nRun = stDat.w;
    ++ nStep;
    if (nStep > mxStep || nStep > 100. && mPtr.z > 0. && mPtr.x > 0.5 - 0.05 / asp &&
       mPtr.y > 0.5 - 0.05) doInit = true;
  }
  if (doInit) {
    ++ nRun;
    nStep = 0.;
  }
  if (mId >= 0) {
    if (doInit || nStep < 10.) Init (mId, rm, vm, qm, wm);
    else Step (mId, rm, vm, qm, wm);
    kp = 4 * mId;
    if      (pxId == kp + 0) stDat = vec4 (rm, 0.);
    else if (pxId == kp + 1) stDat = vec4 (vm, 0.);
    else if (pxId == kp + 2) stDat = qm;
    else if (pxId == kp + 3) stDat = vec4 (wm, 0.);
  } else {
    kp = 4 * nObj;
    if (pxId == kp) stDat = vec4 (nStep, mxStep, cylRad, nRun);
    else if (pxId == kp + 1) stDat = vec4 (mPtr.xyz, 0.);
