struct Rect
{
    vec2 lb;
    vec2 rt;
};

struct Circle
{
    vec2 o;
    float radius;
};

struct Triangle
{
    vec2 a;
    vec2 b;
    vec2 c;
};

bool intersect(Rect rect, vec2 pos)
{
    bool ret = pos.x > rect.lb.x && pos.x < rect.rt.x;
    ret = ret && pos.y > rect.lb.y && pos.y < rect.rt.y;

    return ret;
}

bool intersect(Circle c, vec2 pos)
{
    return length(pos-c.o) < c.radius;
}

float at_line(vec2 a, vec2 b, vec2 pos)
{
    return (pos.y - a.y)*(b.x-a.x) - (pos.x - a.x) * (b.y-a.y);
}

bool intersect(Triangle tri, vec2 pos)
{
    bool ret = at_line(tri.a, tri.b, pos) > 0.0;
    ret = ret && at_line(tri.b, tri.c, pos) > 0.0;
    ret = ret && at_line(tri.c, tri.a, pos) > 0.0;

    return ret;
}

vec3 compare_two_image(vec3 col1, vec3 col2, float horizontal, vec2 pos)
{
    Rect rect = Rect(vec2(horizontal - 0.002, 0.0), vec2(horizontal + 0.002, 1.0));
    Circle c = Circle(vec2(horizontal, 0.5), 0.08);
    Circle c2 = Circle(vec2(horizontal, 0.5), 0.07);
    Triangle tri1 = Triangle(vec2(horizontal - 0.01, 0.54), 
                             vec2(horizontal - 0.06, 0.5),
                             vec2(horizontal - 0.01, 0.46));
    Triangle tri2 = Triangle(vec2(horizontal + 0.01, 0.54),
                             vec2(horizontal + 0.01, 0.46),
                             vec2(horizontal + 0.06, 0.5)
                             );

    bool flag = intersect(rect, pos);
    flag = flag || (intersect(c, pos) && !intersect(c2, pos));
    flag = flag || intersect(tri1, pos) || intersect(tri2, pos);
    if(flag) return vec3(1.0);

    float factor = pos.x < horizontal ? 0.0 : 1.0f;
    return mix(col1, col2, factor);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

