// Rotate vector p by angle t.
vec2 rotate(vec2 p, float t) {
  return cos(t)*p + sin(t)*vec2(-p.y,p.x);
}

float PI = 3.14159;

vec3 transform(vec3 p) {
  if (iMouse.x > 0.0) {
    // Full range of rotation across the screen.
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    p.yz = rotate(p.yz,-theta);
    p.zx = rotate(p.zx,phi);
  }
  return p;
}

const int CHAR_F = 70;
const int CHAR_L = 76;
const int CHAR_V = 86;
const int CHAR_Z = 90;

bool key(int key) {
  return texelFetch(iChannel3,ivec2(key,2),0).x != 0.0;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  // Orthographic projection - this is the ray direction
  vec3 d = vec3((2.0*fragCoord-iResolution.xy)/iResolution.y, 1);
  d = transform(d);
  vec3 p = vec3(0); // Ray marching origin
  if (!key(CHAR_Z)) p.z = 0.2*iTime;
  vec3 q,p0 = p;
  vec3 lightdir = vec3(1,1,1);
  if (key(CHAR_L)) lightdir.xz = rotate(lightdir.xz,0.123*iTime);
  for (int i = 0; i < 120; i++) {
    float lfactor = 0.5;
    float overlap = 0.65;
    if (key(CHAR_V)) overlap += 0.1*sin(0.1*iTime);
    float floorheight = 0.3;
    float s = overlap-length(fract(p+0.5)-0.5);
    if (!key(CHAR_F)) s = min(s, p.y + floorheight);
    p += lfactor*d*s;
    if (i == 80) {
      // We must have hit the surface by now.
      q = p; // Remember position
      p -= d*.01; // Back up a little to stop self-shadowing
      d = lightdir; // Light direction (for shadows)
    }
  }
  ivec3 u = ivec3(q*5e2); // u in [0..255]
  u = u.yzx^u.zxy;             // Mix up for chequer effect
  u &= 255;
  vec3 col = vec3(u)/255.0;
  col *= 0.25*(length(p-q) + 0.9);
  col += 0.1*(length(p-p0));
  fragColor = vec4(col,1);
}

