// Plento

// You can place and delete walls in the grid.

// Image = Graphics
// Buffer A = wall texture (Try out different ones! or make custom one from here)
// Buffer C = grid traversal
// Buffer B = Input and map data.
// Various params in common

// ---CONTROLS---

// Left/Right or Q/E = turn left/right
// Up/Down or W/S = Walk forwards/backwards. 
// A/D = Walk sideways
// Press 2 to toggle grid. 
// Click on the grid to place walls. 
// Hold 1 and click to erase walls.  
// Blue lines show FOV. 
// Wall texture in Buf A. 

// I learned the traversal algorithm from this article: http://citeseerx.ist.psu.edu/viewdoc/download?doi=10.1.1.42.3443&rep=rep1&type=pdf

// This is still a work in progress

vec2 R;


vec4 t(vec2 p){
    return texelFetch(iChannel1, ivec2(p), 0);
}

float ln( in vec2 p, in vec2 a, in vec2 b ){
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

mat2 rot(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}


float grid(vec2 uv){
    float th = 0.18;

    float g = smoothstep(th, th + .0025 , abs(uv.x)) *
        smoothstep(th - .0025, th + .0025, abs(uv.y));

    return g;
}

int GetTile(vec2 id){
    vec2 vs = R / gs; // Pixel size of cells
    vec2 coord = (vs * id) + 1.; // Jump to cell in question
    float val = texelFetch(iChannel1, ivec2(coord), 0).w; // Get cell value

    if(val == 1.)
        return 1;
    else if(val == 2.)
        return 2;
    else if(val == 0.)
        return 0;   
    else
        return 0;

}


vec3 Editor(vec2 ruv, vec2 uv, vec2 id, vec2 p, float a){
    vec3 col = vec3(1);

    int tile = GetTile(id);

    if(tile == 1)
     	col = vec3(0.0, 0.6, 0.0);	   

	col *= grid(ruv); // Grid

    // Test FOV lines
    col = mix(vec3(0.0, 0.0, 1.0), col, 
              smoothstep(0.004, 0.0041,ln(uv, p, vec2(p.x + 1.*cos(a - (FOV/2.)), p.y + 1.*sin(a - (FOV/2.))))));  
    col = mix(vec3(0.0, 0.0, 1.0), col, 
              smoothstep(0.004, 0.0041,ln(uv, p, vec2(p.x + 1.*cos(a + (FOV/2.)), p.y + 1.*sin(a + (FOV/2.))))));

    return col;
}

void mainImage( out vec4 f, in vec2 u )
{
    R = iResolution.xy;
    vec2 nv = vec2(u.xy - 0.5*R.xy)/R.y;
    vec2 uv = u / R;
    uv *= zoom;

    vec2 m = iMouse.xy / R.xy-.5; 
    m.x *= R.x/R.y;

    vec3 topdown = vec3(1);
    vec3 graphics = vec3(0);
    vec3 col = vec3(1);

    vec2 ruv = fract(uv*gs);
    vec2 id = floor(uv*gs);

    // User input
    vec4 h = texture(iChannel1, u / R);

    // Raycast data
    vec4 ray = texture(iChannel2, u / R);

    // Player cell ID
    vec2 pid = floor(h.xy*gs);

    // Toggle value for editor
    float TWO = texelFetch( iChannel3, ivec2(50,2), 0 ).x; 
    float THREE = texelFetch( iChannel3, ivec2(51,0), 0 ).x; 

    // Map editor color
    topdown = Editor(ruv, uv, id, h.xy, h.z);

    ////// 3D graphics ///////
    float height = (2. / ray.x)*.1;

    height *= 1.-step(FAR, ray.x);

    float fog = smoothstep(2.9, 0.0, ray.x);
    vec3 skyCol = vec3(0., 0.8, 0.8);
    vec3 groundCol = vec3(0.5);

    float a = atan(h.y, h.x);

    // 3D grid lines
    vec2 vg = fract(ray.zw*gs - 0.5);
    vec3 vgCol = vec3(1.0, 0.0, 0.0) * smoothstep(0.027, 0.024, abs((vg.y - vg.x)));

    if(nv.y >= height/2.){ // We are ABOVE wall
        graphics = skyCol;    
    }
    else if(nv.y <= -height/2.){ // We are BELOW wall
        graphics = groundCol;
    }
    else{ // We are ON the wall so lets figure out texture coordinates

        // Normalized distance from uv y-coord to bottom of wall.
        float sDist = (-height/2. - nv.y) / height;

        // Texture coordinate
        vec2 tc = vec2((ray.z*8.0 - ray.w*4.0), sDist);

        // Get wall texture
        vec3 wallCol = texture(iChannel0, -tc).xyz; 

        graphics = (wallCol * fog); // Walls
        //graphics += vgCol; // 3D grid lines
    }


    if(TWO == 1.)
        col = mix(topdown, graphics, 0.55);
    else
        col = graphics;

    f = vec4(col, 1.0);

}

