// Plento
vec2 R;

#define ss(a, b, x) smoothstep(a, b, x)
vec3 hash33(vec3 p3){
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}

mat2 rot(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

#define doorX min(min(mod(iTime*.5, 2.), 0.4), mod(-iTime*.5, 2.))
#define count floor(mod(iTime/4., 3.))

float sdBox( vec3 p, vec3 b ){
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}
float sdTorus( vec3 p, vec2 t ){
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float map(vec3 rp){
    float d = 999.;

    d = min(d, sdBox(rp - vec3(0.0, -0.6, 0.0), vec3(1., 0.1, 1.)));
    d = min(d, sdBox(rp - vec3(0.0, 0.6, 0.0), vec3(1., 0.1, 1.)));
    d = min(d, sdBox(rp - vec3(0.8, 0.0, 0.0), vec3(.1, .6, 1.0)));
    d = min(d, sdBox(rp - vec3(-0.8, 0.0, 0.0), vec3(.1, .6, 1.0)));

    // Doors
    d = min(d, sdBox(rp - vec3(0.7 + doorX, 0.0, 1.0), vec3(.7, .7, 0.01)));
    d = min(d, sdBox(rp - vec3(-0.7 - doorX, 0.0, 1.0), vec3(.7, .7, 0.01)));

    vec3 p = rp - vec3(0., 0., 7.);

    if(count == 0.){
        p.xz *= rot(iTime);
    	d = min(d, sdBox(p, vec3(1., 1., 1.)));
    }
    else if(count == 1.){
        p.xz *= rot(iTime*.4);
        p = mod(p, vec3(2))-vec3(2)*0.5;
        d = min(d, length(p) - 0.3);
    }
    else if(count == 2.)
    {
        p.yz *= rot(iTime*1.7 + p.x*1.4);
     	d = min(d, sdTorus(p, vec2(1.4, 0.2)));   

    }

    return d;
}

vec3 normal( in vec3 pos ){
    vec2 e = vec2(0.002, -0.002);
    return normalize(
        e.xyy * map(pos + e.xyy) + 
        e.yyx * map(pos + e.yyx) + 
        e.yxy * map(pos + e.yxy) + 
        e.xxx * map(pos + e.xxx));
}

float march(vec3 rd, vec3 ro){
 	float t = 0., d = 0.;   

    for(int i = 0; i < 64; i++){
    	d = map(ro + rd*t); 	   
        if(abs(d) < .002 || t > 64.){break;}

        t += d * .85;
    }

    return t;
}

vec3 bg(vec3 rd){
    rd.xy *= rot(iTime*0.15 - rd.z*5.);
    vec3 col = vec3(0);
    float scl = mod(-iTime*.1, 0.5);
    float scl2 = mod(-iTime*.1 + .25, 0.5);

    col += ss(0.86, 0.99, texture(iChannel0, scl*rd.xy).x);
    col *= ss(0.4, 0.1, scl);

    col += ss(0.86, 0.99, texture(iChannel0, scl2*rd.xy).x);
    col *= ss(0.4, 0.1, scl2*0.65);

    return col;
}


vec3 color(vec3 p, vec3 rd, vec3 n, float t){
    vec3 lp = vec3(0.2, -9.0, 4.0);
    vec3 ld = normalize(lp-p);
   	vec3 ref = reflect(rd, n );

    float faloff = 1.-exp(-(4. / length(lp - p)));
    float dom = ss(-0.0, 0.3, ref.y+.1);
    float dif = max(dot(n, ld), .035);

    vec3 col = vec3(0);
    vec3 mat = vec3(0);
    vec3 lights = ((.15*dom*vec3(0.7,0.7,0.9)*dom) + (1.75*dif * faloff));

    if(p.z < 1.2){
    	if(abs(n.y) > 0.5){
        	vec2 id = floor(p.xz*7.);
        	float chk = mod(id.x+id.y,2.);

        	col = mix(vec3(0.1, 0.2, 0.3), vec3(0),chk);

    		float x = abs(p.x);
    		float val = ss(x, x+0.03, doorX);
        	col *= val+.02;

        	col = mix(col, (bg(ref)+0.01), val*.11*chk);   
        }
        else{
    		col = vec3(.4)*abs(sin(p.y*3.16 + 1.57))+.0;
    		col *= lights;
        }
    }
    else{
        if(count == 0.)
            mat = vec3(0.8, 0.2, 0.2);
        else if(count == 1.){
            p.z -= 7.;
            p.xz *= rot(iTime*.4);
            mat = hash33(floor((p-.5)/3.)*102.4+342.);
        }
        else if(count == 2.)
            mat = vec3(0.6, 0.4, 0.0);

        col = mat * lights;
    	col = mix(col, bg(rd), step(10., t));   	   
    }

	return col;   
}


void mainImage( out vec4 f, in vec2 u ){
    R = iResolution.xy;
    vec2 uv = vec2(u.xy - 0.5*R.xy)/R.y;

    vec3 rd = normalize(vec3(uv, 0.6));
    vec3 ro = vec3(0., 0., -.4);

    rd.xy *= rot(sin(iTime*2.)*.07);
    rd.yz *= rot(cos(iTime*2.)*.07);

    float t = march(rd, ro);

    vec3 n = normal(ro + rd*t);
    vec3 col = color(ro + rd*t, rd, n, t);

    col *= ss(0.5, 0.3, abs(uv.y));

    f = vec4(sqrt(clamp(col, .0, 1.)), 1.);

}

