    return rgb * conv_rgb2xyz;

    vec3 xyz;
    xyz.x = rgb.r * 0.4124 + rgb.g * 0.3576 + rgb.b * 0.1805;
	xyz.y = rgb.r * 0.2126 + rgb.g * 0.7152 + rgb.b * 0.0722;
