}


vec3 xyz2lab(vec3 xyz) {
    // Observer= 2A, Illuminant= D65
    // x_ref=95.047, y_ref=100., z_ref=108.883

    const vec3 ref = vec3(0.95047, 1.0, 1.08883);

    xyz /= ref;

    if (xyz.x > 0.008856)
        xyz.x = pow(xyz.x, 1.0/3.0);
    else
        xyz.x = (7.787*xyz.x) + (16.0/116.0);

    if (xyz.y > 0.008856)
        xyz.y = pow(xyz.y, 1.0/3.0);
    else
        xyz.y = (7.787*xyz.y) + (16.0/116.0);

    if (xyz.z > 0.008856)
        xyz.z = pow(xyz.z, 1.0/3.0);
    else
        xyz.z = (7.787*xyz.z) + (16.0/116.0);

    return vec3(
        (1.16 * xyz.y) - 0.16,
    	5.0 * (xyz.x - xyz.y),
    	2.0 * (xyz.y - xyz.z));
}


