    //jump flood sorting 
    sortpos(p); //resort this position
    for(int i = 0; i < 8; i++)
    {
        sortpos(p+cross_distribution(i)); 
    }

    ivec4 dnb = getnb(u.x);
    for(int i = 0; i < 4; i++)
