float hash(float a, float b) {
  return fract(sin(dot(vec2(a,b), vec2(12.9898, 78.233))) * 43758.5453)*2.-1.;
}

float id;
float scene(vec3 p) {
  float xscale = 1.;
  id = floor(p.x*xscale);
  p.x = (fract(p.x*xscale)-0.5)/xscale;

  float m1 = hash(id, 69.);
  float m2 = hash(id, 38.);
  float m3 = hash(id, 41.);
  float myTime = iTime + m3*10.;
  p.yz += vec2(m1,m2)*0.2 * sin(myTime*2.);

  return 0.8*(length(vec2(length(p.yz)-0.5, p.x))-0.1);
}

vec3 norm(vec3 p) {
  mat3 k = mat3(p,p,p)-mat3(0.001);
  return normalize(scene(p) - vec3(scene(k[0]), scene(k[1]), scene(k[2])));
}

vec3 srgb(float a, float b, float c) {
  return pow(vec3(a,b,c), vec3(2.));
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax,p)*ax, p, cos(ro)) + sin(ro)*cross(ax,p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = (fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;

  float n1 = hash(hash(uv.x, uv.y), iTime * 11.);
  float n2 = hash(hash(uv.x, uv.y), iTime * 23.);
  uv += vec2(n1,n2)*0.002;

  vec3 cam = normalize(vec3(1, uv));
  float rot = iTime;
  float shift = iTime;
  float outside = smoothstep(0.0,0.5, asin(sin(iTime*acos(-1.)/4.)));
  float look = mix(0.05, 0.4, outside);
  vec3 init = vec3(shift,0,0) + vec3(0, sin(rot), cos(rot))*mix(0.15, 1., outside);
  cam = erot(cam, vec3(0,1,0), cos(rot)*look);
  cam = erot(cam, vec3(0,0,1), -sin(rot)*look);
  vec3 p = init;
  bool hit = false;
  float mini = 100000.;
  int i;
  for (i = 0 ; i < 100 && !hit; i++) {
    float dist = scene(p);
    mini = min(dist, mini);
    hit = dist*dist < 1e-6;
    p+=dist*cam;
  }
  float glow = pow(exp(-mini*20.), 2.);
  float idloc = id;
  float fog = (float(i)/100.);
  vec3 n = norm(p);
  vec3 r = reflect(cam, n);
  float diff = length(sin(n*2.)*0.5+0.5)/sqrt(3.);
  float spec = length(sin(r*2.)*0.5+0.5)/sqrt(3.);
  vec3 col = mix(srgb(0.4,0.1,0.2), srgb(0.9,0.7,0.4), spec);
  col = abs(erot(col, normalize(vec3(1,7,1)), idloc)) + pow(spec, 15.)*2.;
  vec3 bg = mix(srgb(0.5, 0.2, 0.3), srgb(0.2, 0.1, 0.3), sqrt(length(uv))) + glow*0.2;
  fragColor.xyz = hit ? mix(col, bg, fog) : bg;
  fragColor.xyz = sqrt(fragColor.xyz) + hash(hash(uv.x, uv.y), iTime)*0.04;

  fragColor.xyz = smoothstep(vec3(-0.1), vec3(1.1), fragColor.xyz);
}

const int num_colors = 7;
const float threshold = 2.5;
const float threshold_aa = 0.1;

const vec3 colors[num_colors] = vec3[num_colors](
    vec3(0.960,0.321,0.521),
    vec3(1.000,0.572,0.321),
    vec3(0.949,0.917,0.321),
    vec3(0.403,1.000,0.407),
    vec3(0.321,0.909,0.949),
    vec3(0.376,0.627,1.000),
    vec3(0.694,0.423,0.972)
);

#define TAU 6.28318530
#define PI  3.14159265

const float tauToColorRange = float(num_colors) / TAU;

float phi(const float r, const vec2 pos) {
    if (pos.y >= 0.0) {
    	return acos(pos.x / r);
    }
    return -acos(pos.x / r);
}

vec3 rainbow(const vec2 polar) {
    // Scale
    float x = polar.x * 4.0;

    // Rotate
  	float y = polar.y + iTime;

    int index = int(mod(x * 0.9 + y * tauToColorRange, float(num_colors)));
    return colors[index];
}

vec3 metaball(float time, float theta, float start) {
    float theta_2 = mod(theta + time, TAU);
    vec2 angle_vec = vec2(sin(theta_2), cos(theta_2));
    float scale = mod((theta / PI) + time / 2.0, 1.5);
    angle_vec *= 1.5 - scale;
    scale /= 32.0;
    return vec3(angle_vec, start);
}

void mainImage(out vec4 fragColor, in vec2 pos)
{
    // Center
    pos -= iResolution.xy / 2.0;

    // Normalize
    vec2 normPos = pos * 2.0 / iResolution.xy;
    // Normalize aspect ratio
    float aspectRatio = iResolution.x / iResolution.y;
    normPos.x *= aspectRatio;

    // Convert to polar coordinates
    vec2 polar;
    polar.x = length(normPos);
    polar.y = phi(polar.x, normPos);

    // vec3(x, y, radius);
    vec3 metaballs[] = vec3[](
        vec3(0.000000000000, 0.0000000, 0.100),
      	vec3(metaball(iTime, 0.0000000, 0.001)),
        vec3(metaball(iTime, PI * 1.00, 0.001)),
        vec3(metaball(iTime, PI * 0.50, 0.001)),
        vec3(metaball(iTime, PI * 1.50, 0.001)),
        vec3(metaball(iTime, PI * 0.30, 0.001)),
        vec3(metaball(iTime, PI * 0.60, 0.001)),
        vec3(metaball(iTime, PI * 1.70, 0.001)),
      	vec3(metaball(iTime, PI * 1.10, 0.01)),
        vec3(metaball(iTime, PI * 0.10, 0.001)),
        vec3(metaball(iTime, PI * 0.20, 0.001)),
        vec3(metaball(iTime, PI * 1.30, 0.001)),
        vec3(metaball(iTime, PI * 0.13, 0.001)),
        vec3(metaball(iTime, PI * 0.56, 0.001)),
        vec3(metaball(iTime, PI * 0.70, 0.001))
    );

    // Sum all metaball strengths
	float value = 0.0;
    for (int i = 0; i < metaballs.length(); i += 1) {
        value += metaballs[i].z / pow(distance(metaballs[i].xy, normPos), 3.0);
    }

    // Color based on metaball strength and threshold
    if (value > threshold) {
    	fragColor.xyz = rainbow(polar);
    } else if (value > threshold - threshold_aa) {
        float t = smoothstep(threshold, threshold - threshold_aa, value);
    	fragColor.xyz = mix(rainbow(polar), vec3(0.0), t);
    } else {
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
    }
}

const vec3 white = vec3(1., 1., 1.);
const vec3 black = vec3(0., 0., 0.);
const vec3 gray  = vec3(.2, .2, .3);

// circleTest
//
// coord      - pixel to test
// center     - center of circle
// radius     - radius of circle
// width      - thickness of circle line
// pixelWidth - blendiness

vec3 circleTest(vec2 coord, vec2 center, float radius, float width, float pixelWidth) {
    float delta = distance(coord, center)-radius;

    // From shadertoy default shader
    vec3 inside = .5 + .5*cos(iTime+coord.xyx+vec3(0,2,4));

    float blend = smoothstep(0., pixelWidth, abs(delta) - width);

    if (delta  < 0.)
        return mix(white, inside, blend); // rgb -> white (inside  edge)
    else if (delta  > 0.)
        return mix(white, black, blend);  // white -> black (outside edge)
    else
        return white;					  // can't decide

}

// Credit: https://thebookofshaders.com/edit.php#10/ikeda-simple-grid.frag
//
float grid(vec2 st, float res){
    vec2 grid = fract(st*res);
    return 1.-(step(res,grid.x) * step(res,grid.y));
}


void mainImage(out vec4 out_color, vec2 fragCoord) {

    float aspect     = iResolution.y/iResolution.x,
          pixelWidth = 1./iResolution.x,
          width      = 2./iResolution.x,
          maxPetals  = 12.,
          br         = .1;


    vec2 dMouse = iMouse.xy == vec2(0) ? iResolution.xy/2. : iMouse.xy,
         cen    = vec2(1., aspect) / 2.,
    	 q      = fragCoord.xy/iResolution.x,
    	 nq     = q - cen,
    	 m      = dMouse.xy/iResolution.x,
         nm     = m - cen;

    float a   = atan(nq.y,nq.x);
    float p   = ceil(maxPetals * m.x);
	float vr  = 8. * br * aspect * nm.y; // -1./petals keeps bottom row as vr=0
    float s   = sin(p * a);
    float r   = br  + vr * s;

    vec3 c = circleTest(
        q, 
        cen,
        r,
        width, 
        pixelWidth
    );

    c = c == vec3(0.) ? gray * grid(fragCoord, maxPetals/iResolution.x ) : c;

    out_color = vec4(c, 1.);
}

