#define RIPPLE_NUM 24
#define TRAIL 0.9
#define SCALE 0.1
#define SPEED 3.0
float hash1(vec2 p)
{
	return fract(sin(dot(p.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}
vec3 ripple(vec2 p, vec2 cen, float r, float t) {
    p -= cen;
    float len = length(p)/r;
    float len2 = len*len;
    float n1 = cos(len-t+sin(cen.x+cen.y)) * exp(-0.65*len2);
    float n2 = 0.7*cos(len-t+cen.x*1.0+cen.y*2.0) * exp(-0.63*len2);
    float n3 = cos(len-t+cen.x*3.0-cen.y*1.0) * exp(-0.61*len2);
    vec3 rp = vec3(n1,n2,n3) * 1.2;
    return rp * rp;
}

vec3 draw(in vec2 uv, in vec2 mo, in float r, in float t) {
    vec3 col = vec3(0.0);
    float l = 1.0-length(uv);
    col += vec3(0.0,0.05,0.1) * pow(32.0, l) * (0.6 + 0.4*sin(0.5*t));
    col += ripple(uv, mo, r, t);
    for (int i = 0; i < RIPPLE_NUM; i++) 
    {
      float n = fract(float(i) * 1.731 + 0.131);
      vec2 cen = -1.0 + 2.0*vec2(hash1(vec2(n*11.717, 10.0*n*n)), 
                               hash1(vec2(fract(n*11.511), sin(n))));
      //vec2 cen = vec2(0.0);
      cen +=  0.5*vec2(sin(1000.0*n + t), sin(100.0*cos(n) + t));
      col += ripple(uv, cen, r + 0.2*sin(t+11.1*n)*r, t - 2.0*n);  
    }

	return col;
}

