//#define BW
#define KEY_R 82
#define KEY_D 68

vec4 get(int index, sampler2D chan, vec3 res) {
    ivec2 p;
    p.x = index%int(res.x);
    p.y = index/int(res.x);
    return texelFetch(chan,p,0);
}

float lum(vec4 c)
{
    return dot(c.rgb*vec3(0.3,0.59,0.11),vec3(1.));
}

vec4 bw(vec4 col)
{
    col.r = (col.r+col.g+col.b)/3.;
    return col.rrra;
}

vec4 init(sampler2D chan, vec2 uv)
{
    vec4 col = texture(chan, uv);
#ifdef BW
    return bw(col);
#else
    return col;
#endif
}

bool isSorted(vec4 a, vec4 b)
{
    return lum(a) <= lum(b);
}

vec4 compareAndSwap(int i, int j, int isSecond, int size, sampler2D chan, vec3 res)
{
    vec4 a = get(i, chan, res);
	if (j<0 || j>=size) return a;
    vec4 b = get(j, chan, res);

    if (isSecond!=0)
    {
        // note we have to keep the order of comparison, or we will lose pixels!
        // as we can have less(a,b)==less(b,a), both false.
        if (isSorted(b,a)) return a;
        return b;
    }
    if (isSorted(a,b)) return a;
    return b;
}

ivec4 config(vec3 res, int globFrame)
{
    int size = int(res.x)*int(res.y);

    int len = int(log2(float(size)));
    len = len*len+60;

    int frame = globFrame%len;

	int set = globFrame/len%9;

    return ivec4(size,len,frame,set);
}

int xy2i(vec2 pos, vec3 res)
{
    return int(pos.x)+int(res.x)*int(pos.y);
}

