void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec4 conf = config(iResolution,iFrame);
    int index = xy2i(fragCoord, iResolution);
    vec2 uv = fragCoord/iResolution.xy;

    if (conf.w==0)
    {
        // horizontal split, only 4 values
        fragColor = vec4(float(int(fragCoord.x)*4/int(iResolution.x))/4.);
    }
    else if (conf.w==1)
    {
        // reversed
        fragColor = vec4(float(conf.x-index-1)/float(conf.x));
    }
    else if (conf.w==2)
    {
        // color gradient
        fragColor = vec4(1.-uv.y, 1.-uv.x, uv.y, 0.);
    }
    else if (conf.w==3)
    {
        // all sorted, but last element moved to front.
        fragColor = vec4(float((index+conf.x-1)%conf.x)/float(conf.x));
    }
    else if (conf.w==4)
    {
        // even are sorted, odd are reversed
        if (index%2==0)
            fragColor = vec4(0.,float(index)/float(conf.x),0.,0.);
        else
            fragColor = vec4(float(conf.x-index-1)/float(conf.x),0.,0.,0.);
    }
    else if (conf.w==5)
    {
        // London texture
        fragColor = vec4(init(iChannel0, uv).rgb,0.);
    }
    else if (conf.w==6)
    {
        // Alphabet texture
        fragColor = vec4(init(iChannel1, uv).rgb,0.);
    }
    else if (conf.w==7)
    {
        // Blue noise texture
        fragColor = vec4(init(iChannel2, uv).rgb,0.);
    }
    else
    {
        // RGB noise texture
        fragColor = vec4(init(iChannel3, uv).rgb,0.);
    }
    fragColor.a = 0.; // frame number
}

