struct Model {
    float d;
    vec3 p;
    bool isBound;
    int id;
    bool isBloom;
    vec2 uv;
    vec2 cell;
    float wedges;
    float slice;
    float len;
    float neg; // subtract from distance
    float crackdepth;
};

Model newModel() {
    return Model(1e12, vec3(0), false, 0, false, vec2(0), vec2(0), 0., 0., 0., 1e12, 0.);
}

Model opU(Model a, Model b) {
    Model m = a;
    if (b.d < a.d) {
        m = b;
    }
    m.neg = min(a.neg, b.neg);
    return m;
}


