#define VERY_SMOL 0.001
#define PI 3.14159264
#define TAU (PI*2.)
#define MAX_DIST 8.0
#define IBL 64
#define MOUSE_CONTROLS 0
float roughness = 1.;
float sdf(vec3 p)
{
  vec3 q = abs(p) - vec3(2.);
  return length(max(q,0.)) + min(max(q.x,max(q.y,q.z)),0.);
}
float rayMarch(vec3 ro, vec3 rd, float start)
{
    float t = start;
    for (int i = 0; i < 256; ++i)
    {
        float d = sdf(ro + rd * t);
        if (d < VERY_SMOL)
            return t;
        t += d;
        if (t >= MAX_DIST) return MAX_DIST;
    }
    return -t;
}
vec3 getNorm(vec3 p) {
    return normalize(vec3(
    	sdf(vec3(p.x + VERY_SMOL, p.y, p.z)) - sdf(vec3(p.x - VERY_SMOL, p.y, p.z)),
    	sdf(vec3(p.x, p.y + VERY_SMOL, p.z)) - sdf(vec3(p.x, p.y - VERY_SMOL, p.z)),
    	sdf(vec3(p.x, p.y, p.z + VERY_SMOL)) - sdf(vec3(p.x, p.y, p.z - VERY_SMOL))
    ));
}

vec2 hammersley(uint i, uint n) {
	return vec2(
        float(i) / float(n),
    	float(reverseuint(i)) * (1./4294967296.0)
    );
}
vec3 importanceSampleGGX(vec2 xi, vec3 n)
{
    float a = roughness * roughness;
    float phi = 2. * PI * xi.x;
    float cos_theta = sqrt((1.-xi.y) / (1. + (a*a - 1.) * xi.y));
    float sin_theta = sqrt(1. - cos_theta * cos_theta);

    vec3 h;
    h.x = sin_theta * cos(phi);
    h.y = sin_theta * sin(phi);
    h.z = cos_theta;

    return h;
}

float gSmith(float NoV, float NoL)
{
    float a2 = roughness * roughness;
    float lambda_v = (-1. + sqrt(a2 * (1. - NoL*NoL) / (NoL*NoL) + 1.)) * .5;
    float lambda_l = (-1. + sqrt(a2 * (1. - NoV*NoV) / (NoV*NoV) + 1.)) * .5;
    float g_smith = 1. / (1. + lambda_v + lambda_l);
    return g_smith;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 scrpos = uv * 2. - 1.;
    float aspect_ratio = iResolution.x / iResolution.y;

    roughness = iMouse.x / iResolution.x;

  #if MOUSE_CONTROLS
    mat4 view_mat =  rotY(-iMouse.x / iResolution.x * 2. * TAU) * rotX((iMouse.y / iResolution.y - PI) * 2. * TAU);
  #else
    mat4 view_mat =  rotY(iTime*.1 * TAU) * rotX(-.1 * TAU);  
  #endif

    int seed = genSeed(int(fragCoord.x + fragCoord.y * iResolution.x) + genSeed(int(iTime * 1000.0)));
    vec3 cam_pos = (view_mat * vec4(0.0, 0.0, -5.0, 1.0)).xyz;
    vec3 color = vec3(0.0);

    vec3 ro = cam_pos;        
    vec3 rd = (view_mat * vec4(normalize(vec3(
        scrpos.x * aspect_ratio,
        scrpos.y ,
        1.0)), 0.0)).xyz;
    float t = rayMarch(ro, rd, 0.01);
    if (t >= MAX_DIST) //miss
    {
        color += texture(iChannel0, rd).xyz;
    }
    else //hit
    {
    	vec3 ip = ro + rd * t;
    	vec3 n = getNorm(ip);

        //calculage tangents
        vec3 up = abs(n.z) < 0.999 ? vec3(0,0,1) : vec3(1,0,0);
        vec3 tangent = normalize(cross(up, n));
        vec3 bitangent = cross(n, tangent);

        //sample roughness from a texture
        vec2 texcoord = vec2(
            dot(ip/4., tangent),
            dot(ip/4., bitangent)) + vec2(0.5);
        roughness = 1.-texture(iChannel1, texcoord).x;
        //roughness = iMouse.x / iResolution.x;//

        //calculate specular
        vec3 spec_color = vec3(.95, .64, .54);
        vec3 spec = vec3(0);
        vec3 diff = vec3(0);
        for(int i = 0; i < IBL; ++i)
        {
            vec2 xi = hammersley(uint(i), uint(IBL));
            vec3 h = importanceSampleGGX(xi, n);
            h = tangent * h.x + bitangent * h.y + n * h.z;

            vec3 v = -rd;
            vec3 l = 2. * dot(v, h) * h - v;

            float NoV = clamp(dot(n, v), 0., 1.);
            float NoL = clamp(dot(n, l), 0., 1.);
            float NoH = clamp(dot(n, h), 0., 1.);
            float VoH = clamp(dot(v, h), 0., 1.);

            if(NoL > 0.)
            {
                vec3 sample_ = textureLod(iChannel0, l, 0.).xyz;
                float g = gSmith(NoV, NoL);
                float fc = pow(1.-VoH, 5.);
                vec3 f = (1.-fc) * spec_color + vec3(fc);
                spec += sample_ * f * g * VoH / (NoH * NoV);
                diff += sample_ * NoL*spec_color;
            }

        }
        spec /= float(IBL);
        diff /= float(IBL);
        color += spec + diff;
    }



    // Output to screen
    fragColor = vec4(pow(color,vec3(1./2.2)),1.0);
}

