//http://www.reedbeta.com/blog/quick-and-easy-gpu-random-numbers-in-d3d11/
int genSeed(int seed) {
	seed = (seed ^ 61) ^ (seed >> 16);
    seed *= 9;
    seed = seed ^ (seed >> 4);
    seed *= 0x27d4eb2d;
    seed = seed ^ (seed >> 15);
    return seed;
}
float rand(inout int seed) {
	seed ^= (seed << 13);
	seed ^= (seed >> 17);
	seed ^= (seed << 5);
    return float(uint(seed)) * 2.3283064370807974e-10;
}
mat4 rotY(float r)
{
    float c = cos(r);
    float s = sin(r);
    return mat4(
        vec4(c,0,s,1),
        vec4(0,1,0,1),
        vec4(-s,0,c,1),
        vec4(0,0,0,1)
    );
}
mat4 rotX(float r)
{
    float c = cos(r);
    float s = sin(r);
    return mat4(
        vec4(1,0,0,0),
        vec4(0,c,-s,0),
        vec4(0,s,c,0),
        vec4(0,0,0,1)
    );
}
uint reverseuint(uint bits) {
    bits = (bits << 16) | (bits >> 16);
    bits = ((bits & 0x00ff00ffu) << 8) | ((bits & 0xff00ff00u) >> 8);
    bits = ((bits & 0x0f0f0f0fu) << 4) | ((bits & 0xf0f0f0f0u) >> 4);
    bits = ((bits & 0x33333333u) << 2) | ((bits & 0xccccccccu) >> 2);
    bits = ((bits & 0x55555555u) << 1) | ((bits & 0xaaaaaaaau) >> 1);
    return bits;   
}

