/*

==================== Sorting series ====================

https://www.shadertoy.com/view/tsByzt - odd-even sorting network (aka parallel bubble sort).
https://www.shadertoy.com/view/tsBczt - odd-even horizontal-vertical experiment.
https://www.shadertoy.com/view/tdByzd - (2^N)-1 comb sorting network.
https://www.shadertoy.com/view/tsScWG - Leonardo comb sorting network.

Press 'R' to Reset.
Hold 'D' or hold left mouse button to check neighbour Difference. To see how much sorted it is.

*/

int D(vec2 p, float n) {
    int i=int(p.y), b=int(exp2(floor(30.-p.x-n*3.)));
    i = ( p.x<0.||p.x>3.? 0:
    i==5? 972980223: i==4? 690407533: i==3? 704642687: i==2? 696556137:i==1? 972881535: 0 )/b;
 	return i-i/2*2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    if (iMouse.z>0. && iMouse.x<fragCoord.x || iMouse.z<=0. && uv.x>.5+.3*sin(iTime*.5) || texelFetch(iChannel1,ivec2(68,0),0).r>0.)
        fragColor = lum(texelFetch(iChannel0,ivec2(fragCoord),0)) < lum(texelFetch(iChannel0,ivec2(fragCoord)-ivec2(1,0),0)) ? vec4(0.) : vec4(1.);
    else
	    fragColor = texelFetch(iChannel0,ivec2(fragCoord),0);

    // sorting set number
    vec2 i = fragCoord / 10.;
    int size = int(iResolution.x)*int(iResolution.y);
    int s1 = int(log2(float(size)));
    int steps = s1*s1;
    int set = iFrame/steps%6;
    if ((i.x-=1.)<3. && i.y<7.) { fragColor += vec4(D(i,float(set))); }
}

