    if (iFrame%steps==0 || get(0,iChannel2,iResolution).xy != iResolution.xy || texelFetch(iChannel3,ivec2(82,0),0).r>0.)
    {
        int index = int(fragCoord.x)+int(iResolution.x)*int(fragCoord.y);


        int set = iFrame/steps%6;
        if (set==0)
        {
            // horizontal split, only 4 values
            fragColor = vec4(float(int(fragCoord.x)*4/int(iResolution.x))/4.);
        }
        else if (set==1)
        {
            // reversed
            fragColor = vec4(float(size-index-1)/float(size));
        }
        else if (set==2)
        {
            // color gradient
	        fragColor = vec4(1.-fragCoord.y/iResolution.y, 1.-fragCoord.x/iResolution.x, fragCoord.y/iResolution.y, 0.);
        }
        else if (set==3)
        {
            // all sorted, but last element moved to front.
            fragColor = vec4(float((index+size-1)%size)/float(size));
        }
        else if (set==4)
        {
            // even are sorted, odd are reversed
            if (index%2==0)
	            fragColor = vec4(0.,float(index)/float(size),0.,0.);
            else
                fragColor = vec4(float(size-index-1)/float(size),0.,0.,0.);
        }
        else
        {
            // London texture
        	fragColor = vec4(init(iChannel1, fragCoord/iResolution.xy).rgb,0.);
        }
        fragColor.a = 0.; // frame number
    }
    else
    {
        int index = int(fragCoord.x)+int(iResolution.x)*int(fragCoord.y);
        int frame = int(texelFetch(iChannel0,ivec2(fragCoord),0).a*256.);
        fragColor = update(index, int(iResolution.x)*int(iResolution.y), frame);
    	fragColor.a = float(frame+1)/256.;
    }
}

