#define PI 3.14
#define col0 vec4(0.58,0.56,0.66,1.0)
#define col1 vec4(0.39,0.29,0.4,1.0)
#define col2 vec4(0.09,0.01,0.05,1.0)
#define sunCol vec4(0.9,1.0,0.87,1.0)

float waveAlpha(vec2 uv, float height, float rate, float amplitude, float xOffset, float yOffset, float yWaveAmount){
    return step(uv.y-height+sin(iTime + yOffset)*yWaveAmount, (sin(uv.x*PI*rate+ sin(xOffset*iTime))*0.5 + 0.5)*amplitude);
}
float circleAlpha(vec2 uv, vec2 pos, float radius){
    uv.y = uv.y*iResolution.y/iResolution.x;
 	return step(distance(uv,pos),radius);
}
float circleAlphaOutline(vec2 uv, vec2 pos, float radius){
    uv.y = uv.y*iResolution.y/iResolution.x;
 	return clamp(1.0-distance(uv,pos)/radius,0.0,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	float wave0 = waveAlpha(uv, 0.2, 8.0, 0.15, 0.25, 0.0, 0.01);
	float wave1 = waveAlpha(uv, 0.1, 8.0, 0.15, -0.5, 0.1, 0.025);
    float wave2 = waveAlpha(uv, 0.0, 8.0, 0.15, 0.5, 0.2, 0.03);
	float circle = circleAlpha(uv, vec2(0.75,0.4), 0.1);
    float circleOutline = circleAlphaOutline(uv, vec2(0.75,0.4), 0.4);
    vec4 col = vec4(0.73,0.8, 0.8, 1.0);
    col = mix(col,col0,wave0);
    col = mix(col,col1, wave1);
    col = mix(col,col2, wave2);
    col = mix(col, sunCol, circle);
    col = mix(col, sunCol, circleOutline);
    //col += vec4(circleOutline);
    // Output to screen
    fragColor = col;
}

