#define switch_after 2
#define shift_right_when_vertical 1

vec4 update(int i, int size, int frame)
{
    int width = int(iResolution.x);
    int vertical = frame/switch_after%2;

    int isSecond = (i+frame)%2;
    int j = i + 1 - isSecond*2;

    if (vertical!=0)
