#ifdef GL_FRAGMENT_PRECISION_HIGH
precision highp float;
#else
precision mediump float;
#endif



float line = 0.1;

float smin( float d1, float d2)
{
	float k = 0.6521; k = 1.1;
	float h = clamp( 0.5+0.5*(d2-d1)/k, 0.0, 1.0 );
	return mix( d2, d1, h ) - k*h*(1.0-h);
}

float f(vec2 uv)
{
	float x = uv.x;
	float y = uv.y;
    float f1 = cos((iTime + x)) - y;
    float f2 = sin(iTime + x)*abs(cos(iTime)) - y;
    //return smin(f1, f2);
    return f1;
}

vec2 df( in vec2 x )

{
	vec2 h = vec2( 0.01, 0.001 );
	return vec2( f(x+h.xy) - f(x-h.xy), f(x+h.yx) - f(x-h.yx) )/(2.0*h.x);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord/iResolution.xy * vec2(10.0f, 10.0f);
    uv += vec2(0.0, -4.0);
	float col = float (abs(f(uv))/length(df(uv)));
	col = abs(f(uv))/sqrt(1.0 + pow(df(uv).x, 2.0));
	fragColor = vec4(smoothstep(0.98* line, line, col));
}

vec4 sphere = vec4(0.0, 0.0, 1.0, 0.4);
vec3 lightPos = vec3(-9.0, 8.0, -8.0);
vec3 lightCol = vec3(1.0, 1.0, 1.0);

float sphereDist1(vec3 ro, vec3 rd)
{
	float R = sphere.w;
	vec3 d1 = sphere.xyz - ro;
	float b = dot(rd, d1);
	float d2 = dot(d1, d1) - b * b;
	float h2 = R * R - d2;
    if (h2 <= 0.0)
        return (-1.0);
    return (b - sqrt(h2));
}

float sphereDist(vec3 p)
{
 return (length(p - sphere.xyz) - sphere.w);   
}

vec3 hsv2rgb(vec3 c)
{
    vec3 rgb = clamp(abs(mod(c.x * 6.0 + vec3(0.0,4.0,2.0),6.0) - 3.0) - 1.0, 0.0, 1.0);
	return (c.z * mix(vec3(1.0), rgb, c.y));
}

// https://iquilezles.org/articles/rmshadows
float getShadow(vec3 ro, vec3 rd, float tmin, float tmax, const float k)
{
	float res = 1.0;
    float t = tmin;
    for( int i = 0; i < 50; i++)
    {
		float h = sphereDist(ro + rd * t);
        res = min(res, k * h / t);
        t += clamp(h, 0.02, 0.20);
        if(res < 0.005 || t > tmax)
            break;
    }
    return clamp(res, 0.0, 1.0);
}

// https://iquilezles.org/articles/normalsSDF
vec3 getNormal(vec3 pos)
{
    const float ep = 0.0001;
    vec2 e = vec2(1.0, -1.0) * 0.5773;
    return normalize( e.xyy*sphereDist(pos + e.xyy*ep) + 
					  e.yyx*sphereDist(pos + e.yyx*ep) + 
					  e.yxy*sphereDist(pos + e.yxy*ep) + 
					  e.xxx*sphereDist(pos + e.xxx*ep));
}


vec2 f(vec2 z)
{
    float mag = dot(z, z);
    return (2.0 * z + vec2(z.x * z.x - z.y * z.y, -2.0 * z.x * z.y) / (mag * mag)) / 3.0;
}

vec2 roots[] = vec2[](vec2(1.0, 0.0), vec2(-0.5, 0.5 * sqrt(3.0)), vec2(-0.5, -0.5 * sqrt(3.0)));

float newton(vec2 z) 
{
    vec4 res;
    for (int i = 0; i < 100; ++i)
    {
    	z = f(z); 
        for (int root = 0; root < roots.length(); ++root)
        {
            vec2 differ = z - roots[root];
            float dist = dot(differ, differ);
            if (dist < 0.00001)
				return (0.78 * cos(1.5 * log2(log(dist) / log(0.0001))));
        }
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
   vec2 aspectRatio = vec2(iResolution.x / iResolution.y, 1.0);
    vec2 uv = aspectRatio * (fragCoord.xy / iResolution.xy - 0.5);
    vec2 mouse = 7.0 * (iMouse.xy / iResolution.xy - 0.5);  
    vec3 ro = vec3(0.0, 0.0, 0.0);
    vec3 rd = normalize(vec3(uv, 1.0));
    mouse.y += 0.3;
	float rot = -0.2;//iTime * 0.2;
    mat3 rotX = mat3(
        vec3(cos(mouse.x - rot), 0.0, sin(mouse.x - rot)),
        vec3(0.0, 1.0, 0.0),
        vec3(-sin(mouse.x - rot), 0.0, cos(mouse.x - rot)));  
	mat3 rotY = mat3(
        vec3(1.0, 0.0, 0.0),
        vec3(0.0, cos(mouse.y), sin(mouse.y)),
        vec3(0.0, -sin(mouse.y), cos(mouse.y)));

    float dSphere = sphereDist1(ro, rd);
	vec3 pos = ro + dSphere * rd;
	vec3 normal = getNormal(pos);
	vec3 v3 = rotX * rotY * normal;
    vec2 v2 = 9.0 * (cos(iTime * 0.2)) * v3.xy / abs(v3.z);
    vec3 material;
    if (dSphere < 0.0)
        material = hsv2rgb(vec3(newton(v2), 0.8, 0.5));
    else
        material = hsv2rgb(vec3(newton(v2), 1.0, 1.2));
    vec3 lightDir = normalize(vec3(lightPos - pos));
    vec3 reflectDir = normalize(reflect(lightDir, normal));
    float shadow = getShadow(pos, lightDir, 0.001, 1.0, 32.0);

    float diffuse = clamp(dot(normal, lightDir), 0.0, 1.0) * shadow * 0.4;
    float ambient = 0.4 + 0.55 * normal.y;
	float specular = pow(clamp(dot(reflectDir, rd), 0.0, 1.0), 32.0) * 1.2 * shadow;

	vec3 color = lightCol * material * (diffuse + specular + ambient);
    color = sqrt(color);
    color = pow(color, lightCol);
    fragColor = vec4(color, 1.0); 
}

#define MAX_STEPS 100
#define ACCURACY 0.001
#define MAX_DIST 100.0


float GetDist(vec3 p)
{
	vec4 s = vec4(0, 1, 6, 1); //(posx, posy, poz, radius)
	float sphereDist =  length(p - s.xyz) - s.w;

	float planeDist = p.y; //Plane formed by world x and world z
	float d = min(sphereDist, planeDist);

	return d;
}

vec3 GetNormal(vec3 p)
{
	float d = GetDist(p);
	vec2 e = vec2(.01, 0);

	vec3 n = d - vec3(	GetDist(p-e.xyy),
						GetDist(p-e.yxy),
						GetDist(p-e.yyx)
					 );

	return normalize(n);
}

float rayMarch(vec3 ro, vec3 rd)
{
	float traveled = 0.;

	for (int i=0; i<MAX_STEPS; i++)
	{
		vec3 p = ro + rd*traveled;
		float dist = GetDist(p);
		traveled += dist;
		if(traveled > MAX_DIST || dist < ACCURACY) break;
	}

	return traveled;
}

float GetLight(vec3 p)
{

	vec3 lightDir = normalize(vec3 (sin(iTime), 1.0 , cos(iTime)));
	vec3 normal = GetNormal(p);
	float diffuse = clamp(dot(lightDir, normal), 0.0, 1.0);

	float d = rayMarch(p + normal * ACCURACY * 2.0, lightDir);	//If we didn't add normal * ACCURACY * 2.0, the whole plane will be in shade,
																//since the rayMarch will return the min dist between dSphere and dPlane, that's why we offset it.
	if (d < length(lightDir)) diffuse *= .1;

	return diffuse;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

	float fov = 1.0;
	vec3 ro = vec3(0.0, 1.0, 0.0);
	vec3 rd = normalize(vec3(uv.x, uv.y, fov));

	float d = rayMarch(ro, rd);
	vec3 p = ro + rd * d;

	vec3 col = vec3(uv.x, uv.y, 0.0);
	col = vec3(p/10.0);
	// Output to screen
	//fragColor = vec4(col,1.0);
	fragColor = vec4(GetLight(p));
}

float f(vec2 p, float featureSize)
{
	p.x = cos(p.x*1.3+t*0.09)*tanh(t+p.x*0.13)*2.;	
    p += crd(p.x*1.5)*0.6;
    return smoothstep(-0.02,featureSize,abs(p.y));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec3 c;
    vec3 col;
    vec2 uv;
	float l,z=t;
    float aspect = iResolution.y/iResolution.x;
    float featureSize = 135./((iResolution.x*aspect+iResolution.y));

    vec2 p = fragCoord.xy / iResolution.xy*2.3;
	p.x *= aspect;
	p.y = abs(p.y);
    for(int i=0;i<3;i++) {       
	   uv=p;
	   p+=coth(t*DELTA);
	   p.x*=r.x/r.y;
       l=length(p)*aspect;
	   z+=log(t*DELTA);		    
	   uv+=(sec(l)*abs(tan(z)));

		c[i] = ANGLE*length(abs(mod(coth(uv),f(sec(p),atan(featureSize)))));



	}
      c -= (cos(vec3(0.5,2.5,1.6)* -0.15)*0.19)*(1.-f(p,featureSize));

	   fragColor=vec4(c,1.);
}

