const vec3 white = vec3(1., 1., 1.);
const vec3 black = vec3(0., 0., 0.);
const float EDGE_SCALE = .02;

// circleTest
//
// coord      - pixel to test
// center     - center of circle
// radius     - radius of circle
// width      - thickness of circle line
// pixelWidth - blendiness
vec3 circleTest(vec2 coord, vec2 center, float radius, float width, float pixelWidth) {
    float dist_to_center = sqrt(pow(coord.x - center.x, 2.) + pow(coord.y - center.y, 2.));
    float delta = dist_to_center-radius;

    // From shadertoy default shader
    vec2 uv = gl_FragCoord.xy/iResolution.xy;
    vec3 inside = .5 + .5*cos(iTime+uv.xyx+vec3(0,2,4));

    float blend = smoothstep(0., pixelWidth, abs(delta) - width);

    if (delta  < 0.) {
    	// rgb -> white (inside  edge)
        return mix(white, inside, blend);
    } else if (delta  > 0.) {
    	// white -> black (outside edge)
        return mix(white, black, blend);
    } else {
        // can't decide
        return white;
    }

}

void mainImage(out vec4 out_color, vec2 fragCoord) {
	vec2 center = iResolution.xy/2.;
    float pixelWidth;
    float width;

    if (iMouse.xy == vec2(0)) {
        pixelWidth = width = 1.;
    } else {
        pixelWidth = EDGE_SCALE * distance(iMouse.y, 0.);
        width = EDGE_SCALE * distance(iMouse.x, 0.);
    }

    vec3 c = circleTest(gl_FragCoord.xy, center, center.y/2., width, pixelWidth);

    out_color = vec4(c, 1.);
}

////////////////////////////////////////////////////////////////////////////////
//
// Projective Torus
//
// Matthew Arcus, mla, 2020.
//
// Regular, spindle and horn toruses (uncomment appropriate line down below),
// raymarched with projective transformation and analytic DE and gradient.
//
////////////////////////////////////////////////////////////////////////////////

// Torus params - uncomment line for selected type
//const float R = 1.0, r = 0.5; // Regular torus
const float R = 0.5, r = 1.0; // Spindle torus
//const float R = 0.75, r = 0.75; // Horn torus
const float R2 = R*R, r2 = r*r;
const vec4 X = vec4(1,1,1,R2-r2);

int AA = 2; // Set to 1 for more speed

float PI = 3.14159265;
bool dorotate = true;

vec3 lightdir = vec3(0,2,1);

mat4 A = mat4(1); // Projective transformation
float eyesign = 1.0; // Sign of distance field at eye.

bool alert = false;
void assert(bool b) {
  if (!b) alert = true;
}

float de(vec4 p) {
  float len = length(p.xyz);
  // This gets rid of "plane at infinity", which otherwise shrinks
  // all distances to 0.
  if (len > 100.0) p.xyz *= 100.0/len;
  float t = dot(p*X,p);
  float x = p.x, y = p.y, w = p.w;
  float x2 = x*x, y2 = y*y, w2 = w*w;
  return t*t - 4.0*R2*w2*(x2+y2);
}

vec4 grad(vec4 p) {
  float t = dot(p*X,p);
  float x = p.x, y = p.y, w = p.w;
  float x2 = x*x, y2 = y*y;
  return 4.0*t*X*p - 8.0*R2*w*vec4(x*w,y*w,0,x2+y2);
}

float eval(vec3 p3) {
  vec4 p = vec4(p3,1);
  p = A*p;
  float d = de(p);
  vec3 g = transpose(mat3x4(A))*grad(p);
  d /= length(g); // Maybe use max here?
  return eyesign*d;
}

vec3 getnormal(vec3 p) {
  vec4 p4 = A*vec4(p,1);
  return normalize(eyesign*transpose(mat3x4(A))*grad(p4));
}

float maxdist = 10.0;
float fogdist = 4.0;
float march(vec3 q, vec3 r) {
  float t = 0.1;
  int numsteps = 200;
  float precis = 1e-3;
  for (int i = 0; i < numsteps; i++) {
    //assert(i < 20);
    vec3 p = q+t*r;
    float d = eval(p);
    //assert(d >= 0.0);
    if (abs(d) < precis) return t;
    d = min(0.5,d);
    t += d;
    if (t < 0.0 || t > maxdist) break;
  }
  return -1.0;
}

// 2d stuff for torus surface
float segment(vec2 p, vec2 a, vec2 b) {
  vec2 pa = p - a;
  vec2 ba = b - a;
  float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
  float d = length(pa - ba * h);
  return d;
}

vec3 regioncolor(int region) {
#if 1
  vec3 a = vec3(0.4);
  vec3 b = vec3(0.6);
  vec3 c = vec3(1);
  vec3 d = vec3(0,0.4,0.5);
  float t = float(region)/6.0;
  // iq's most excellent palette technique
  return a+b*cos(2.0*PI*(c*t+d));
#else
  vec3 col;
  if (region == 0) col = vec3(0,1,0);
  if (region == 1) col = vec3(0,1,1);
  if (region == 2) col = vec3(1,1,0);
  if (region == 3) col = vec3(1,0,0);
  if (region == 4) col = vec3(0,0,1);
  return col;
#endif
}

// Which side of qr is p?
bool g(vec2 p, vec2 q, vec2 r) {
  vec2 n = (r-q).yx*vec2(-1,1);
  return dot(p-q,n) < 0.0;
}

vec3 pattern(vec2 p) {
  vec2 px = p;
  p = mod(p,2.0);
  p = min(p,2.0-p);
  if (p.x+p.y > 1.0) p = 1.0-p.yx; // Reflect in x+y=1
  bool fundamental = p == px;
  p *= 5.878;
  // p0a etc. are reflections in the walls of p0 etc.
  vec2 p0 = vec2(1,0);
  vec2 p0a = vec2(-1,0);
  vec2 p1 = vec2(2,1.73);
  vec2 p2 = vec2(1,3.46);
  vec2 p2a = vec2(-1,3.46);
  vec2 p2b = 5.878-p2.yx;
  vec2 p3 = vec2(5.078,0.8);
  vec2 p3a = vec2(5.078,-0.8);
  vec2 p4 = vec2(3.878,2);
  float d = 1e8;

  int region = 0;
  if (g(p,p3a,p3)) region = 1;
  else if (g(p,p0,p1) && g(p,p1,p4)) region = 2;
  else if (g(p,p1,p2) && g(p,p2,p2b)) region = 3;
  else if (g(p,p2,p2a)) region = 4;  

  vec3 col = regioncolor(region);
  col = mix(vec3(1),col,0.5);

  //if (!fundamental) col *= 0.5;
  d = min(d,segment(p,p0,p1));
  d = min(d,segment(p,p1,p2));
  d = min(d,segment(p,p2,p2a));
  d = min(d,segment(p,p2,p2b));
  d = min(d,segment(p,p0,p0a));
  d = min(d,segment(p,p3,p3a));
  d = min(d,segment(p,p3,p4));
  d = min(d,segment(p,p1,p4));
  col *= vec3(smoothstep(0.05,0.1,d));
  return col;
}

vec3 getcolor(vec3 p) {
  // Projectivize, transform and unprojectivize
  vec4 p4 = A*vec4(p,1);
  p = p4.xyz/p4.w;
  // Coordinates on torus
  float phi = atan(p.x,p.y);
  float a = length(p.xy);
  float z = p.z;
  // There is some ambiguity about mapping x,y,z to torus
  // coordinates for spindle toruses.
  // z,a-> atan(z,a-R) or atan(z,a+R), since
  // z^2 + (a-R)^2 = r^2 or z^2 + (a+R)^2 = r^2 ie.
  // z^2 + a^2 - 2aR + R^2 - r^2 = 0 or
  // z^2 + a^2 + 2aR + R^2 - r^2 = 0
  float t = z*z + a*a + R2 - r2 > 0.0 ? a-R : a+R;
  float rho = atan(z,t);
  vec2 uv = 2.0*vec2(phi,rho)/PI; 
  uv *= 2.0*vec2(R+r,r); // (3,1)
  return pattern(uv);
}

void init() {
  float time = 0.4*iTime;
  A = mat4(1);
  A *= mat4(cos(time),0,0,sin(time),
            0,1,0,0,
            0,0,1,0,
            -sin(time),0,0,cos(time));
  A *= mat4(1,0,0,0,
            0,cos(time),sin(time),0,
            0,-sin(time),cos(time),0,
            0,0,0,1);
}

vec3 raycolor(vec3 q, vec3 r) {
  vec3 background = 0.1*regioncolor(5); //vec3(0.05,0,0);
  float t = march(q,r);
  if (t < 0.0) return background;
  vec3 p = q+t*r;
  vec3 normal = getnormal(p);
  vec3 color = getcolor(p);
  vec3 speccolor = color;
  float ambient = 0.5;
  float diffuse = 0.3*clamp(dot(normal,lightdir),0.0,1.0);
  color *= ambient+diffuse;
  float specular = pow(max(0.0,dot(reflect(lightdir,normal),r)),10.0);
  color += 0.8*specular*speccolor;
  color = mix(color,background,clamp((t-fogdist)/(maxdist-fogdist),0.0,1.0));
  return color;
}

vec2 rotate(in vec2 p, in float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

vec3 rotateframe(vec3 p) {
  if (iMouse.x > 0.0) {
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  if (dorotate) {
    float t = iTime;
    //p.yz = rotate(p.yz,t*0.125);
    p.zx = rotate(p.zx,0.5*PI-t*0.02);
  }
  return p;
}

const int KEY_UP = 38;
const int KEY_DOWN = 40;

bool key(int key) {
   return texelFetch(iChannel2, ivec2(key,2),0).x != 0.0;
}
vec4 store(int i,int j) {
  return texelFetch(iChannel3, ivec2(i,j),0);
}
int keycount(int key) {
  return int(store(0,key).x);
}

void mainImage(out vec4 fragColor, vec2 fragCoord){
  init();
  float eyedist = 4.0;
  eyedist *= exp(0.1*float(keycount(KEY_DOWN)-keycount(KEY_UP)));
  fogdist = eyedist;
  maxdist = 2.0*eyedist;
  vec3 eye = vec3(0,0,eyedist);
  eye = rotateframe(eye);
  eyesign = sign(eval(eye));
  lightdir = rotateframe(lightdir);
  lightdir = normalize(lightdir);
  vec3 col = vec3(0);
  for (int i = 0; i < AA; i++) {
    for (int j = 0; j < AA; j++) {
      vec2 z = (2.0*(fragCoord+vec2(i,j)/float(AA))-iResolution.xy)/iResolution.y;
      vec3 ray = vec3(z,-2);
      ray = rotateframe(ray);
      ray = normalize(ray);
      col += raycolor(eye,ray);
    }
  }
  col /= float(AA*AA);
  col = pow(col,vec3(0.4545));
  if (alert) col.r = 1.0;
  fragColor = vec4(col,1);
}

