#define VERY_SMOL 0.001
#define SMOL 0.001
#define MAX_DIST  50.0
#define SPP 8
#define MAX_RAY_DEPTH 5
#define PI 3.14159265358
#define TAU (2.*PI)
bool inside;
//http://www.reedbeta.com/blog/quick-and-easy-gpu-random-numbers-in-d3d11/
int genSeed(int seed) {
	seed = (seed ^ 61) ^ (seed >> 16);
    seed *= 9;
    seed = seed ^ (seed >> 4);
    seed *= 0x27d4eb2d;
    seed = seed ^ (seed >> 15);
    return seed;
}
float rand(inout int seed) {
	seed ^= (seed << 13);
	seed ^= (seed >> 17);
	seed ^= (seed << 5);
    return float(uint(seed)) * 2.3283064370807974e-10;
}
float sdf(vec3 p)
{
  //float result = length(p) - 1.0;
  //vec2 t = vec2(1.0, 0.3);
  //vec2 q = vec2(length(p.xz)-t.x,p.y);
  //float result = length(q)-t.y;

    vec3 c = vec3(10.0);
    vec3 q = p - c * round(p / c);


  float d= 4.;
  return length(q) - 1.0 + (sin(d*q.x+iTime)*sin(d*q.y+iTime)*sin(d*q.z+iTime)) * 0.1;

}
float sdf2(vec3 p)
{
    float s = sdf(p);
 	return inside ? -s : s;   
}

float rayMarch(vec3 ro, vec3 rd, float start, float end)
{
    float depth = start;
    for (int i = 0; i < 256; ++i)
    {
        float distance = sdf2(ro + rd * depth);
        if (distance < VERY_SMOL)
            return depth;
        depth += distance;
        if (depth >= end) return end;
    }
    return -depth;
}
vec3 getNorm(vec3 p) {
    return normalize(vec3(
    	sdf2(vec3(p.x + SMOL, p.y, p.z)) - sdf2(vec3(p.x - SMOL, p.y, p.z)),
    	sdf2(vec3(p.x, p.y + SMOL, p.z)) - sdf2(vec3(p.x, p.y - SMOL, p.z)),
    	sdf2(vec3(p.x, p.y, p.z + SMOL)) - sdf2(vec3(p.x, p.y, p.z - SMOL))
    ));
}
float fresnel(float n1, float n2, vec3 normal, vec3 view)
{
  float r0 = (n1 - n2) / (n1 + n2);
  r0 = r0 * r0;

  float fresnel = r0 + (1.0 - r0) * pow(1.0 - dot(normal, view), 5.0);
  return clamp(fresnel, 0.0, 1.0);
}
mat4 rotY(float r)
{
    float c = cos(r);
    float s = sin(r);
    return mat4(
        vec4(c,0,s,1),
        vec4(0,1,0,1),
        vec4(-s,0,c,1),
        vec4(0,0,0,1)
    );
}
mat4 rotX(float r)
{
    float c = cos(r);
    float s = sin(r);
    return mat4(
        vec4(1,0,0,0),
        vec4(0,c,-s,0),
        vec4(0,s,c,0),
        vec4(0,0,0,1)
    );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 scrpos = uv * 2. - 1.;
    float aspect_ratio = iResolution.x / iResolution.y;

    mat4 view_mat =  rotY(-iMouse.x / iResolution.x * 2. * TAU) * rotX((iMouse.y / iResolution.y - PI) * 2. * TAU);

    int seed = genSeed(int(fragCoord.x + fragCoord.y * iResolution.x) + genSeed(int(iTime * 1000.0)));
    vec3 cam_pos = (view_mat * vec4(0.0, 0.0, -2.0, 1.0)).xyz;
    vec3 color = vec3(0.0);

    for (int samp = 0; samp < SPP; ++samp)
    {
        vec3 ro = cam_pos;

        vec3 rd = (view_mat * vec4(normalize(vec3(
                (scrpos.x + (2. * rand(seed) / iResolution.x)) * aspect_ratio,
                (scrpos.y + (2. * rand(seed) / iResolution.y)),
                1.0)), 0.0)).xyz;

        vec3 throughput = vec3(1.0);
        int depth;
        for (depth = 0; depth < MAX_RAY_DEPTH; ++depth)
        {
        	inside = sdf(ro) < 0.0;

            float t = rayMarch(ro, rd, 0.01, MAX_DIST);
            if (t < 0.)
            {
                fragColor = vec4(vec3(-t / MAX_DIST),0);
                return;
            }

            if (t >= MAX_DIST) {
                throughput *= texture(iChannel0, rd).xyz;
                color += throughput;
                break;
            }

            vec3 ip = ro + rd * t; //intersection point
            vec3 norm = getNorm(ip);


            float kRefract = 1.3333;
            float n1 = 1.0;
            float n2 = kRefract;
            if(inside) {n2 = 1.0; n1 = kRefract;}


            float f = fresnel(n1, n2, norm, -rd);
            float r0 = rand(seed);
            if (f > r0)
            {
            	ro = ip + norm * 0.01;
                rd = normalize(reflect(rd, norm));
            }
            else
            {
                vec3 refr = (refract(rd, norm, n1 / n2));
                if(abs(dot(refr, refr)) < VERY_SMOL)
                {
                    //handle total inner reflection
                	ro = ip + norm * 0.01;
                	refr = reflect(rd, norm);
                }
                else
                {
                	ro = ip - norm * 0.01;
                }

                rd = refr;   
            }
        }


        //reached max depth, cheat by saying we hit the skybox =]
        if(depth == MAX_RAY_DEPTH) color += texture(iChannel0, rd).xyz;
    }
    color *= (1.0 / float(SPP));


    // Output to screen
    fragColor = vec4(pow(color,vec3(1./2.2)),1.0);
    //fragColor = vec4(color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (1.0/32.0)*fragCoord/iResolution.xy;

    vec4 a = texture(iChannel0, uv);
    vec4 b = texture(iChannel0, vec2(uv.x+0.5,uv.y));

    vec4 c = a-b;
    c *= c*4.0;


    fragColor = c+texture(iChannel1,fragCoord/iResolution.xy);
}

