    if (char_pos.y == 0.) { C(n) C(o) C(t) C(i) C(c) C(e) C(space) C(m) C(e) C(space) C(s) C(e) C(m) C(p) C(a) C(i) }
    else if (char_pos.y == 1.) { C(a) C(l) C(c) C(a) C(t) C(r) C(a) C(z) }
	else if (char_pos.y == 2.) { C(c) C(o) C(n) C(s) C(p) C(i) C(r) C(a) C(c) C(y) }
	else if (char_pos.y == 3.) { C(c) C(t) C(r) C(l) C(dash) C(a) C(l) C(t) C(dash) C(t) C(e) C(s) C(t) }
	else if (char_pos.y == 4.) { C(f) C(a) C(i) C(r) C(l) C(i) C(g) C(h) C(t)  }
	else if (char_pos.y == 5.) { C(f) C(a) C(r) C(b) C(r) C(a) C(u) C(s) C(c) C(h)  }
	else if (char_pos.y == 6.) { C(L) C(J)  }
	else if (char_pos.y == 7.) { C(l) C(o) C(g) C(i) C(c) C(o) C(m) C(a)  }
	else if (char_pos.y == 8.) { C(m) C(e) C(r) C(c) C(u) C(r) C(y)  }
	else if (char_pos.y == 9.) { C(o) C(r) C(a) C(n) C(g) C(e)  }
	else if (char_pos.y == 10.) { C(p) C(r) C(i) C(s) C(m) C(b) C(e) C(i) C(n) C(g) C(s)  }
	else if (char_pos.y == 11.) { C(q) C(u) C(i) C(t) C(e)  }
	else if (char_pos.y == 12.) { C(S) C(a) C(n) C(d) C(S)  }
	else if (char_pos.y == 13.) { C(s) C(t) C(i) C(l) C(l)  }
	else if (char_pos.y == 14.) { C(t) C(dash) C(r) C(e) C(x)  }
	else if (char_pos.y == 15.) { C(t) C(h) C(r) C(o) C(b)  }
	else if (char_pos.y == 16.) { C(t) C(i) C(t) C(a) C(n)  }
	else if (char_pos.y == 17.) { C(j) C(e) C(t) C(l) C(a) C(g) C(slash) C(s) C(t) C(u) C(c) C(k)  }
	else if (char_pos.y == 18.) { C(k) C(e) C(e) C(n) C(space) C(p) C(r) C(o) C(v) C(o) C(d)  }
	else if (char_pos.y == 19.) { C(r) C(e) C(v) C(i) C(s) C(i) C(o) C(n) C(space) C(n2) C(n0) C(n2) C(n0)  }
    return 32.;

}
float text(vec2 p) {
    p *= 1024.;
    const vec2 glyph_size = vec2(21., 44.);
    vec2 char_pos = floor(p / glyph_size);
    p = fract(p / glyph_size) - .5;
    vec2 t_glyph_size = iChannelResolution[1].xy / 16.;

    char_pos.y = -1. - char_pos.y;
    float char = getchar(char_pos);
    vec2 t_glyph_pos = vec2(mod(char, 16.), 15. - floor(char / 16.));
    p.x *= glyph_size.x / glyph_size.y;
    return smoothstep(.53, .48, texture(iChannel1, (t_glyph_pos + .5 + p) * t_glyph_size / iChannelResolution[1].xy, -8.).a);
}

float mask(vec2 p) {
	if (t < 1016.) return 0.;
	if (p.x < 0. || p.x > 32.) return 0.;
	if (p.y > 2.8 ) return 0.;
	p -= 2.;

	float line = floor(p.y / 1.5);
	if (t < 1152.) {
		if (line == -1.) {
		} else if (line == -2.) {
			line = (t < 1056.) ? 0. : -2. - floor(t-1056.)/8.;
		} else {
			return 0.;
		}
	} else {
		line -= 16.;
	}
	p.y = line * 1.5 + mod(p.y, 1.5);
	return text(p/32.);
}

float w(vec3 p) { return min(p.y+2., length(p)-2.); }
vec3 wn(vec3 p) { return normalize(vec3(
	w(p+E.yxx),w(p+E.xyx),w(p+E.xxy))-w(p));
}
float tr(vec3 O, vec3 D, float l, float L) {
	for (float i = 0.; i < 40.; ++i) {
		float d = w(O + D * l);
		l += d;
		if (d < .001 || l > L) break;
	}
	return l;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {        
    t = 4. * iTime * 125. / 60.;
    t = mod(t, 1232.);
    //t = 4. * iChannelTime[0] * 125. / 60.; /* iChannelTime[0] is updated like 10 times per second :( */
	vec2 uv=(fragCoord.xy/iResolution.xy)*2.-1.;uv.x*=iResolution.x/iResolution.y;

	vec3 c=vec3(0.);
	vec3 O, D, P, N;
	vec2 UV;
	float M;
	float l;
	float seed = fract(t + uv.x);
	float bar = floor(t/16.);

	vec3 sundir = normalize(vec3(1.));
	float dof = .4;
	float fov = 1. + 1. * fract(t/32.);

	vec3 ca = vec3(0., -1., 0.);
	vec3 cp = vec3(0., 1.9, 5.);
	cp.z += 4. * fract(t/32.);
	float lfoc = 0.;

	float mskymat = 0.;
	float mballmat = 0.;
	float mfloormat = 0.;
	float yu = 0.;

	if (t < 304.) {
		mskymat = 0.;
		mballmat = 0.;
		mfloormat = 0.;
		dof = .01;
		fov = 2.;

		ca = vec3(0., 0., 0.);
		cp = vec3(0., 1., 5. + 2. * fract(t/16.));
	} else {
		ca = vec3(
			sin(bar*3.) * 4.,
			sin(bar*4.),
			sin(bar*5.) * 4.);

		mskymat = 1.;
		mballmat = 0.;
		mfloormat = 0.;
		dof = .2;
		fov = 2.;

		cp = vec3(
			sin(bar) * 10.,
			2. + 2. * sin(bar*7.),
			10. * cos(bar*3.));
	}

	if (t > 362.) {
		mskymat = 1.;
		mfloormat = 3.;
		mballmat = 0.;

		float ph = fract((t-362.) / 128.);
		ca = vec3(0., 0., 0.);
		cp = vec3(0., 1., 5. + 32. * ph);
		fov = 1. + 4. * ph;
	}

	if (t > 490.) {
		mballmat = 1.;
		dof = .4;
	}

	if (t > 580.) {
		ca = vec3(
			sin(bar*3.) * 4.,
			sin(bar*4.),
			sin(bar*5.) * 4.);
		cp = vec3(
			sin(bar) * 10.,
			2. + 2. * sin(bar*7.),
			10. * cos(bar*3.));
	}

	if (t > 704.) {
		mballmat = 2.;
		mskymat = 2.;
		dof = .4;
	}

	if (t > 832.) {
		// FIXME proper material ranges
		mskymat = mod(floor(t/8.),4.);
		mballmat = mod(floor(t/4.)/*TODO beat sync, 4th beat is earlier*/,5.);
		mfloormat = mod(floor(t/6.),4.);
		if (mskymat == 3.)
			mfloormat = 1.;

		float ph = fract(t/16.);
		float a = sin(bar), b = sin(bar+3.);
		cp = ca + mix(
			vec3(cos(a)*10.,2.+2.*sin(bar*2.),sin(a)*10.),
			vec3(cos(b)*10.,2.+2.*sin(bar*3.),sin(b)*10.), ph * ph);
		cp *= max(1., 8. / length(cp));
		ca = vec3(
			sin(bar*3.) * 2.,
			sin(bar*4.),
			sin(bar*5.) * 2.);
			yu = sin(bar*17.);
	}

	if (t > 1016.) {
		ca = vec3(5., 1., 0.);
		cp.z = abs(cp.z);
		cp *= max(1., 8. / length(cp));

		float ph = fract(t/64.);
		fov = 1. + 2. * ph;
		cp = vec3(8. + 10. * sin(bar + t/16.), 2., 10.);
	}

	if (t > 1152.) {
		mskymat = 0.;
		cp = vec3(10., 2., 10.);
		yu = 0.;
	}

	if (t > 1184.) {
		mfloormat = 3.;
	}

	if (t > 1216.) {
		mballmat = 1.;
	}

	lfoc += length(ca-cp);

	// PATHTRACER STARTS
    const float samples_per_pixel = 32.;
	for (float s=0.;s<samples_per_pixel;++s) {
		D = normalize(cross(O = normalize(ca-cp), vec3(yu,1.,0.)));
		vec3 up = normalize(cross(D, O));
		mat3 mv = mat3(D, up, -O);

		if (t < 266.) {
			D = mv*normalize(vec3(uv/fov, -1.)*lfoc - O);
			O = mv*O + cp;

			float L = 100., l = tr(O, D, 0., L);
			if (l < L) {
				P = O + D * l;
				vec3 n = wn(P);
				if (bar < 2.) c = vec3(1.);
				else if (bar < 4.) c = vec3(l/L);
				else if (bar < 6.) c = P;
				else if (bar < 8.) c = fract(P);
				else if (bar < 10.) c = n;
				else {
					c += .8 * vec3(max(0., dot(n, sundir)));
					if (bar > 14.)
						c *= step(5., tr(P, sundir, .1, 5.));
					if (bar > 12.)
						c = vec3(.01) + .5 *c;
				}
				c *= samples_per_pixel;
			}
			break;
		}

		float a = hash1(seed+=O.x)*6.2831;
		O = vec3(vec2(cos(a),sin(a))*sqrt(hash1(seed+=uv.y))*dof,0.);
		D = mv*normalize(vec3(uv / fov, -1.)*lfoc - O);
		O = mv*O + cp;

		float ins = 1.;
		float hue = hash1(seed += P.x);
		vec3 kc = clamp(abs(fract(vec3(3.,2.,1.)/3. + hue)*6.-3.)-1.,0.,1.);

		for (float i = 0.; i < 6.; ++i) {
			vec3 me = vec3(0.), ma = vec3(.8);
			float mr = 1.;
			vec2 mf = vec2(1., 1.);
			l = 1e6;
			M = 0.;

			if (D.y < 0.) {
				l = (-2. - O.y) / D.y;
				P = O + D * l;
				N = E.xzx;
				UV = P.xz;
				M = 1.;
			}

			float b = dot(-O, D);
			float det2 = b * b - dot(-O, -O) + 4.;
			if (det2 >= 0.) {
				det2 = sqrt(det2);
				float t1 = b - det2, t2 = b + det2;
				if (t1 < 0.) t1 = 1e6;
				if (t2 < 0.) t2 = 1e6;
				float ls = min(t1, t2);
				if (ls < l) {
					l = ls;
					P = O + D * l;
					N = ins * normalize(P);
					M = 2.;
				}
			}

			// text plane
			float lp = -O.z / D.z;
			if (lp > 0. && lp < l) {
				vec3 p = O + D * lp;
				if (mask(p.xy) > .5) {
					P = p;
					l = lp;
					N = E.xxz;
					UV = P.xy;
					M = 3.;
				}
			}

			if (M == 0.) { // SKY
				ma = vec3(0.);
				if (mskymat == 0.) {
						me = vec3(100.) * pow(max(0., dot(D,sundir)), 300.);
				} else if (mskymat == 1.) {
					vec2 skp = D.xz*(10.-O.y)/D.y * .1;
					float sk = noise2(skp)*.5 + noise2(skp*12.1)*.125 + noise2(skp*3.8)*.125 + noise2(skp*9.)*.0625;
					me = .3 * vec3(.3,.5,.9)
						+ 400.*vec3(.9,.6,.2) * pow(max(0., dot(D,sundir)), 400.)
						+ vec3(smoothstep(.4,.6,sk));
						;
				} else if (mskymat == 2.) {
					me = 10. * vec3(step(abs(dot(D,sundir)+mod(t/16.,1.)-1.), .05));
					vec3 bd = normalize(vec3(-1., .5, .1));
					me += .2 * vec3(.4, .1, .3) * pow(max(0., dot(D,bd)), 3.);
					bd = normalize(vec3(1., .3, .3));
					me += .2 * vec3(.1, .5, .4) * pow(max(0., dot(D,bd)), 4.);
				}
			} else if (M == 2.) { // BALL
				if (mballmat == 0.) {
				} else if (mballmat == 1.) {
					ma = vec3(.8);
					mf = vec2(.5, mix(.95,.8,hue)); // FIXME fresnel angle dependent?
					mr = .0;
				} else if(mballmat == 2.) {
					ma = vec3(1.);
					mf = vec2(1.,1.);
					mr = .2;
				} else if(mballmat == 3.) {
					me = N;
					ma = vec3(1.);
					mf = vec2(1.,1.);
					mr = .4;
				} else if(mballmat == 4.) {
					ma = vec3(1.);
					mf = vec2(.0, mix(.85,.8,hue)); // FIXME fresnel angle dependent?
					mr = .4;
				}
			} else if (M == 1.) { // GROUND
				if (mfloormat == 0.) {
				} else if (mfloormat == 1.) {
					ma = vec3(.9);
					mr = .5;
					UV *= 4.;
					vec2 cid = floor(UV);
					vec2 cc = fract(UV)*2. - 1.;

					vec2 rcid = vec2(floor(t/2.) + length(cid), atan(cid.x, cid.y));

					me += vec3(1.,.6,.3) * step(.97,hash2(rcid));
					me += vec3(.3,.4,.9) * step(.98,hash2(rcid+1.));
					me += vec3(.3,.8,.3) * step(.98,hash2(rcid+2.));

					me *= vec3(
						step(length(cc),.8),
						step(length(cc+vec2(.1,0.)),.8),
						step(length(cc+vec2(.0,.1)),.8)
					);
					mr = .001 + hash2(cid)*.3;
					ma *= step(abs(cc.x),.9)*step(abs(cc.y),.9);

					me *= 10.;
				} else if (mfloormat == 2.) {
					ma = vec3(1.);
					float ns =
						.5 * noise2(UV)
						+ .25 * noise2(UV * 1.9)
						+ .125 * noise2(UV * 3.9)
						+ .0625 * noise2(UV * 9.);
					mr = .2 + .2 * ns;
				} else if (mfloormat == 3.) {
					ma = vec3(1.);
					UV *= .7;
					float ns =
						.5 * noise2(UV)
						+ .25 * noise2(UV * 1.9)
						+ .125 * noise2(UV * 3.9)
						+ .0625 * noise2(UV * 9.);
					mr = .01 + .2 * smoothstep(.4, .5, ns);
				} else if (mfloormat == 4.) {
					ma = vec3(1.);
					mr = .3;
					me = vec3(step(abs(P.z + 2. * floor(mod(t, 8.)) - 8.), 1.));
				}
			} else { // TEXT
				me = vec3(4.);
				ma = vec3(0.);
			}

			c += kc * me;
			kc *= ma;

			if (all(lessThan(kc,vec3(.001)))) break;

			if (hash1(seed+=P.y) > mf.x) {
				O = P - .01 * N;
				D = normalize(refract(D, N, mf.y));
				ins = -ins;
			} else {
				O = P + .01 * N;
				D = normalize(mix(
					reflect(D, N),
					vec3(hash1(seed+=P.z),hash1(seed+=D.x),hash1(seed+=P.y))-.5, mr));
				D *= sign(dot(D, N));
			}
		}
	}

	fragColor=vec4(sqrt(c/samples_per_pixel), 0.);
}

#define PI 3.14

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //Normalize    
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) /iResolution.y;

    float pixelSize = 2.0 / iResolution.y;

    //Waves
    float wave1 = sin (uv.x * 3.0 + iTime) * .1 + 0.1;
    float wave2 = sin (uv.x * 2. + iTime * 2.0) * .1 - 0.2;
    float wave3 = sin (uv.x * 1. + iTime * 4.0) * .1 - 0.6;

    //Fields
    float land = 1.0 - step (wave1, uv.y);
    float land2 = 1.0 - step (wave2, uv.y);
    float land3 = 1.0 - step (wave3, uv.y);

    //Colors
    vec4 sky = vec4(0.1, 0.6, 0.7, 0.0); 
    vec4 grass = vec4(0.0, 0.1, 0.04, 0.0);
	vec4 grass2 = vec4(0.01, .2, 0.09, 0.0);
    vec4 grass3 = vec4(0.05, .25, 0.1, 0.0);



    //Moon
    vec2 moonCenter = vec2(cos (iTime),sin (iTime));
    float radius = .250;
    float dist = distance(uv, moonCenter);
    float moon = 1.0 - smoothstep(radius - pixelSize, radius + pixelSize, dist);

    //MoonLight
    float distGradient = distance(uv * 1.5, moonCenter * 1.5);
    float moonLight = clamp((1. - distGradient),0.0, 1.0) / 1.5;

    //Sun
    vec2 sunCenter = vec2(cos (iTime + PI),sin (iTime + PI));
    float radiusSun = .30;
    float distSun = distance(uv, sunCenter);
    float sun = 1.0 - smoothstep(radiusSun - pixelSize, radiusSun + pixelSize, distSun);

    //SunLight
    float distGradientSun = distance(uv, sunCenter);
    float sunLight = clamp((1. - distGradientSun),0.0, 1.0) / 1.5;
    vec4 sunLightColor = vec4(0.3,0.3,0.,0.);
    sunLightColor = sunLightColor + vec4(clamp((sin(iTime  + PI)), 0.2, 1.0), clamp((sin(iTime + PI)), 0.1, 1.0),0.0,0.0);

    //Ambient addition   
    moon += moonLight;
    sun += sunLight;

    //gradient overlay
    vec4 gradient = vec4(-1.,-1.,-1.,0.6);
    vec4 gradientFront = vec4(-.2,-.2,-.2,0.6);
    float daynight = clamp(sin(uv.y / 3. + iTime), 0.0, 0.3);


    vec4 picture = sky;
    picture = mix(picture, gradient, daynight);
    picture = mix(picture,  vec4(1.0), moon);
    picture = mix(picture, sunLightColor, sun);
    picture = mix(picture, grass, land);
    picture = mix(picture, grass2, land2);
    picture = mix(picture, grass3, land3);
    picture = mix(picture, gradientFront, daynight);
    picture.rgb = pow(picture.rgb, vec3(1.0/2.2)); 



    fragColor = vec4(picture);
}

////////////////////////////////////////////////////////////////////////////////
//
// Concentricity
// Matthew Arcus, mla, 2020
//
// Octahedral symmetry again.
//
// Inspired by: https://www.shadertoy.com/view/wd2cWW
//
// Nested spherical disdyakis dodecahedrons (this is actually what they are
// called: https://en.wikipedia.org/wiki/Disdyakis_dodecahedron)
//
////////////////////////////////////////////////////////////////////////////////

int AA = 1; // Set to 2 for quality

vec3 light = vec3(0,-2,1); // Position of light
float eradius = 0.03; // Thickness of an edge
float eyedist = 2.5;  // Eye is at (0,0,eyedist);
float PI = 3.1415927; 

// Raymarching configuration.
int maxsteps = 100;
float precis = 1e-3; 

bool dorotate = false;

// Find the distance to a spherical segment, encapsulated in m.
// m maps p to nearest point in some subspace (ie. a plane).
float ssegment(vec3 p, mat3 m) {
  vec3 p1 = normalize(m*p);
  // And return the 'distance' to the closest point.
  float len = length(p);
  float x = distance(p/len,p1);    // Distance on surface
  float y = len-1.0; // Radial distance
  return max(x,abs(y))-eradius;
}

// Matrix for segment through q and r - take a point p,
// Use q and r as basis vectors for subspace, so first
// take dot products with q and r, then map to coordinates
// in that basis (matrix m), then map back to R3. This is
// then the nearest point to p in the subspace.
// Assumes q and r normalized
mat3 mkmatrix(vec3 q, vec3 r) {
  mat2 m = inverse(mat2(1,dot(q,r),dot(q,r),1));
  return mat2x3(q,r)*m*transpose(mat2x3(q,r));
}

mat3 m0,m1,m2;
vec4 q0,q1,q2,qbg,qlight;

void init() {
  vec3 A = vec3(1,0,0);
  vec3 B = vec3(0,1,0);
  vec3 C = vec3(0,0,1);
  // The midpoints of the sides, named for opposites
  vec3 A1 = normalize(0.5*(B+C));
  vec3 B1 = normalize(0.5*(C+A));
  vec3 C1 = normalize(0.5*(A+B));
  m0 = mkmatrix(A,B);
  m1 = mkmatrix(A,A1);
  m2 = mkmatrix(C,C1);

  float t = 0.1*iTime;
  q0 = vec4(sin(t)*normalize(vec3(1,1,0)),cos(t));
  q1 = vec4(sin(t)*normalize(-vec3(1,1,1)),cos(t));
  q2 = vec4(sin(t)*normalize(vec3(0,1,1)),cos(t));
  qbg = vec4(sin(t)*normalize(vec3(1,0,1)),cos(t));
  qlight = vec4(sin(t)*normalize(vec3(0,1,0.5)),cos(t));
}

float de0(vec3 p, float k) {
  p *= k;
  p = abs(p); // Map to single face - skip this to see single face.
  // Sort the coordinates to map to fundamental region.
  if (p.x < p.y) p.xy = p.yx;
  if (p.y < p.z) p.yz = p.zy;
  if (p.x < p.y) p.xy = p.yx;

  float d = 1e8;
  d = min(d,ssegment(p,m0));
  d = min(d,ssegment(p,m1));
  d = min(d,ssegment(p,m2));
  return d/k;
}

// Quaternion multiplication
vec4 qmul(vec4 p, vec4 q) {
  vec3 P = p.xyz, Q = q.xyz;
  return vec4(p.w*Q+q.w*P+cross(P,Q),p.w*q.w-dot(P,Q));
}

vec4 qconj(vec4 p) {
  return vec4(-p.xyz,p.w);
}

// Might be better to turn into a matrix.
vec3 qrot(vec3 p, vec4 q) {
  vec4 r = qmul(qconj(q),qmul(vec4(p,0),q));
  return r.xyz;
}

float de(vec3 p) {
  float d = 1e8;
  float k = 1.2;
  d = min(d,de0(qrot(p,q0),k));
  d = min(d,de0(qrot(p,q1),1.0));
  d = min(d,de0(qrot(p,q2),1.0/k));
  return d;
}

// Get the normal of the surface at point p.
vec3 getnormal(vec3 p) {
  float eps = 0.001;
  vec2 e = vec2(eps,0);
  return normalize(vec3(de(p + e.xyy) - de(p - e.xyy),
                        de(p + e.yxy) - de(p - e.yxy),
                        de(p + e.yyx) - de(p - e.yyx)));
}

// Rotate vector p by angle t.
vec2 rotate(vec2 p, float t) {
  return cos(t)*p + sin(t)*vec2(-p.y,p.x);
}

// Rotate according to mouse position
vec3 transformframe(vec3 p) {
  if (iMouse.x > 0.0) {
    // Full range of rotation across the screen.
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  // autorotation
  if (dorotate) {
    p.yz = rotate(p.yz,-iTime*0.125);
    p.zx = rotate(p.zx,iTime*0.1);
  }
  return p;
}

vec3 getbasecolor(int type) {
  return vec3(0.75,1,0.75);
}

vec3 getbackground(vec3 r) {
  vec3 col = qrot(r,qbg);
  col = abs(col);
  col = pow(col,vec3(1.5));
  return col; // Colourful fun
  return vec3(0,0,0.1); // The more sober option.
}

float maxdist = 10.0;
float march(vec3 q, vec3 r) {
  float t = 0.0;
  for (int i = 0; i < maxsteps; i++) {
    vec3 p = q+t*r;
    float d = de(p);
    if (abs(d) < precis) return t; // Close enough to the surface.
    t += d;
    if (t > maxdist) break;
  }
  return -1.0;
}

vec3 raycolor(vec3 q, vec3 r) {
  float t = march(q,r);
  if (t < 0.0) return getbackground(r);
  // Get the surface point that has been hit,
  vec3 p = q+t*r;
  // and the normal at that point.
  vec3 normal = getnormal(p);
  if (dot(normal,r) > 0.0) normal = vec3(0);

  // Apply lighting. This is a basic "Lambertian" model.
  vec3 lightdir = normalize(light-p);
  vec3 color = getbasecolor(0);
  float ambient = 0.3;
  float diffuse = 0.7*clamp(dot(normal,lightdir),0.0,1.0);
  color *= ambient+ diffuse;
  color = mix(color,getbackground(r),t/maxdist); // Fog
  return color;
}

void mainImage(out vec4 fragColor, vec2 fragCoord){
  init();
  maxdist = eyedist + 1.0;
  vec3 eye = vec3(0,0,eyedist);
  eye = transformframe(eye);
  light = qrot(light,qlight);
  light = transformframe(light);
  vec3 col = vec3(0);
  for (int i = 0; i < AA; i++) {
    for (int j = 0; j < AA; j++) {
      vec2 z = (2.0*(fragCoord+vec2(i,j)/float(AA))-iResolution.xy)/iResolution.y;
      vec3 ray = vec3(z,-2);
      ray = transformframe(ray);
      ray = normalize(ray);
      col += raycolor(eye,ray);
    }
  }
  col /= float(AA*AA);
  col = pow(col,vec3(0.4545)); // Gamma
  fragColor = vec4(col,1);
}

