// CONSTANTS & UTILS
#define QP 0.785398163397448
#define HP 1.570796326794897
#define P 3.141592653589793
#define TP 6.283185307179586

#define t iTime
#define ht 0.5 * t
#define tt 0.1 * t


// UTILS FN
/* map a value `n` from a range `start1` -> `stop1` to a range `start2` -> `stop2` */
float map(float n, float start1, float stop1, float start2, float stop2) {
  	return (n - start1) / (stop1 - start1) * (stop2 - start2) + start2;
}

/* map a value `n` from the range -1.0 -> 1.0 to a range `start2` -> `stop2`
to be used with cos/sin
`map01(sin(x), 0.0, 1.0)`
*/
float map01(float n, float start2, float stop2) {
  	return (0.5 * n + 0.5) * (stop2 - start2) + start2;
}


// MAIN
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  	vec2 uv = fragCoord.xy / iResolution.xy;
  	vec2 uv0 = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

  	vec3 color = vec3(0.0);
    vec3 palette = vec3(1.0, 1.0, 0.0);

    // compute the angle from the center of the screen and the current pixel
    float angle = atan(uv0.y, uv0.x);
    // `atan` returns values from {-PI -> PI}, the mapping is just for conveniance
    angle = map(angle, -P, P, 0.0, 1.0);
    // define the section of the circle that is displayed
    float section = map01(sin(t), 0.0, 0.125); 
    // section = 0.125;

    // signed distance field of a circle
    float circSDF = length(uv0.xy);
    // compute the circle drawing
    float circ = 1.0 - step(0.5, circSDF);

    // cooking everything together and tadaa!
    color += step(section, angle) * circ;
    // pacman
    color *= palette;

  	fragColor = vec4(color, 1.0);
}

