vec4 sphere = vec4(0.0, 0.0, 1.0, 0.4);
vec3 lightPos = vec3(-9.0, 8.0, -8.0);
vec3 lightCol = vec3(0.8, 0.9, 1.0);

float sphereDist1(vec3 ro, vec3 rd)
{
	float R = sphere.w;
	vec3 d1 = sphere.xyz - ro;
	float b = dot(rd, d1);
	float d2 = dot(d1, d1) - b * b;
	float h2 = R * R - d2;
    if (h2 <= 0.0)
        return (-1.0);
    return (b - sqrt(h2));
}

float sphereDist(vec3 p)
{
 return (length(p - sphere.xyz) - sphere.w);   
}

// https://iquilezles.org/articles/rmshadows
float getShadow(vec3 ro, vec3 rd, float tmin, float tmax, const float k)
{
	float res = 1.0;
    float t = tmin;
    for( int i = 0; i < 50; i++)
    {
		float h = sphereDist(ro + rd * t);
        res = min(res, k * h / t);
        t += clamp(h, 0.02, 0.20);
        if(res < 0.005 || t > tmax)
            break;
    }
    return clamp(res, 0.0, 1.0);
}

// https://iquilezles.org/articles/normalsSDF
vec3 getNormal(vec3 pos)
{
    const float ep = 0.0001;
    vec2 e = vec2(1.0, -1.0) * 0.5773;
    return normalize( e.xyy*sphereDist(pos + e.xyy*ep) + 
					  e.yyx*sphereDist(pos + e.yyx*ep) + 
					  e.yxy*sphereDist(pos + e.yxy*ep) + 
					  e.xxx*sphereDist(pos + e.xxx*ep));
}

//smooth color gradient - https://www.shadertoy.com/view/4df3Rn
float mandelbrot(vec2 c)
{
    vec2 z = vec2(0.0);
    const float B = 256.0;
    float l = 0.0;
    for (int i = 0; i < 512; i++)
    {
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;
        if (dot(z, z) > (B * B))
            break;
        l += 1.0;
    }
    if (l > 511.0)
        return (0.0); 
    //float l = l - log(log(length(z))/log(B))/log(2.0);
    l = l - log2(log2(dot(z, z))) + 4.0;
    return (l);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 aspectRatio = vec2(iResolution.x / iResolution.y, 1.0);
    vec2 uv = aspectRatio * (fragCoord.xy / iResolution.xy - 0.5);
    vec2 mouse = 7.0 * (iMouse.xy / iResolution.xy - 0.5);  
    vec3 ro = vec3(0.0, 0.0, 0.0);
    vec3 rd = normalize(vec3(uv, 1.0));

	float rot = iTime * 0.2;
    mat3 rotX = mat3(
        vec3(cos(mouse.x - rot), 0.0, sin(mouse.x - rot)),
        vec3(0.0, 1.0, 0.0),
        vec3(-sin(mouse.x - rot), 0.0, cos(mouse.x - rot)));  
	mat3 rotY = mat3(
        vec3(1.0, 0.0, 0.0),
        vec3(0.0, cos(mouse.y), sin(mouse.y)),
        vec3(0.0, -sin(mouse.y), cos(mouse.y)));

    vec3 color = vec3(0.09 - uv.x, 0.38, uv.x + 0.4) * (0.9 - uv.x);

    float dSphere = sphereDist1(ro, rd);
    if (dSphere < 0.0)
    {
		fragColor = vec4(color, 1.0);
		return;
    }
	vec3 pos = ro + dSphere * rd;
	vec3 normal = getNormal(pos);
	vec3 v3 = rotX * rotY * normal;
    vec2 v2 = 1.5 * v3.xy / abs(v3.z);
    float dMb = mandelbrot(v2);

    vec3 material = 0.44 + 0.5 * cos(3.0 + dMb * 0.11 + vec3(0.0, 0.5, 1.0)); 
    vec3 lightDir = normalize(vec3(lightPos - pos));
    vec3 reflectDir = normalize(reflect(lightDir, normal));
    float shadow = getShadow(pos, lightDir, 0.001, 1.0, 8.0);

    float diffuse = clamp(dot(normal, lightDir), 0.0, 1.0) * shadow * 2.0;
    float ambient = 0.6 + 0.6 * normal.y;
	float specular = pow(clamp(dot(reflectDir, rd), 0.0, 1.0), 16.0) * 2.2 * shadow;

	color = lightCol * material * (diffuse + specular + ambient);
    color = sqrt(color);
    fragColor = vec4(color, 1.0);
}

//rotate a vector... Not very happy with this huge function...
mat3 rotateXYZ(vec3 t)
{
      float cx = cos(t.x);
      float sx = sin(t.x);
      float cy = cos(t.y);
      float sy = sin(t.y);
      float cz = cos(t.z);
      float sz = sin(t.z);
      mat3 m=mat3(
        vec3(1, 0, 0),
        vec3(0, cx, -sx),
        vec3(0, sx, cx));

      m*=mat3(
        vec3(cy, 0, sy),
        vec3(0, 1, 0),
        vec3(-sy, 0, cy));

      return m*mat3(
        vec3(cz, -sz, 0),
        vec3(sz, cz, 0),
        vec3(0, 0, 1));
}

//SDF-Functions
float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}


float softmin(float f1, float f2, float val)
{
      float e = max(val - abs(f1 - f2), 0.0);
      return min(f1, f2) - e*e*0.25 / val;     
}

float map(vec3 p)
{
      vec3 rotPlane=(rotateXYZ(vec3(1.5,.0,.0))*p);
      vec3 rotCube=(rotateXYZ(vec3(1,iTime,sin(iTime*.5)*.5))*p);
      float myplane=sdRoundBox(rotPlane-vec3(.0,.0,1.5),vec3(20.,20,.01),.1);
      float mycube=sdRoundBox(rotCube+vec3(0.,.5,0.),vec3(.75/2.),.1);
      return(softmin(myplane,mycube,1.));
}


vec3 normal(vec3 p)
{
      vec2 eps=vec2(.005,0);
      return normalize(vec3(map(p+eps.xyy)-map(p-eps.xyy),
                            map(p+eps.yxy)-map(p-eps.yxy),
                            map(p+eps.yyx)-map(p-eps.yyx)));
}

// LIGHT
float diffuse_directional(vec3 n,vec3 l, float strength)
{
      return (dot(n,normalize(l))*.5+.5)*strength;
}

float specular_directional(vec3 n, vec3 l, vec3 v, float strength)
{
      vec3 r=reflect(normalize(l),n);
      return pow(max(dot(v,r),.0),128.)*strength;
}

float ambient_omni(vec3 p, vec3 l)
{
      float d=1.-abs(length(p-l))/100.;
      return pow(d,32.)*1.5;
}



//SHADOW
float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k )
{
    float res = 1.0;
    float ph = 1e20;
    for( float t=mint; t<maxt; )
    {
        float h = map(ro + rd*t);
        if( h<0.0001 )
            return .0;
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, k*d/max(0.0,t-y) );
        ph = h;
        t += h;
    }
    return res;
}

// MAINLOOP
void mainImage(out vec4 fragColor, in vec2 fragCoord )
{  
    vec2 uv= fragCoord.xy/iResolution.xy-.5;
    uv.x/=iResolution.y/iResolution.x;
    vec3 ro=vec3(.0,.0,-3.5); 
    vec3 p=ro;
    vec3 rd=normalize(vec3(uv,1.));
    float shading=.0;
    bool hit=false;

    vec3 color;
    while(p.z<20.)
    {
        float d=map(p);
        if(d<.0001)
        {
            hit=true;
            break;
        }
        p += rd*d;
    }

    float t=length(ro-p);
    if (hit)
    {
        shading=length(p*10.);
        vec3 n=normal(p);
        vec3 l1=vec3(1,.5,-.25);
        float rl=ambient_omni(p,l1)*diffuse_directional(n,l1,.5)+specular_directional(n,l1,rd,.9);
        color=vec3(rl)+vec3(.1,.4,.1);
        color*=color;
        vec3 pos = ro + t*rd;
        color=mix(vec3(.0),color,softshadow(pos,normalize(l1),.01,10.0,20.)*.25+.75);
    }
    color*=mix(color,vec3(1.,1.,1.),1.-exp(-.1*pow(t,128.)));
    color-=t*.05;
    color=sqrt(color);

    fragColor=vec4(color,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //display Buffer A output
    fragColor = texture(iChannel0, fragCoord/iResolution.xy);
}

