// (C) F. Claudel 2020
// this buffer memorizes the background to draw over it
// AUDIO FEED in iChanel1
//   microphone ,
//   soundcloud
//    https://soundcloud.com/nemixmusic/valkyrie-profile-tomorrow-nemix-remix
//    https://soundcloud.com/classical-piano-hits/waltz-chopin
//
// Get audio feed FFT, display as a Sppectrogram , frequencies on log scale
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //number of octaves, normally log2(512) => 9
    float nbSamp = iChannelResolution[1].x;

    // number of octaves (first octave is spread on a just one value...)
    float nbOct=log2(nbSamp);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // y Offset in screen for first pixel
    float yOffscr = 0.5/iResolution.y;
    // x offset in FF buffer
    float xOffFFT = 0.5/nbSamp;
    //low octaves to discard for LOGY display
    float octOffRem = YLOG ? octOffLog : 0.0;

    // number of octaves to display
	float nbOctDisp = min(maxOctaves, nbOct-octOffRem);


    float pFreq; //posiiton inf FFT buffer (normalized 0=>1)
    float phase; // note phase within octavbe, for coloring
    if (YLOG) {
    	//compute for each Y the position in FOurier series (using log display)       
    	pFreq = exp2((uv.y-yOffscr)*nbOctDisp+octOffRem)/exp2(nbOct) + xOffFFT;
        phase = mod((uv.y-yOffscr)*nbOctDisp, 1.0);
    } else {
        //linear frequency display
        pFreq = (uv.y-yOffscr)/nbOct*nbOctDisp +xOffFFT;
        phase = mod(-log2(uv.y-yOffscr)/log2(nbOctDisp),1.0);
    }

    // Thickness of spetroram moving column (2 pixels, normalized)
    float COLTHICK = 1./iResolution.x; 

    // x position on screen depends time and preious time
    float tSound= iTime;
    float xpos1 = fract((tSound-iTimeDelta)*speedx); //fract => periodize
    float xpos2 = fract((tSound)*speedx);
    if (xpos1 > xpos2) xpos1 -= 1.0; //avoid carriage return

     // define smooth alpha on time + period+thickness : 1 during time, 0 out
	float alphaCol = smoothstep( 0.0, COLTHICK, uv.x - (xpos1-COLTHICK/2.0));
    alphaCol -= smoothstep( 0.0, COLTHICK, uv.x - (xpos2+COLTHICK/2.0));

    // mix rgba with previous buffer
    vec4 prevCol = texture(iChannel0, fragCoord/iResolution.xy).rgba;
    float freqI = texture(iChannel1, vec2(pFreq,0.25)).r;

    //check mouse status to see if we inverse palettes
    if (iMouse.z>0.001) { PALPHASE = !PALPHASE; }

    if (PALPHASE) {
        // palette color is phase, amplify with intensity
   		fragColor = mix(prevCol,
        	palettePhase(freqI, phase), 
       		alphaCol );
    } else {
        //palette inetnsity
   		fragColor = mix(prevCol,
        	paletteSound(freqI), 
       		alphaCol );
    }
}

common_code:
// LOG or linear frequency o Y axis : 
// use 1.0 for LOG dispolay,, else 0.0 for LINEAR
const bool YLOG = true;

// Colouring with Note Phase in octave + intensity dimming
//  else intensiy colouring
bool PALPHASE = true; // also changed by clicking the mouse

//speed of spectrogram : screen fraction per second
const float speedx = 1./15.;

//max number of octaves displayed (low octaves can be skipped in log mode)
const float maxOctaves = 9.0;
//for log display, skip those octaves (not enough data in FFT)
const float octOffLog = 2.0; 
//Amplification of Spectrum
const float specAmpli = 1.0;

vec4 paletteSound(in float x) {
    // convert intensity to colors, alpha is 1.0
    x*=specAmpli;
    //x*=x;//adapt level
	vec3 p = vec3( x, 4.0*x*(1.0-x), 1.0-x ) * x;
    return vec4(p,1.0); //alpha=1
}

vec4 palettePhase(in float intensity, in float phase) {
    //phase is between 0 an 1
    //periodic palette for notes pitch in octave, alpha is 1.0
    // note : no gamma correction because FFT values seem non-linear...

    vec3 p = 0.5+0.5*sin(6.28318530718*(vec3(0.0,0.33,0.66)+phase));
    // modulate by intensity              
    return vec4( intensity*p ,1.0); //alpha=1
}

