// suggested from tdhooper. Thanks!
// improve compilation time & overall fps.
const int NORMAL_STEPS = 6;
vec3 norm(vec3 pos) {
    vec3 eps = vec3(.01, 0, 0);
    vec3 nor = vec3(0);
    float invert = 1.;
    for (int i = 0; i < NORMAL_STEPS; i++) {
        nor += scene(pos + eps * invert) * eps * invert;
        eps = eps.zxy;
        invert *= -1.;
    }
    return normalize(nor);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  vec3 cam = normalize(vec3(2,uv));
  vec3 init = vec3(-9,0,1.1);

  float yrot = 0.3;
  float zrot = sin(iTime)*0.4-0.9;
  cam = erot(cam, vec3(0,1,0), yrot);
  init = erot(init, vec3(0,1,0), yrot);
  cam = erot(cam, vec3(0,0,1), zrot);
  init = erot(init, vec3(0,0,1), zrot);
  vec3 p = init;
  bool hit = false;
  for (int i = 0; i < 200 + donttouch; i ++){
    float dist = scene(p);
    if (dist*dist < 1e-6) {
      hit = true;
      break;
    }
    p+=dist*cam;
  }
  vec3 localstemcoords = globalstemcoords;
  int matloc = mat;
  vec3 n = norm(p);
  float ao = sqrt(scene_proxy(p+n*0.5)+0.5);
  ao *= sqrt(scene_proxy(p+n*0.2)/0.2*0.5+0.5);
  ao *= sqrt(scene_proxy(p+vec3(1))/sqrt(2.)*0.5+0.5);
  ao *= sqrt(scene_proxy(p+vec3(.5))/sqrt(2.)+0.55);
  //ao *= smoothstep(0., 0.5, length(p.xy))*0.2+0.8;
  vec3 r = reflect(cam, n);
  float wildness = 3.5;
  float powr = 20.;
  float specmult = 1.5;
  vec3 darkcol;
  vec3 litecol;


  float splaty = noise(p.zy*vec2(2,10))*0.3+0.6;
  float splatx = noise(p.zx*vec2(2,10)+100.)*0.3+0.6;
  float splatz = noise(vec2(length(p.xy), atan(p.x,p.y)*5.)+200.)*0.3+0.6;
  float marble = mix(mix(splaty, splatx, abs(n.y)), splatz, abs(n.z));
  if (matloc == 1) {
    darkcol = vec3(0.01);
    litecol = mix(vec3(1.,0.05,0.04), vec3(0.1), smoothstep(-1.2, 1.8, scene_proxy(p+r)/dot(r,n))*0.7+0.5);
  } else if (matloc == 0) {
    litecol = vec3(1.,0.05,0.04);
    litecol = mix(vec3(1.,0.2,0.15), litecol, sqrt(marble*0.5+0.5));
    float d = pow(smoothstep(0.01, 0.06, dots(p, 0.26)), 0.4);
    litecol = mix(vec3(1.1,0.01,0.05), litecol, d);
    litecol = mix(vec3(1.5,0.45,0.2), litecol, pow(d, 0.2));
    litecol *= marble;
    darkcol = litecol*0.02;

    vec3 noffset = vec3(0);
    noffset.x += cos(dot(p, 8.*vec3(3,2,2)));
    noffset.z += cos(dot(p, 8.*vec3(4,1,8)));
    noffset.y += cos(dot(p, 8.*vec3(4,2,2)));
    noffset.z += cos(dot(p, 10.*vec3(5,2,4)));
    noffset.y += cos(dot(p, 10.*vec3(1,1,6)));
    noffset.y += cos(dot(p, 10.*vec3(1,6,2)));
    noffset.x += cos(dot(p, 10.*vec3(2,1,5)));
    noffset.x += cos(dot(p, 10.*vec3(3,6,1)));
    n = normalize(n+noffset*0.01);
  } else {
    float grad = smoothstep(2.56, 2.3, p.z);
    vec2 messscale = vec2(500.,50.);
    float mess = noise(1.5+localstemcoords.xy*messscale);
    float mess2 = noise(1.+localstemcoords.xy*messscale);
    float mess3 = noise(0.5+localstemcoords.xy*messscale);
    float stemmix = cos(localstemcoords.z*90.-0.8)*0.5+0.5;
    n = normalize(n - normalize(tan(vec3(mess,mess2,mess3)))*0.3*(stemmix*0.5+0.5));
    litecol = mix(vec3(0.5,0.07,0.02), vec3(0.4,0.03,0.025), grad);
    litecol = mix(vec3(0.6,0.3,0.2), litecol, stemmix);
    powr = 5.;
    specmult = mix(0.25, 0.1, grad);
    litecol *= marble;
    darkcol = litecol*0.04;
  }
  r = reflect(cam, n);
  float spec = pow(length(sin(r*wildness)*0.5+0.5)/sqrt(3.), powr)*specmult;
  float diff = length(sin(n*wildness)*0.5+0.5)/sqrt(3.)*0.6;
  vec3 diffcol = mix(darkcol, litecol, diff);
  vec3 speccol = vec3(1,0.7,0.7)*spec;
  fragColor.xyz = hit ? ao*(diffcol + speccol) : vec3(0.05);
  fragColor.xyz = sqrt(fragColor.xyz);
  fragColor.xyz = mix(fragColor.xyz, smoothstep(vec3(0),vec3(1), fragColor.xyz), 0.3) + hash(hash(uv.x, uv.y), iTime)*0.02;
}

float speed = 1.5;

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float map(in vec3 pos)
{
    float time = mod(iTime * speed,30.);
    float box = sdBox(pos, vec3(0.4));

    float sphere = length(pos) - 0.5;

    float boxSphere = max(-sphere ,box);

    float flr = pos.y - (-0.30);

    if(time > 6. && time < 8.) return flr;
    if(time > 8. && time < 10.) return min(box,flr);
    if(time > 10.) return min(boxSphere,flr);


}

vec3 calcNormal(in vec3 pos)
{
    vec2 e = vec2(0.0001,0.);
    return normalize(vec3(map(pos + e.xyy)-map(pos-e.xyy),
                          map(pos + e.yxy)-map(pos-e.yxy),
                          map(pos + e.yyx)-map(pos-e.yyx))
                    );
}

float castRay(in vec3 ro, in vec3 rd)
{
    float t = 0.;
    for(int i = 0; i<120; i++)
    {
        vec3 pos = ro + t*rd;

        float h = map(pos);

        if(h < 0.001) break;

        t += h;

        if(t > 20.)
        {
            t = -1.; //set for the shadow
            break;
        }
    }

    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    float time = mod(iTime * speed,30.);

    float angle = 10.0*iMouse.x/iResolution.x;

    vec3 ro = vec3(1.5 * sin(angle),0.,1.5 * cos(angle));
    vec3 ta = vec3(0.);

    vec3 ww = normalize(ta-ro); //front
    vec3 uu = normalize(cross(ww,vec3(0.,1.,0.))); //right
    vec3 vv = normalize(cross(uu,ww)); //up

    vec3 rd = normalize(uv.x*uu + uv.y*vv + 1.5*ww);

    vec3 col = vec3(0.5, 0.7, 1.) - max(rd.y,0.0)*0.5;

    float t = castRay(ro,rd);

    vec3 mate = vec3(0.2);

    if(t > 0.)
    {
        vec3 pos = ro + t*rd;
        vec3 norm = calcNormal(pos);

        vec3 sunDir = normalize(vec3( 1.0,0.4,.2));
       	float sunDif = clamp( dot(norm, sunDir),0.,1.);
        float skyDif = clamp( 0.5 + 0.5*dot(norm, vec3(0.,1.,0.)),0.,1.);
        float sunSha = step(castRay(pos + norm * 0.001, sunDir),0.);
        float bouceDif = clamp( 0.5 + 0.5*dot(norm, vec3(0.,-1.,0.)),0.,1.);

        if(time > 12. && time < 14.) col = vec3(t);
        if(time > 14. && time < 16.) col = norm;

        if(time > 16. && time < 18.)
        {
            col =  mate*vec3(7.0,5.0,3.) * sunDif;
        }
        if(time > 18. && time < 20.)
        {
            col =  mate*vec3(7.0,5.0,3.) * sunDif * sunSha;
        }
        if(time > 20. && time < 22.)
        {
            col =  mate*vec3(7.0,5.0,3.) * sunDif * sunSha;
            col += mate*vec3(0.5,0.8,0.9) * skyDif;        
        }
        if(time > 22.)
        {
            col =  mate*vec3(7.0,5.0,3.) * sunDif * sunSha;
            col += mate*vec3(0.5,0.8,0.9) * skyDif;        
            col += mate*vec3(0.7,0.5,0.3) * bouceDif;
        }

    }

    col = pow(col, vec3(0.4545)); 

    if(time < 2.) col = vec3(uv,0.);
    if(time > 2. && time < 4.) col = rd;
    if(time > 4. && time < 12.) col = vec3(t);


    fragColor = vec4(col,1.0);
}

// CC0 licensed, do what thou wilt.
const float SEED = 42.0;

// returns a float between -1.0 and 1.0
float swayRandomized(float seed, float value)
{
    float f = floor(value);
    float start = sin((cos(f * seed) + sin(f * 1024.)) * 345. + seed);
    float end   = sin((cos((f+1.) * seed) + sin((f+1.) * 1024.)) * 345. + seed);
    return mix(start, end, smoothstep(0., 1., value - f));
}

// returns a float betweeen -3.0 and 3.0. Does not modify con.
float cosmic(float seed, vec3 con)
{
    float sum = swayRandomized(seed, con.z + con.x);
    sum +=      swayRandomized(seed, con.x + con.y + sum);
    sum +=      swayRandomized(seed, con.y + con.z + sum);
    return sum;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // aTime, s, and c could be uniforms in some engines.
    float aTime = iTime * 0.16;
    vec3 s = vec3(swayRandomized(-16405.31527, aTime - 1.11),
                  swayRandomized(-77664.8142, aTime + 1.41),
                  swayRandomized(-50993.5190, aTime + 2.61)) * 3. + 1.;
    vec3 c = vec3(swayRandomized(-10527.92407, aTime - 1.11),
                  swayRandomized(-61557.6687, aTime + 1.41),
                  swayRandomized(-43527.8990, aTime + 2.61)) * 3. + 1.;
    vec3 con = vec3(0.0004375, 0.0005625, 0.0008125) * aTime + c * uv.x + s * uv.y;
    con.x = cosmic(SEED, con);
    con.y = cosmic(SEED, con);
    con.z = cosmic(SEED, con);

    fragColor = vec4(sin(con) * 0.5 + 0.5,1.0);
}

void mainImage(out vec4 e, in vec2 v)
{
    vec3 d=.5-vec3(gl_FragCoord.xy,1)/iResolution.y,p,o;
    for(int i=0;i<32;i++)
    {
        o=p;
        o.z-=iTime*9.;
        float a=o.z*.1;
        o.xy*=mat2(cos(a),sin(a),-sin(a),cos(a));
        p+=(.1-length(cos(o.xy)+sin(o.yz)))*d;
    }
    e=vec4((sin(p)+vec3(2,5,9))/length(p)*vec3(1), 1);
}

