const float cells = 40.;
const float subtile = 1.;
const bool boxed = true;

vec2 N22(vec2 p) {
    vec3 a = fract(p.xyx*vec3(123.34, 234.34, 345.65));
    a += dot(a, a+34.45);
    return fract(vec2(a.x*a.y, a.y*a.z));
}

vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;
	float t = iTime * 0.2+200.;

    vec3 col;

    float id = 0.;
    float minD = 1000.;

    if (boxed) {
        vec2 guv = fract(uv*subtile)-.5;
        vec2 gid = floor(uv*subtile);
		vec2 cid;

        for (float x = -1.; x < 2.; x++) {
            for (float y = -1.; y < 2.; y++) {
                vec2 off = vec2(x, y);
                vec2 n = N22(gid+off);
                vec2 p = off + sin(n * t*4.)*0.5;
                vec2 dVec = guv-p;
                float d = abs(dVec.x)+abs(dVec.y);
                if (d < minD) {
                    minD = d;
                    cid = off+gid;
                }
            }
        }
        float h = N22(cid).x*0.2;
        float s = smoothstep(1., -0.6, minD);
        float b = 0.3+N22(cid).x*(1.-minD);

        col = hsb2rgb(vec3(h, s, b));
    } else {
        for (float i = 0.; i < cells; i++) {
            vec2 n = N22(vec2(i));
            vec2 p = sin(n * t);
            p.x *= iResolution.x / iResolution.y;
            vec2 dVec = uv-p;
            float d = abs(dVec.x)+abs(dVec.y);
            if (d < minD) {
                minD = d;
                id = i / cells;
            }
            float h = id*0.2;
            float s = smoothstep(0.7, -0.6, minD);
            float b = 0.3+id*(1.-minD);

            col = hsb2rgb(vec3(h, s, b));
        }
    }
    fragColor = vec4(col,1.);
}

