////////////////////////////////////////////////////////////////////////////////
//
// John Byrom: device from title page of "The Universal English Short-hand":
// https://books.google.co.uk/books?id=Azg5AAAAMAAJ&printsec=frontcover
// https://en.wikipedia.org/wiki/John_Byrom
//
// Matthew Arcus (mla), 2020
//
// 'r' shows fundamental region
//
// John Byrom, FRS (1692-1763): English poet and the inventor of a
// revolutionary system of shorthand. He is most remembered as the
// writer of the lyrics of the Anglican hymn 'Christians awake! Salute
// the happy morn'.
//
////////////////////////////////////////////////////////////////////////////////

bool alert = false;
void assert(bool b) {
  if (!b) alert = true;
}

bool key(int code) {
  return texelFetch(iChannel3, ivec2(code,2),0).x != 0.0;
}

const int CHAR_R = 82;

float circle(vec2 p, vec3 c) {
  vec2 centre = c.xy;
  float radius = c.z;
  return abs(length(p-centre)-radius);
}

float byrom(vec2 p, float lwidth, out bool fundamental) {
  float d = 1e8;
  //p = p-3.0*round(p/3.0); // Repeat grid of [-1.5,1.5] squares
  vec2 p0 = p;
  p = abs(p);  // Reflect into primary quadrant

  // Next line draws the 2 little circles on the x-axis.
  // Do this before the reflection in x = 0.5
  d = min(d,circle(p,vec3(0.94,0,0.06)));

  p.x = min(p.x,1.0-p.x); // Reflect in x = 0.5

  fundamental = p == p0;

  // The arcs of the larger circles - there are 3 in the fundamental
  // region. Use a fixed circle and reflect p in y=0 and x=0.5 (could
  // fix p and reflect the circle, but seems clearer this way round).
  vec3 c = vec3(2.5,-1,2.5);
  d = min(d,circle(p,c));
  d = min(d,circle(vec2(1.0-p.x,p.y),c)); // x=0.5
  d = min(d,circle(vec2(p.x,-p.y),c));    // y=0.0

  // The small semicircles on top and bottom edges
  d = min(d,circle(p,vec3(0.5,1,0.5)));

  // The diagonal lines - translate to any point on line, then
  // dot product with line normal. The compiler can do the
  // normalization for use.
  d = min(d,abs(dot(p-vec2(0.5,0),normalize(vec2(2,1)))));

  // The grid lines
  d = min(d,abs(p.y));
  d = min(d,abs(p.y-1.0));
  d = min(d,abs(p.x));

  // Clip to region, just the sides needed.
  d = max(d,p.y-1.0);
  d = max(d,-p.x);

  // Surrounding circle - use original p0 for this and make circle
  // slightly larger to ensure square corners are covered.
  d = min(d,circle(p0,vec3(0,0,sqrt(2.0+2.0*lwidth))));
  return d;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
   p *= 1.55; // scale
    //p.x += iTime;
  float lwidth = 0.012;
  bool fundamental;
  float d = byrom(p,lwidth,fundamental);

  // Draw the line!
  vec4 tt = texture(iChannel0,p);
  float t = 1.0-smoothstep(0.0,lwidth*(1.0+tt.y),d);
  t *= tt.x;
  t = 1.0-t;
  t *= 0.8+0.1*tt.z;
  vec3 col = t*vec3(1,1,0.8);

  // The fundamental region is the points that haven't moved.a
  if (key(CHAR_R) &&  fundamental) col.gb *= 0.8;
  fragColor = vec4(col,1);
}

