#define PI 3.1415926

float sinc(in float x) {
	if (x == 0.0)
        return 1.0;
    return sin(PI*x) / (PI*x);
}



vec3 sampleBilinear(in vec2 uv) {
    ivec2 uvi; vec2 interp;
    sharedGetUvAndInterp(uv, uvi, interp);
    vec4 weights = vec4((1.0-interp.x) * (1.0-interp.y),
                        interp.x * (1.0-interp.y),
                        (1.0-interp.x) * interp.y,
                        interp.x * interp.y);
    return  texelFetch(iChannel0, uvi + ivec2(0,0),0).xyz * weights.x
          + texelFetch(iChannel0, uvi + ivec2(1,0),0).xyz * weights.y
          + texelFetch(iChannel0, uvi + ivec2(0,1),0).xyz * weights.z
          + texelFetch(iChannel0, uvi + ivec2(1,1),0).xyz * weights.w;
}

vec3 sampleSinc(in vec2 uv) {
    ivec2 uvi; vec2 interp;
    sharedGetUvAndInterp(uv, uvi, interp);

    vec3 col = vec3(0.0);
    float weights = 0.0;
    for (int y = -2; y <= 2; ++y) {
        for (int x = -2; x <= 2; ++x) {
            float effective_x = interp.x - float(x);
            float effective_y = interp.y - float(y);
            float window = 1.0;//sinc(effective_x / 4.0) * sinc(effective_y / 4.0);
            float w = sinc(effective_x) * sinc(effective_y) * window;
            col += texelFetch(iChannel0, uvi + ivec2(x,y),0).xyz * w;
            weights += w;
        }
    }
    return col / weights;
}

vec4 getBsplineWeights(in float x) {
  	// float w0 = 1.0/6.0 * (1.0-x) * (1.0-x) * (1.0-x);
  	float w1 = 2.0/3.0 - 0.5 * x*x * (2.0-x);
  	float w2 = 2.0/3.0 - 0.5 * (1.0-x)*(1.0-x) * (2.0-(1.0-x));
  	float w3 = 1.0/6.0 * x*x*x;
	return vec4(1.0 - w3 - w1 - w2, w1, w2, w3);
}

vec3 sampleBSpline(in vec2 uv) {
    ivec2 uvi; vec2 interp;
    sharedGetUvAndInterp(uv, uvi, interp);

    vec4 bspline_weights_x = getBsplineWeights(interp.x);
    vec4 bspline_weights_y = getBsplineWeights(interp.y);
    vec3 col = vec3(0.0);
    for (int y = -1; y <= 2; ++y) {
        for (int x = -1; x <= 2; ++x) {
            col += texelFetch(iChannel0, uvi + ivec2(x,y),0).xyz * bspline_weights_x[x+1] * bspline_weights_y[y+1];
        }
    }
    return col;
}

vec3 getBiquadraticWeights(in float x) {
    return vec3(0.5 - x + 0.5 * x * x, x - x * x + 0.5, 0.5 * x * x);
}

vec3 sampleBiquadratic(in vec2 uv) {
    ivec2 uvi; vec2 interp;
    uv *= iChannelResolution[0].xy;
    uv -= 0.5;
    interp = uv - round(uv);
    uvi = ivec2(round(uv));

    vec3 biquadratic_weights_x = getBiquadraticWeights(interp.x+0.5);
    vec3 biquadratic_weights_y = getBiquadraticWeights(interp.y+0.5);
    vec3 col = vec3(0.0);

    for (int y = -1; y <= 1; ++y) {
        for (int x = -1; x <= 1; ++x) {
            col += texelFetch(iChannel0, uvi + ivec2(x,y),0).xyz 
                * biquadratic_weights_x[x+1] * biquadratic_weights_y[y+1];
        }
    }
    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 origUV = fragCoord/iResolution.xy;
    vec2 uv = origUV / 5.0;
    uv += vec2(0.5, 0.48);
    uv *= iChannelResolution[0].xy;
    if (fract(0.15 * iTime) < 0.8)
    	uv = round(uv);
    uv /= iChannelResolution[0].xy;
    uv += vec2(sin(iTime),cos(iTime)) / 150.0 * vec2(iResolution.y/iResolution.x, 1.0);
    vec2 phase = fract(uv * iChannelResolution[0].xy - vec2(0.5));

    vec3 col = vec3(0.0);

    if (origUV.x > origUV.y) {
        if (origUV.x > 1.0 - origUV.y) {
        	col = sampleSinc(uv);
        } else {
            col = sampleBSpline(uv);
        }
    } else {
        if (origUV.x > 1.0 - origUV.y) {
        	col = sampleBilinear(uv);
        } else {
            col = sampleBiquadratic(uv);
        }
    }
    if (abs(origUV.x - origUV.y) < 0.005 || abs(origUV.x - (1.0-origUV.y)) < 0.005)
        col = vec3(1.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

