//   4) Fourier fit 
//
// I might play around with other ways of approximating these functions

vec3 hsluv(float t) {

    t *= 6.283185307179586;

    vec2 cs1 = vec2(cos(t), sin(t));
    vec2 cs2 = vec2(cos(2.0*t), sin(2.0*t));
    vec2 cs3 = vec2(cos(3.0*t), sin(3.0*t));

    vec3 p = vec3(0.4583040035795564, 0.4226990280106653, 0.4727729414154237);
    p += vec3(0.6170329948525921, -0.1888829613996365, -0.05349878095695151)*cs1.x;
    p += vec3(-0.002078927113488328, 0.09658721174877186, -0.6178501553877714)*cs1.y;
    p += vec3(-0.01706265814602095, -0.03406876642641538, 0.01381827001423946)*cs2.x;
    p += vec3(-0.1136674359039428, 0.07588882701812819, -0.1422138970855871)*cs2.y;
    p += vec3(-0.04646079965938044, 0.02472070929461941, -0.02796074953468969)*cs3.x;
    p += vec3(-0.02750642491382323, 0.0318915903730297, -0.001136745851157035)*cs3.y;

    return p;

}

vec3 hpluv(float t) {

    t *= 6.283185307179586;

    vec2 cs1 = vec2(cos(t), sin(t));
    vec2 cs2 = vec2(cos(2.0*t), sin(2.0*t));
    vec2 cs3 = vec2(cos(3.0*t), sin(3.0*t));

