    const vec3 n3 = vec3(402449.1952667068, 8.215822679311859, -26.50421098884263);

    vec3 num = n0+t*(n1+t*(n2+t*n3));

    const vec3 d1 = vec3(118740.4629146444, 6.273896609959839, -1.468039758700077);
    const vec3 d2 = vec3(-429488.4500615235, -18.0038243539569, 11.99227708890398);
    const vec3 d3 = vec3(388362.8120573136, 11.06612403569916, -10.0995891932555);

    vec3 denom = 1.0+t*(d1+t*(d2+t*d3));

    return num/denom;

}

vec3 hpluv(float t) {

    const vec3 n0 = vec3(0.7755223888597467, 0.4819666219768237, 0.551173411404921);
    const vec3 n1 = vec3(0.2813802962619366, -0.7671094845567339, -3.216380071388543);
    const vec3 n2 = vec3(-9.568788047994651, 0.3022781094227005, 8.202467388623468);
    const vec3 n3 = vec3(11.91677649652787, 0.07208781864656497, -4.957255852700072);

    vec3 num = n0+t*(n1+t*(n2+t*n3));

    const vec3 d1 = vec3(1.28647580169279, -2.450901333406877, -2.708450209280833);
    const vec3 d2 = vec3(-12.69228327425032, 2.470825420781031, 5.080029261806732);
    const vec3 d3 = vec3(14.77069145126272, -0.8359320903961928, -2.287476595664842);

    vec3 denom = 1.0+t*(d1+t*(d2+t*d3));

    return num/denom;

}

