////////////////////////////////////////////////////////////////////////////////
//
// "Nine Point Conic" on a projective sphere.
//
// <mouse>: rotate
// <up>/<down>: move in and out
// b: show background
// r: autorotation
// s: show sphere

// See also:
// https://www.shadertoy.com/view/Wd2cRG
// https://www.shadertoy.com/view/WsscRB
//
////////////////////////////////////////////////////////////////////////////////

////////////////////////////////////////////////////////////////////////////////
//
// The Nine Point Conic
// Matthew Arcus, 2020.
//
// Given a triangle ABC and a point P, can draw a conic between:
// The midpoints of the triangle sides
// The midpoints of the lines from P to each vertex
// The points where these lines cut the sides of the triangle
//
// The conic changes from ellipse to hyperbola when it crosses one of
// the extended triangle sides, and becomes a circles when P coincides
// with the orthocentre (intersection of altitudes).
//
////////////////////////////////////////////////////////////////////////////////

bool check = false;

void assert(bool b) { if (!b) check = true; }

////////////////////////////////////////////////////////////////////////////////
//
// Geometric constructions
//
// Homogeneous coordinates used throughout (so the lines and points are
// "projective"):
// (x,y,z) represents the point (x/z,y/z) for z != 0
// (x,y,0) represents a "point at infinity" which acts like a direction.
// (a,b,c) represents the line ax + by + cz = 0
// (0,0,c) represents the "line at infinity"
//
////////////////////////////////////////////////////////////////////////////////

// The line between two projective points, or the intersection of two
// projective lines, is just their cross product.
vec3 join(vec3 p, vec3 q) {
  return cross(p,q);
}

// The midpoint between p and q (also conjugate with point at infinity)
vec3 midpoint(vec3 p, vec3 q) {
  return p*q.z + p.z*q;
}

float distance2(vec3 p, vec3 q) {
  p /= p.z; q /= q.z;
  return abs(dot(p-q,p-q));
}

// Perpendicular bisector (a line)
vec3 bisector(vec3 p,vec3 q) {
  // (p-r).(p-r) = (q-r).(q-r) // r equidistant from p and q
  // pp - 2pr + rr = qq - 2qr + rr
  // pp - qq = 2pr - 2qr
  // 2r.(p-q) = pp - qq
  p /= p.z; q /= q.z;
  return vec3(2.0*(p.xy-q.xy),dot(q,q)-dot(p,p));
}

// Drop perpendicular from p onto line q.
vec3 perpendicular(vec3 p, vec3 q) {
  // (p + kq.xy).q = 0
  // p.q + k(q.yx).q = 0
  float k = dot(p,q)/dot(q.xy,q.xy);
  return p - k*vec3(q.xy,0);
}

// Find a projective mapping taking p0,p1,p2,p4 to
// triangle of reference and unit point, ie:
// p0 -> (1,0,0), p1 -> (0,1,0), p2 -> (0,0,1), p3 -> (1,1,1)
// No three points collinear.
mat3 rproject(vec3 p0, vec3 p1, vec3 p2, vec3 p3) {
  // Just an inverse for the first three points
  // (the triangle of reference). No inverse if collinear.
  mat3 m = inverse(mat3(p0,p1,p2)); // column major!
  vec3 p3a = m*p3;
  // Then scale each row so the unit point (1,1,1) is correct
  m = transpose(m);
  // zero components here only if not collinear
  m[0] /= p3a[0];
  m[1] /= p3a[1];
  m[2] /= p3a[2];
  m = transpose(m);
  return m;
}

// Construct the conic defined by 5 points.
// Method taken from "Geometry", Brannan, Esplan & Gray, CUP, 2012
mat3 solve(vec3 p0, vec3 p1, vec3 p2, vec3 p3, vec3 p4) {
  // p takes p0,p1,p2,p3 to triangle of reference and unit point
  mat3 p = rproject(p0,p1,p2,p3);
  // Now construct a conic through the images of p0-p4,
  vec3 p4a = p*p4;
  float a = p4a.x, b = p4a.y, c = p4a.z;
  float d = c*(a-b);
  float e = b*(c-a);
  float f = a*(b-c);
  mat3 m = mat3(0,d,e,
                d,0,f,
                e,f,0);
  // And combine the two.
  mat3 res = transpose(p)*m*p;
  assert(abs(determinant(res)) > 0.1);
  return res;
}

////////////////////////////////////////////////////////////////////////////////
// Distance functions
////////////////////////////////////////////////////////////////////////////////

float point(vec3 p, vec3 q) {
  float pz = p.z, qz = q.z;
  p *= qz; q *= pz;
  return distance(p,q)/abs(p.z);
}

float line(vec3 p, vec3 q) {
  return abs(dot(p,q)/(p.z*length(q.xy)));
}

float line(vec3 p, vec3 q, vec3 r) {
  return line(p,join(q,r));
}

float segment(vec3 p, vec3 q, vec3 r) {
  p /= p.z; q /= q.z; r /= r.z; // normalize
  vec3 pa = p-q;
  vec3 ba = r-q;
  float h = clamp(dot(pa,ba)/dot(ba,ba),0.0,1.0);
  float d = length(pa-h*ba);
  return d;
}

float conic(vec3 p, mat3 m) {
  float s = dot(p,m*p);
  vec3 ds = 2.0*m*p; // Gradient
  return abs(s/(p.z*length(ds.xy))); // Normalize for Euclidean distance
}

float circle(vec3 p, vec4 c) {
  // (x-a)^2 + (y-b)^2 = r^2
  // x2 -2ax + a2 + y2 -2by + b2 -r2 = 0
  vec3 q = c.xyz;
  float r2 = c.w;
  q /= q.z;
  return conic(p,mat3(1,0,-q.x,
                      0,1,-q.y,
                     -q.x,-q.y,dot(q.xy,q.xy)-r2));
}

float quad(vec3 p, vec3 a, vec3 b, vec3 c, vec3 d) {
  float t = 1e8;
  t = min(t,segment(p,a,b));
  t = min(t,segment(p,b,c));
  t = min(t,segment(p,c,d));
  t = min(t,segment(p,d,a));
  return t;
}

////////////////////////////////////////////////////////////////////////////////
// Drawing functions
////////////////////////////////////////////////////////////////////////////////

void getmin(inout float d, inout int index, int i, float t) {
  if (t < d) { d = t; index = i; }
}

vec3 colors[] =
  vec3[](vec3(1,0,0),
         vec3(1,1,0),
         vec3(0,0,1),
         vec3(0,0.8,0),
         vec3(0,1,1),
         vec3(0.8),
         vec3(1,0,1),
         vec3(1,1,1)
         );

vec3 getcol(int i) {
  return colors[i];
}

vec3 diagram(vec3 p, vec3 P) {
  vec3 pcol = vec3(0.3);
  float lwidth = 0.005;
  float dwidth = 1.5*fwidth(p.x/p.z);
  float pwidth = 0.03;
  // The triangle (P is parameter)
  vec3 A = vec3(1.0,1.0,1), B = vec3(-1.5,-0.5,1), C = vec3(1,-1,1);
  vec3 AB = join(A,B), BC = join(B,C), CA = join(C,A);
  vec3 PA = join(P,A), PB = join(P,B), PC = join(P,C);
  vec3 mBC = midpoint(B,C), mCA = midpoint(C,A), mAB = midpoint(A,B);
  vec3 mPA = midpoint(P,A), mPB = midpoint(P,B), mPC = midpoint(P,C);
  vec3 aA = join(PA,BC), aB = join(PB,CA), aC = join(PC,AB);
  vec3 pA = perpendicular(A,BC);
  vec3 pB = perpendicular(B,CA);
  vec3 pC = perpendicular(C,AB);
  vec3 H = join(join(A,pA),join(B,pB));
  mat3 X = solve(mAB,mBC,mCA,mPA,mPB);

  vec3 bA = bisector(A,B);
  vec3 bB = bisector(B,C);
  // Intersect at the circumcentre
  vec3 O = join(bA,bB);
  // centre of the circumcircle.
  vec4 C1 = vec4(O,distance2(O,A));

  float d = 1e8;
  int index = -1;
  getmin(d,index,0,line(p,AB));
  getmin(d,index,0,line(p,BC));
  getmin(d,index,0,line(p,CA));

  getmin(d,index,1,line(p,PA));
  getmin(d,index,1,line(p,PB));
  getmin(d,index,1,line(p,PC));

  getmin(d,index,4,line(p,A,pA));
  getmin(d,index,4,line(p,B,pB));
  getmin(d,index,4,line(p,C,pC));

  if (false) {
    getmin(d,index,3,quad(p,mAB,mCA,mPC,mPB));
    getmin(d,index,3,quad(p,mBC,mAB,mPA,mPC));
    getmin(d,index,3,quad(p,mCA,mBC,mPB,mPA));
  }
  getmin(d,index,2,conic(p,X));
  getmin(d,index,5,circle(p,C1));

  //vec3 bgcolor = 0.1*vec3(0.75,0.25,1); //vec3(1,1,0.8);
  vec3 col = vec3(1,1,0.8);  
  col = mix(0.9*getcol(index),col,smoothstep(lwidth,lwidth+dwidth,d));
  d = 1e8;
  getmin(d,index,0,point(p,P));

  getmin(d,index,0,point(p,A));
  getmin(d,index,0,point(p,B));
  getmin(d,index,0,point(p,C));

  getmin(d,index,0,point(p,mBC));
  getmin(d,index,0,point(p,mCA));
  getmin(d,index,0,point(p,mAB));

  getmin(d,index,0,point(p,mPA));
  getmin(d,index,0,point(p,mPB));
  getmin(d,index,0,point(p,mPC));

  getmin(d,index,0,point(p,aA));
  getmin(d,index,0,point(p,aB));
  getmin(d,index,0,point(p,aC));

  getmin(d,index,0,point(p,H));

  col = mix(pcol,col,smoothstep(pwidth,pwidth+dwidth,d));
  return col;
}

////////////////////////////////////////////////////////////////////////////////
// The Sphere
////////////////////////////////////////////////////////////////////////////////

const float AA = 2.0;
const int maxiterations = 50;

bool dobackground = true;
bool dorotate = true;
bool dosphere = true;

const float PI = 3.14159265;

// R3 inversion in sphere, centre q, square radius r2.
vec3 invert(vec3 p, vec3 q, float r2) {
  p -= q;
  p *= r2/dot(p,p);
  p += q;
  return p;
}

vec3 stereographic0(vec3 p) {
  return invert(p,vec3(0,0,-1),2.0);
}

// Invert in sphere radius sqrt(2), centre (0,0,-1)
// is stereographic projection from the unit sphere
// to z=0 (and its inverse).
vec2 stereographic(vec3 p) {
  return stereographic0(p).xy;
}

vec3 istereographic(vec2 z) {
  return stereographic0(vec3(z,0));
}

vec3 transform(in vec3 p);

vec3 getspherecolor(vec3 p3) {
  //if (p3.z < 0.0) return vec3(0,0,0.2);
  // Map to the plane
  p3 = transform(p3);
  vec2 p = p3.xy/p3.z;
  vec3 P = iMouse.x <= 0.0 ? vec3(0,0,1)
    : vec3((2.0*iMouse.xy-iResolution.xy)/iResolution.y,1);
  P = vec3(cos(iTime),sin(1.1*iTime),1);
  return diagram(vec3(p,1),P);
}

struct Ray {
  vec3 q;               // origin
  vec3 d;               // direction
};

struct Hit {
  float t;      // solution to p=q+t*d
  vec3 n;       // normal
};

struct Sphere {
  vec3 p;       // centre
  float r;      // radius
};

bool intersectSphere(Sphere s, Ray ray, out Hit hit[2]) {
  vec3 p = s.p;
  float r = s.r;
  float c = length(p);
  vec3 q = ray.q, d = ray.d;
  // Formula for x^2 + 2Bx + C = 0
  // |q + t*d - p|^2 = r^2
  float B = dot(q-p,d);
  float C = dot(q,q)-2.0*dot(q,p)+(c+r)*(c-r);
  float D = B*B - C;
  if (D < 0.0) return false;
  D = sqrt(D);
  float t0,t1;
  if (B >= 0.0) {
    t0 = -B-D; t1 = C/t0;
  } else {
    t1 = -B+D; t0 = C/t1;
  }
  hit[0] = Hit(t0,(q+t0*d-p)/r);
  hit[1] = Hit(t1,(q+t1*d-p)/r);
  return true;
}

bool intersectScene(Ray r, out Hit hit[2]) {
  Sphere s = Sphere(vec3(0),1.0);
  if (intersectSphere(s,r,hit)) {
    return true;
  }
  return false;
}

vec3 light;

vec3 solve(Ray r,vec2 uv) {
  Hit hit[2];
  vec3 c = vec3(0);
  if (!dosphere || !intersectScene(r,hit)) {
    if (dobackground) {
      vec3 p = istereographic(uv);
      c = getspherecolor(p);
      c *= dosphere ? 0.2 : 0.8;
    }
  } else {
    for (int i = 0; i < 2; i++) {
      vec3 p = r.q+hit[i].t*r.d;
      vec3 n = hit[i].n;
      vec3 basecolor = getspherecolor(hit[i].n);
      if (basecolor == vec3(0)) continue;
      vec3 color = vec3(0);
      float ambient = 0.5;
      float diffuse = 0.5;
      color += basecolor*ambient;
      color += basecolor*diffuse*max(0.0,dot(light,n));
      float specular = pow(max(0.0,dot(reflect(light,n),r.d)),5.0);
      vec3 speccolor = basecolor; //vec3(1);
      color += 1.0*specular*speccolor;
      return color;
    }
  }
  return c;
}

const int CHAR_B = 66;
const int CHAR_C = 67;
const int CHAR_R = 82;
const int CHAR_S = 83;
const int CHAR_T = 84;

const int KEY_LEFT = 37;
const int KEY_UP = 38;
const int KEY_RIGHT = 39;
const int KEY_DOWN = 40;

bool key(int code) {
  return texelFetch(iChannel0, ivec2(code,2),0).x != 0.0;
}

vec4 store(int i,int j) {
  return texelFetch(iChannel1, ivec2(i,j),0);
}

int keycount(int key) {
  return int(store(0,key).x);
}

// Smooth HSV to RGB conversion 
// Function by iq, from https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb( in vec3 c ) {
  vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
  return c.z * mix( vec3(1.0), rgb, c.y);
}

vec2 rotate(vec2 p, float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  if (dorotate) {
    p.yz = rotate(p.yz,iTime * 0.125);
    p.zx = rotate(p.zx,iTime * 0.2);
  }
  return p;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  dobackground = !key(CHAR_B);
  dorotate = !key(CHAR_R);
  dosphere = !key(CHAR_S);

  light = vec3(0.5,1.0,1.0);
  light = normalize(light);

  vec3 p = vec3(0,0,6);
  p.z *= exp(-0.1*float(keycount(KEY_UP)-keycount(KEY_DOWN)));
  vec3 col = vec3(0);
  for (float i = 0.0; i < AA; i++) {
    for (float j = 0.0; j < AA; j++) {
      vec2 uv = (2.0*(fragCoord+vec2(i,j)/AA)-iResolution.xy)/iResolution.y;
      vec3 r = vec3(uv,-6);
      r = normalize(r);
      col += solve(Ray(p,r),uv);
    }
  }
  col = pow(col/(AA*AA),vec3(0.4545));
  //if (check) fragColor = 3(1,0,0);
  fragColor = vec4(col,1);
}

