#define MASK 123459876
#define Q 16.0
#define INV_Q (1.0/Q)

// Taken from "Numerical Recipes In C" Cambridge Uni. 2nd ed
int rbit(inout int s)
{
    s ^= MASK;
    int res = (s >> 17) & 1 ^
        (s >> 4) & 1 ^ 
    	(s >> 1) & 1 ^
        s & 1;
    s ^= MASK;
    s = (s << 1) | res;
    return res;
}

vec2 hash(float v)
{
    int s = int(v);
    vec2 res = vec2(0);
    for(int i =0;i<5;i++)
    {
        res.x += float(rbit(s)<<i);
        res.y += float(rbit(s)<<i);
    }
    res.x *= (rbit(s) == 1? -1.0:1.0)/64.0;
    res.y *= (rbit(s) == 1? -1.0:1.0)/64.0;
    return res;
}

float hitAt(vec2 uv, float v)
{
    vec2 p = hash(floor(v));
    vec2 np = hash(floor(v+1.0));
    float m = fract(v);
    m *= m*(3.0-2.0*m);
    return length(uv - mix(p,np, m)) < 0.25? 1.0:0.0;
}

vec2 normal(vec2 uv, float s, float v)
{
    vec2 u = vec2(s,0.0);
    return normalize(hitAt(uv,v)-vec2(hitAt(uv-u.xy,v), hitAt(uv-u.yx,v)));
}

vec3 scene(vec2 uv)
{
    uv = INV_Q*floor(Q*uv);
    float t = iTime;
    vec3 col = vec3(0,0.2,0.2);
    col += hitAt(uv,t)*vec3(0,0,0.7);
    vec2 n = 0.5+0.5*normal(uv,INV_Q, t);
    return length(n)>0.5? 0.5*col:col;
}

