// CHANGE DETECTION, F.Claudel 2020
//define FLIPHZ for horzontal flip of video source (webcam)
const bool FLIPHZ = true;

// vidoe time period (seconds)
const float dtFrame = 1.0/60.0; // 1./60 for 60 fps

// changeThresh for background : normalized change on all colors (max sqrt(3))
const float changeThresh = 0.11;
// changeThresh for detection buffer: normalized change on all colors (max sqrt(3))
const float changeDetThresh = 0.1;

//time delay for integartion into background (seconds)
const float tInteg = 1.0;
//time delay for integartion into detection buffer (seconds)
const float tIntegDet = 0.25;
// max duration of stable detection (seonds), before switching to background
const float  tDetStable = 3.0;

//max Number of stable 'etected frames' (before switching to backgound)
const float maxDetStableFrames = tDetStable / dtFrame;

//weight ratio of new frame for background, when no detection (smoothes noise)
const float rNew  = 1.0-exp2(-3.0/(tInteg   /dtFrame));
const float rNewD = 1.0-exp2(-3.0/(tIntegDet/dtFrame));

// color for background (unchanged)
const vec4 COLBACK = vec4(0.0, 0.0, 0.0, 1.0);

// DETECTION FUNCTION, used for Buffer A and B
vec4 doDetection(vec4 newFrame, vec4 backb, vec4 detb, bool isBack) {
// isBack : true  to return updated background buffer,
//          false to return updated detectiuon buffer

    ///absolute norm of change/ref : min 0, max sqrt(3)
    float change = length(backb.rgb - newFrame.rgb);

    //if small change : incorporate into background at slow speed       
    if (change <= changeThresh) {
        //no significant change in new frame => incorporate in backb
        backb.rgb = rNew *newFrame.rgb + (1.0-rNew)*backb.rgb;
        backb.a   += 1.0;
        //no dectection
        detb.rgb  = COLBACK.rgb;
        detb.a    = 0.0;
    } else {
        //check if newFrame is the same as stored detectionBufer
        float chDet = length(detb.rgb - newFrame.rgb);
        bool noChDet= (chDet <= changeDetThresh);
        //detection of change in newFrame

        if (noChDet) {
            detb.a  += 1.0;
            //update detb color slowly
            detb.rgb =  rNewD *newFrame.rgb + (1.0-rNewD)*detb.rgb;
        } else {
            //reste counter to 0, and store new colour
            detb.a    = 0.0;
            detb.rgb  = newFrame.rgb;
        }
    }

    //if detection buffer counter is large
    if ((detb.a >= maxDetStableFrames) || (detb.a > backb.a))  {
        // put detectionBufer into background
        backb.rgb = detb.rgb;
        backb.a   = detb.a;
        //disable detection
        detb.rgb = COLBACK.rgb;
        detb.a = 0.0;                
    }

    //decide which buffer to return accoridng to caller option isBack
    return (isBack) ? backb : detb;
}

