    ivec2 p = ivec2(fragCoord);

    if(p.x > N.x || p.y > N.y) discard;

    int id = xy2i(p);

    u = ivec4(-1); d = vec4(1e10); 

    tid = id;
    pos = getParticle(id).xy;

    sortneighbor(id); 

    for(int i = 0; i < 8; i++)
    {
        sort(hash(ivec4(p, iFrame, i)).x%tot_n); //random sort    
    }

    ivec4 nb = get(id);
    for(int i = 0; i < 4; i++)
    {
        sortneighbor(nb[i]); 
    }

    if( any(lessThan(u, ivec4(-1))) || any(greaterThan(u, ivec4(tot_n))))
    {
        u = ivec4(0);
    }


    U = save(u);
}

