image_code:
/*
Mapping of the projective plane to a disk.

The whole affine plane is mapped to the disk without border,
the border is the line at infinity. Two opposing points on
the disk border are corresponding to just one point in the
projective plane. I gave two opposing points the same color
to emphasize this.

Basically, the lines parallel to the x axis are
an interpolation between the x axis and the disk border,
those parallel to the y axis between y axis and disk border accordingly.
Every point can then be described as the intersection of two
of those circles.
(You can think of the disk border as third axis)

The animation shows how ellipses are those conic sections without
intersections with the line at infinity,
parabolas are those with exactly one intersection, and
hyperbolas those with exactly two intersections.

(Or optionally a triangle where one point is movable with mouse)
(Or optionally an elliptic curve whose parameters you can change with mouse)

I got the basic idea of the mapping from a prof of mine,
i derived the exact formulas myself. I don't know
how this mapping is called, but this has certainly been done
before.
*/

//#define TRIANGLE
//#define ELLIPTIC_CURVE

const float pi=3.14159265358979;

const float eps=.000001;

const float t0=2.00001;
const float t1=4.00002;
const float t2=7.00003;
const float t3=9.00004;
const float tt=11.00005;
//the perturbations are a workaround for https://gitlab.freedesktop.org/mesa/mesa/issues/2610

float sgn(float a){
	if(a<0.){
		return -1.;
	}
	else{
		return 1.;
	}
}

vec2 from_projective(vec3 p, float stretch_factor, float radius, out vec2 int_p2){
	vec2 int_p;

	float a=p.x*p.x+p.y*p.y;
	float b=p.z*p.x/(stretch_factor*stretch_factor);
	float c=-p.x*p.x*radius*radius;

	float int_x=(-b+sgn(p.x)*sqrt(b*b-4.*a*c))/(2.*a);
	float int_y=int_x*p.y/p.x;

	int_p=vec2(int_x,int_y);

	if(abs(p.z)<eps){
		int_p2=-int_p;
	}
	else{
		int_p2=int_p;
	}

	return int_p;
}

vec3 to_projective(vec2 p, float stretch_factor, float radius){
	return vec3(p.xy,stretch_factor*stretch_factor*(radius*radius-dot(p,p)));
}

mat2x3 to_projective_jacobi(vec2 p, float stretch_factor, float radius){
	vec3 column1=vec3(1.,0.,-2.*stretch_factor*stretch_factor*p.x);
	vec3 column2=vec3(0.,1.,-2.*stretch_factor*stretch_factor*p.y);

	return mat2x3(column1, column2);
}

float to_projective_line(float a, float b){
	return -sgn(a)*sgn(b)*abs(a)/(abs(a)+abs(b));
}

vec2 from_projective_line(float a){
	return vec2(a,1.-abs(a));
}

float line_dist(vec2 uv, const vec2 p0, vec2 p1){
	vec2 tang=p1-p0;
	vec2 nor=normalize(vec2(tang.y,-tang.x));

	if(dot(tang,uv)<dot(tang,p0)){
		return distance(p0,uv);
	}
	else if(dot(tang,uv)>dot(tang,p1)){
		return distance(p1,uv);
	}
	else{
		return abs(dot(nor,uv)-dot(nor,p0));
	}
}

vec3 line(vec2 p_0, vec2 p_1){
	vec2 tan1 = p_1-p_0;
	vec2 nor1 = normalize(vec2(tan1.y,-tan1.x));
	return vec3(nor1,-dot(nor1,p_0));
}

vec3 line(vec3 p_0, vec3 p_1){
	return normalize(cross(p_0,p_1));
}

//mapping from barth's skript:
//https://web.archive.org/web/20051104003320/http://www.mi.uni-erlangen.de/~barth/docs/geoset.ps (german)
//it basically gives the formula and says:
//by this formula, you see the projective plane can be bijectively mapped to a disk, but this is not too useful
//the mapping does not have any favorably properties compared to the other one, i just implemented it for reference
vec2 from_projective_barth(vec3 p, float radius, out vec2 int_p2){
	vec2 int_p = 2.*radius/pi*p.xy*atan(dot(p.xy,p.xy)/(p.z*p.z))/length(p.xy);

	if(abs(p.z)<eps){
		int_p=radius*normalize(p.xy);
		int_p2=-int_p;
	}
	else{
		int_p2=int_p;
	}

	return int_p;
}

vec3 to_projective_barth(vec2 p, float radius){
	return vec3(p.xy*sqrt(tan(pi/(2.*radius)*length(p))),length(p));
}

mat2x3 to_projective_jacobi_barth(vec2 p, float radius){
	float fac=pi/(2.*radius);
	float len=length(p);

	float tmp=tan(len*fac);

	float a11=.5*(tmp*tmp + 1.)*fac*p.x*p.x/(len*sqrt(tmp)) + sqrt(tmp);
	float a12=.5*(tmp*tmp + 1.)*fac*p.x*p.y/(len*sqrt(tmp));
	float a22=.5*(tmp*tmp + 1.)*fac*p.y*p.y/(len*sqrt(tmp)) + sqrt(tmp);

	vec3 column1=vec3(a11,a12,p.x/len);
	vec3 column2=vec3(a12,a22,p.y/len);

	return mat2x3(column1,column2);
}

//elliptic degree is the same but for lines instead of points
float elliptic_distance(vec3 p1, vec3 p2){
	return acos(1./sqrt(dot(p1,p1)*dot(p2,p2))*dot(p1,p2));
}

float spectral(float x){
        return clamp(abs(mod((x/pi+1.)*4.,8.)-4.)-2.,-.75,.75)*.5/.75+.5;
}

vec3 to_col(vec2 z){
	float direction = 2.*atan(z.x,z.y);
	return vec3(spectral(direction),spectral(direction+pi/2.),spectral(direction-pi));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv-=.5;
	uv.x *= iResolution.x / iResolution.y;

	vec2 mouse = iMouse.xy / iResolution.xy;
	mouse-=.5;
	mouse.x *= iResolution.x / iResolution.y;

	vec2 mouse2 = iMouse.zw / iResolution.xy;
	mouse2-=.5;
	mouse2.x *= iResolution.x / iResolution.y;

    #ifdef TRIANGLE
    float animation=1.;
    #else
    #ifdef ELLIPTIC_CURVE
    float animation=1.;
    #else
    float animation;

    float tc=abs(mod(iTime-tt,2.*tt)-tt);

    if(tc<t1){
        animation=exp(mix(5.,0.,smoothstep(0.,t0,tc)));
    }
    else if(tc<.5*(t1+t2)){
        animation=exp(mix(0.,5.,smoothstep(t1,.5*(t1+t2),tc)));
    }
    else if(tc<t3){
        animation=exp(mix(5.,0.,smoothstep(.5*(t1+t2),t2,tc)));
    }
    else{
        animation=exp(mix(0.,5.,smoothstep(t3,tt,tc)));
    }
    #endif
    #endif

    float stretch_factor=1./animation;
    float radius=.47*animation;

	const float line_width = .001;
	const float circle_width = .003;
	const float grid_width = .0;
	const float curve_width = .001;
	const float dot_size = .005;
	const float axis_width = .005;

	const float grid_size = .8;

	const vec3 bg_col = vec3(1,1,1);
	const vec3 axis_col = vec3(0,0,0);
	const vec3 grid_col = vec3(.75);
	const vec3 line_col = vec3(0,1,0);
	const vec3 curve_col = vec3(0,0,1);
	const vec3 dot_col = vec3(1,0,0);

	vec3 circle_col = to_col(uv);

    float border = 2./iResolution.y;

	float curve_dis = 1e38;
	float dot_dis = 1e38;
	float axis_dis = 1e38;
	float grid_dis = 1e38;
	float line_dis = 1e38;
	float circle_dis = 1e38;

	vec3 color=bg_col;

	circle_dis=min(circle_dis,abs(length(uv)-radius)-circle_width);

	axis_dis=min(axis_dis,line_dist(uv,vec2(0,radius),vec2(0,-radius))-line_width);
	axis_dis=min(axis_dis,line_dist(uv,vec2(radius,0),vec2(-radius,0))-line_width);

	vec3 uv_p=to_projective(uv,stretch_factor,radius);

	mat2x3 jac=to_projective_jacobi(uv,stretch_factor,radius);

    #ifdef TRIANGLE
	vec2 m0=normalize(mouse)*min(length(mouse),radius);
	vec3 p0=to_projective(m0,stretch_factor,radius);
	vec3 p1=vec3(2.7,2.2,1.);
	vec3 p2=vec3(2.,-2.,1.);

    vec2 int_p;
	vec2 int_p2;

	int_p = from_projective(p0,stretch_factor,radius,int_p2);

	dot_dis=min(dot_dis,distance(uv,int_p)-dot_size);
	dot_dis=min(dot_dis,distance(uv,int_p2)-dot_size);

	int_p = from_projective(p1,stretch_factor,radius,int_p2);

	dot_dis=min(dot_dis,distance(uv,int_p)-dot_size);
	dot_dis=min(dot_dis,distance(uv,int_p2)-dot_size);

	int_p = from_projective(p2,stretch_factor,radius,int_p2);

	dot_dis=min(dot_dis,distance(uv,int_p)-dot_size);
	dot_dis=min(dot_dis,distance(uv,int_p2)-dot_size);

    vec3 l1=line(p0,p1);
	vec3 l2=line(p1,p2);
	vec3 l3=line(p2,p0);

	curve_dis=min(curve_dis,abs(dot(uv_p,l1)/length(l1*jac)));
	curve_dis=min(curve_dis,abs(dot(uv_p,l2)/length(l2*jac)));
	curve_dis=min(curve_dis,abs(dot(uv_p,l3)/length(l3*jac)));
    #else
    #ifdef ELLIPTIC_CURVE

	float g_2 = 10.*mouse.x;
	float g_3 = 10.*mouse.y;

	float elliptic_curve_val=(4.*uv_p.x*uv_p.x-g_2*uv_p.z*uv_p.z)*uv_p.x-(g_3*uv_p.z*uv_p.z+uv_p.y*uv_p.y)*uv_p.z;
	vec3 elliptic_curve_grad=vec3(12.*uv_p.x*uv_p.x-g_2*uv_p.z*uv_p.z,-2.*uv_p.y*uv_p.z,(-3.*g_3*uv_p.z-2.*g_2*uv_p.x)*uv_p.z-uv_p.y*uv_p.y);

    curve_dis=min(curve_dis,abs(elliptic_curve_val/length(elliptic_curve_grad*jac)));
    #else
	float hyperbola_val=8.*uv_p.x*uv_p.x-4.*uv_p.y*uv_p.y+uv_p.z*uv_p.z;
	vec3 hyperbola_grad=vec3(8.*2.*uv_p.x,-4.*2.*uv_p.y,+2.*uv_p.z);

	float parabola_val=uv_p.x*uv_p.x-uv_p.y*uv_p.z;
	vec3 parabola_grad=vec3(2.*uv_p.x,-uv_p.z,-uv_p.y);

	float circle_val=uv_p.x*uv_p.x+uv_p.y*uv_p.y-uv_p.z*uv_p.z;
	vec3 circle_grad=vec3(2.*uv_p.x,2.*uv_p.y,-2.*uv_p.z);

	float mix_val;
	vec3 mix_grad;

    mix_val=mix(circle_val,parabola_val,smoothstep(t0,t1,tc));
	mix_grad=mix(circle_grad,parabola_grad,smoothstep(t0,t1,tc));

    if(tc>t2){
        mix_val=mix(parabola_val,hyperbola_val,smoothstep(t2,t3,tc));
		mix_grad=mix(parabola_grad,hyperbola_grad,smoothstep(t2,t3,tc));
    }

	curve_dis=min(curve_dis,abs(mix_val/length(mix_grad*jac)));
    #endif
    #endif

	vec3 grid_grad=vec3(1./uv_p.z,0.,-uv_p.x/(uv_p.z*uv_p.z));
	grid_dis = min(grid_dis, abs(mod(uv_p.x/uv_p.z-grid_size*.5,grid_size)-grid_size*.5)/length(grid_grad*jac)-grid_width);
	grid_grad=vec3(0.,1./uv_p.z,-uv_p.y/(uv_p.z*uv_p.z));
	grid_dis = min(grid_dis, abs(mod(uv_p.y/uv_p.z-grid_size*.5,grid_size)-grid_size*.5)/length(grid_grad*jac)-grid_width);

	float d0=length(uv)-radius;

	grid_dis=max(d0,grid_dis);
	curve_dis=max(d0,curve_dis);

	color=vec3(mix(grid_col,color,smoothstep(0., border, grid_dis)));
	color=vec3(mix(axis_col,color,smoothstep(0., border, axis_dis)));
	color=vec3(mix(line_col,color,smoothstep(0., border, line_dis)));
	color=vec3(mix(circle_col,color,smoothstep(0., border, circle_dis)));
	color=vec3(mix(curve_col,color,smoothstep(0., border, curve_dis)));
	color=vec3(mix(dot_col,color,smoothstep(0., border, dot_dis)));

	fragColor = vec4(color,1);
}

#define USE_GAMMA ( uv.y > .5 )
#define LEFT_COL vec3(1.0, 0.0, 0.0)
#define RIGHT_COL vec3(0.0, 1.0, 0.0)
#define GAMMA_FACTOR 2.2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col1 = LEFT_COL;
    vec3 col2 = RIGHT_COL;

    if(USE_GAMMA){
        col1 = pow(col1, vec3(GAMMA_FACTOR));
        col2 = pow(col2, vec3(GAMMA_FACTOR));
    }
    vec3 col = mix(col1, col2, uv.x);

    if(USE_GAMMA){
        float invGamma = 1.0 / GAMMA_FACTOR;
        col = pow(col, vec3(invGamma));
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

/*
Done from memory as an exercise.

Even trying to find bits you remember when you have read several tutorials can
be problematic. It's a good way to make sure you know things.

Credit to The Art of Code and Code Parade on YouTube, Inigo Quilez, The Book of Shaders, and many more.

*/
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Move coordinate origin to center
    // 0-1 * 2 gives a range of 0-2
    // Subtracting 1 gives range of -1 to +1 with origin in the center
    uv *= vec2(2.);
    uv -= vec2(1.);

    // Correct perspective so the square appears square
    // Screen res is usually higher horizontally than vertically, so
    // multiply x by the x-axis proportion to the y-axis
    uv.x *= iResolution.x/iResolution.y;

    // This is the default color that will be returned.
    vec3 pixel = vec3(0.);  // black

    // Gradient BG for fun.
    // grayscale, so distance from origin determines the brightness
    // uv/vec2(2.) is to normalize to our coords
    // 1 - distance to start with black outside
    vec3 gradient = vec3(1.-distance(uv/vec2(2.), vec2(0.,0.)));
    pixel = gradient;


	// Origin (0,0)is in center, so +/-0.5 will be a square in the center.
    if(abs(uv.x)<= 0.5 && abs(uv.y)<=0.5)
        pixel = vec3(0.);  // white

    // Output to screen, adding alpha value
    fragColor = vec4(pixel,1.0);
}

void mainImage( out vec4 O, vec2 u )
{
    O -= O;
    vec2  R = iResolution.xy, 
          U = 3.*u/R.y, A,
          F = floor(U);                                               // cells
    int t = int(iDate.w*pow(10.,F.x-4.*F.y)) % 10,                    // digit to display at cell
        m = int[](0,16,257,273,325,341,365,381,495,511)[t];           // mask from dice table
    U = 3.6*(U-F)-.3; ivec2 C = ivec2(U) % 3;                         // dot subcell
    A = abs(U-1.5); max(A.x,A.y) < 1.5 &&                             // padding
  //C.x+3*C.y < t                                                     // order count ( comment one
    ( m >> (C.x+3*C.y)  & 1 ) > 0                                     // dice count    of these 2 )
        ? O +=   smoothstep (20./R.y, 0., length( fract(U)-.5 ) -.4 ) // display dot
               * vec4(1,F/3.,1)
        : O;
}

//This parameter changes the magnification.
const float mag = 9.0;
const float distortion_scale = mag*mag;

const int GREEN = 0;
const int BLUE = 1;
const int GRAY = 2;
const int YELLOW = 3;
const int WHITE = 4;
const int FOREST_GREEN = 5;
const int LIGHTBLUE = 6;
const int SKYBLUE = 7;
const int SNOW = 8;
const int WHITESMOKE = 9;
const int LIGHTGRAY = 10;
const int LIME = 11;
const int LIGHTYELLOW = 12;
const int BEIGE = 13;
const int SAND = 14;
const int TAN = 15;
const int ORANGE = 16;
const int RED = 17;
const int BLACK = 18;
const int DARKGRAY = 19;
const int ORANGERED = 20;

const int SWAMP_BIOME = 1;
const int GLACIER_BIOME = 2;
const int FORESTS_AND_MOUNTAINS = 3;
const int SAVANNA_BIOME = 4;
const int FOREST_BIOME = 5;

//this function is from https://www.shadertoy.com/view/4djSRW
int hash12(vec2 p,float num_colors)
{
    //set the number of colors to be randomly generated
    p = sin(p.xx-p.yy);
    vec3 p3 = (vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return int(floor(fract((p3.x + p3.y) * p3.z)*num_colors));
}

int magnify(vec2 fragCoord,float mag,float num_colors){
    fragCoord += vec2(sin(fragCoord.y/(distortion_scale*mag)+iTime),sin(fragCoord.x/(distortion_scale*mag)+iTime))*10.0*mag*mag;
    return hash12(floor(fragCoord/pow(3.0,mag)),num_colors);
}

ivec4 get_neighbors(vec2 fragCoord,float mag,float colors){
	return ivec4(magnify(fragCoord+vec2(0,1),mag,colors),
        magnify(fragCoord+vec2(1,0),mag,colors),
        magnify(fragCoord-vec2(0,1),mag,colors),
        magnify(fragCoord-vec2(1,0),mag,colors));
}


bool is_next_to(int color,ivec4 neighbors){
	return (neighbors.x == color)
        || (neighbors.y == color)
        || (neighbors.z == color)
        || (neighbors.w == color);
}

vec3 rgb(float r,float g,float b){
	return vec3(r,g,b)/255.0;
}

vec3 get_color(int color){
    if(color == BLUE){
    	return vec3(0.149,0.141,0.912);
   	}
    else if(color == GREEN){
    	return vec3(0.000,0.833,0.224);
   	}
    else if(color == FOREST_GREEN){
    	return rgb(34.0,139.0,34.0);
   	}
    else if(color == WHITE){
    	return vec3(1.0,1.0,1.0);
   	}
    else if(color == GRAY){
    	return vec3(192.0,192.0,192.0)/255.0;
    }
    else if(color == YELLOW){
    	return vec3(1.0,1.0,0.0);
   	}
    else if(color == LIGHTBLUE){
    	return rgb(173.0,216.0,230.0);
   	}
    else if(color == SKYBLUE){
    	return rgb(135.0,206.0,235.0);
    }
    else if(color == SNOW){
    	return rgb(255.0,250.0,250.0);
    }
    else if(color == WHITESMOKE){
    	return rgb(245.0,245.0,245.0);
    }
    else if(color == LIGHTGRAY){
    	return rgb(211.0,211.0,211.0);
    }
    else if(color == LIME){
    	return rgb(0.0,255.0,0.0);
    }
    else if(color == LIGHTYELLOW){
    	return rgb(255.0,255.0,153.0);
    }
    else if(color == BEIGE){
    	return rgb(245.0,245.0,220.0);
    }
    else if(color == TAN){
    	return rgb(210.,180.,140.);
    }
    else if(color == ORANGE){
    	return vec3(255,127,80)/255.0;
    }
    else if(color == RED){
    	return vec3(1.0,0.0,0.0);
   	}
    else if(color == DARKGRAY){
    	return rgb(128.0,128.0,128.0);
    }
    else if(color == ORANGERED){
    	return rgb(255.0,69.0,0.0);
    }
}


int glacier_biome(inout int color1, ivec4 neighbors){
	int GREEN = 0;
    int BLUE = 1;
    int GRAY = 2;
    int WHITE = 3;
    int YELLOW = 4;

    return (color1 == GREEN && is_next_to(BLUE,neighbors))
        ? GRAY
    : (color1 == GREEN && is_next_to(WHITE,neighbors))
        ? GRAY
    : (color1 == BLUE && is_next_to(GREEN,neighbors))
        ? WHITE
    : (color1 == YELLOW && is_next_to(GREEN,neighbors))
        ? GRAY
    : (color1 == YELLOW && is_next_to(GRAY,neighbors))
        ? BLUE
    :(color1 == WHITE && is_next_to(YELLOW,neighbors))
        ? GRAY
    :(color1 == YELLOW && is_next_to(WHITE,neighbors))
        ? WHITE
    :(color1 == GRAY && is_next_to(BLUE,neighbors))
        ? BLUE
    :color1;
}

void biome(inout int color1, ivec4 neighbors,int biome){
    color1 =
        glacier_biome(color1,neighbors);
}

int biome(in vec2 fragCoord,float mag,float zoom,int colors[5],int biome1){
    fragCoord *= zoom;


    float num_colors = float(colors.length());

    int color1 = magnify(fragCoord,mag,num_colors);

    ivec4 neighbors;

    while(mag > 1.0){
    neighbors = get_neighbors(fragCoord,mag,num_colors);
    biome(color1, neighbors,biome1);
    mag -= 1.0;
    }
    return colors[int(color1)];;
}


int glacier_biome(in vec2 fragCoord,float mag,float zoom,int[5] colors){
    return biome(fragCoord,mag,zoom,colors,GLACIER_BIOME);
}

int lava_biome(in vec2 fragCoord,float mag,float zoom){
	return glacier_biome(fragCoord,mag,zoom,int[](DARKGRAY,YELLOW,ORANGERED,ORANGE,ORANGERED));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = mag*(((iMouse.x-iMouse.y)*2.0+iResolution.x)/iResolution.x);
    fragCoord *= zoom;
    fragCoord = fragCoord + floor(iTime*100.0)*vec2(zoom,0.0);

	int biome = lava_biome(fragCoord,mag,1.0);

    fragColor = vec4(get_color(biome),1.0);
}

// fitting polynomials to HSLuv and HPLuv colormaps
//
// License CC0 (public domain) 
//   https://creativecommons.org/share-your-work/public-domain/cc0/
//
// answered tbird's request in https://www.shadertoy.com/view/3lBXR3
// to do the HSLuv colormap (see https://www.hsluv.org/comparison/)
// and threw in HPLuv too, for good measure
//
// not thrilled with the results, frankly -- the source colormaps are 
// much less smooth than inputs to my previous shader and the quality of 
// fit is not as good.
//
// partially this is also because the original HSLuv has some hard clipping
// which the polynomial fit doesn't love.
//
// outputs, bottom to top:
//
//   1) original HSLuv values computed from https://github.com/hsluv/hsluv-python
//   2) my polynomial fit - purple looks kind of like garbage :(
//   3) original HPLuv values from https://github.com/hsluv/hsluv-python
//   4) my polynomial fit - less garbage than the other polyfit
//
// I might play around with other ways of approximating these functions

vec3 hsluv(float t) {

    const vec3 c0 = vec3(1.112190141256249, 0.2892005552571448, 0.4533874092347643);
    const vec3 c1 = vec3(-2.700459896899569, 1.230016915698432, -8.866692238834126);
    const vec3 c2 = vec3(13.27461904052131, 9.770495221624461, 40.21502987811905);
    const vec3 c3 = vec3(-81.52914608827041, -72.38313527130981, -70.91008175027898);
    const vec3 c4 = vec3(160.2873495871779, 177.6692475972083, 72.98689200890533);
    const vec3 c5 = vec3(-113.7533707882922, -191.0996400048753, -43.90364824070267);
    const vec3 c6 = vec3(25.63678954693401, 74.92090437235763, 10.41364910112663);

    return c0+t*(c1+t*(c2+t*(c3+t*(c4+t*(c5+t*c6)))));

}

vec3 hpluv(float t) {

    const vec3 c0 = vec3(0.8498955002581585, 0.4832532405217748, 0.5421264483909258);
    const vec3 c1 = vec3(-4.409059134214186, 0.389492555133492, -1.168238680182178);
    const vec3 c2 = vec3(48.62277754887025, -0.1355162304606739, -7.080954185520974);
    const vec3 c3 = vec3(-236.4070580335005, 2.645941964751115, 51.62563361795004);
    const vec3 c4 = vec3(478.9041299570086, -8.918428894628779, -97.1930145700894);
    const vec3 c5 = vec3(-423.6767542055926, 8.066163760207662, 75.49542389205953);
    const vec3 c6 = vec3(136.9698973865875, -2.043826721347202, -21.67708580809141);

    return c0+t*(c1+t*(c2+t*(c3+t*(c4+t*(c5+t*c6)))));

}

vec3 paletteLUT(float row, float u) {

    vec2 uv;

    uv.x = mix(0.5, 254.5, u);
    uv.y = 0.5 + row;

    uv /= iResolution.xy;

    return texture(iChannel0, uv, 0.0).xyz;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    float u = fragCoord.x / iResolution.x;

    //fragColor = vec4(paletteLUT(2.0, u), 1);


    float y;
    vec2 uv;

    if (max(iMouse.z, iMouse.w) > 0.) {

        uv = fragCoord / iResolution.xy;
        y = floor(iMouse.y * 4. / iResolution.y );

    } else {

        const float m = 2.0;

        vec2 dims = iResolution.xy - vec2(2.*m, 2.*m);

        float h = floor(0.25*dims.y);

        dims.y = 4.0*h;

        fragCoord -= 0.5*(iResolution.xy-dims) + mod(m, 2.)*0.5;

        y = floor(fragCoord.y / h);

        uv = fragCoord / dims;

        float b = mod(fragCoord.y + 0.5*h, h);

        if (abs(b - 0.5*h) < 0.5*m || uv.x < 0. || uv.x >= 1.) {
            y = -1.;
        }

    }

    fragColor = vec4(0, 0, 0, 1);

    if (y == 0.) {
        fragColor.xyz = paletteLUT(1.0, uv.x);
    } else if (y == 1.) {
        fragColor.xyz = hsluv(uv.x);
    } else if (y == 2.) {
        fragColor.xyz = paletteLUT(2.0, uv.x);
    } else if (y == 3.) {
        fragColor.xyz = hpluv(uv.x);
    }


}

